{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "ApplicationSpecificConfiguration",
  "type": "object",
  "additionalProperties": {
    "$ref": "#/definitions/AscApplicationRulesOrSchema"
  },
  "definitions": {
    "ApplicationIdentifier": {
      "type": "string",
      "enum": [
        "Exe",
        "Class",
        "Title",
        "Path"
      ]
    },
    "AscApplicationRules": {
      "description": "Rules that determine how an application is handled",
      "type": "object",
      "properties": {
        "floating": {
          "description": "Rules to manage specific windows as floating windows",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "ignore": {
          "description": "Rules to ignore specific windows",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "layered": {
          "description": "Rules to identify applications which have the `WS_EX_LAYERED` Extended Window Style",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "manage": {
          "description": "Rules to forcibly manage specific windows",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "object_name_change": {
          "description": "Rules to identify applications which send the `EVENT_OBJECT_NAMECHANGE` event on launch",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "slow_application": {
          "description": "Rules to identify applications which are slow to send initial event notifications",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "transparency_ignore": {
          "description": "Rules to ignore specific windows from the transparency feature",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        },
        "tray_and_multi_window": {
          "description": "Rules to identify applications which minimize to the tray or have multiple windows",
          "type": [
            "array",
            "null"
          ],
          "items": {
            "$ref": "#/definitions/MatchingRule"
          }
        }
      }
    },
    "AscApplicationRulesOrSchema": {
      "anyOf": [
        {
          "$ref": "#/definitions/AscApplicationRules"
        },
        {
          "type": "string"
        }
      ]
    },
    "IdWithIdentifier": {
      "type": "object",
      "required": [
        "id",
        "kind"
      ],
      "properties": {
        "id": {
          "type": "string"
        },
        "kind": {
          "$ref": "#/definitions/ApplicationIdentifier"
        },
        "matching_strategy": {
          "anyOf": [
            {
              "$ref": "#/definitions/MatchingStrategy"
            },
            {
              "type": "null"
            }
          ]
        }
      }
    },
    "MatchingRule": {
      "anyOf": [
        {
          "$ref": "#/definitions/IdWithIdentifier"
        },
        {
          "type": "array",
          "items": {
            "$ref": "#/definitions/IdWithIdentifier"
          }
        }
      ]
    },
    "MatchingStrategy": {
      "type": "string",
      "enum": [
        "Legacy",
        "Equals",
        "StartsWith",
        "EndsWith",
        "Contains",
        "Regex",
        "DoesNotEndWith",
        "DoesNotStartWith",
        "DoesNotEqual",
        "DoesNotContain"
      ]
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "KomobarConfig",
  "description": "The `komorebi.bar.json` configuration file reference for `v0.1.33`",
  "type": "object",
  "required": [
    "left_widgets",
    "monitor",
    "right_widgets"
  ],
  "properties": {
    "center_widgets": {
      "description": "Center widgets (ordered left-to-right)",
      "type": "array",
      "items": {
        "oneOf": [
          {
            "type": "object",
            "required": [
              "Battery"
            ],
            "properties": {
              "Battery": {
                "type": "object",
                "required": [
                  "enable"
                ],
                "properties": {
                  "data_refresh_interval": {
                    "description": "Data refresh interval (default: 10 seconds)",
                    "type": "integer",
                    "format": "uint64",
                    "minimum": 0.0
                  },
                  "enable": {
                    "description": "Enable the Battery widget",
                    "type": "boolean"
                  },
                  "hide_on_full_charge": {
                    "description": "Hide the widget if the battery is at full charge",
                    "type": "boolean"
                  },
                  "label_prefix": {
                    "description": "Display label prefix",
                    "oneOf": [
                      {
                        "description": "Show no prefix",
                        "type": "string",
                        "enum": [
                          "None"
                        ]
                      },
                      {
                        "description": "Show an icon",
                        "type": "string",
                        "enum": [
                          "Icon"
                        ]
                      },
                      {
                        "description": "Show text",
                        "type": "string",
                        "enum": [
                          "Text"
                        ]
                      },
                      {
                        "description": "Show an icon and text",
                        "type": "string",
                        "enum": [
                          "IconAndText"
                        ]
                      }
                    ]
                  }
                }
              }
            },
            "additionalProperties": false
          },
          {
            "type": "object",
            "required": [
              "Cpu"
            ],
            "properties": {
              "Cpu": {
                "type": "object",
                "required": [
                  "enable"
                ],
                "properties": {
                  "data_refresh_interval": {
                    "description": "Data refresh interval (default: 10 seconds)",
                    "type": "integer",
                    "format": "uint64",
                    "minimum": 0.0
                  },
                  "enable": {
                    "description": "Enable the Cpu widget",
                    "type": "boolean"
                  },
                  "label_prefix": {
                    "description": "Display label prefix",
                    "oneOf": [
                      {
                        "description": "Show no prefix",
                        "type": "string",
                        "enum": [
                          "None"
                        ]
                      },
                      {
                        "description": "Show an icon",
                        "type": "string",
                        "enum": [
                          "Icon"
                        ]
                      },
                      {
                        "description": "Show text",
                        "type": "string",
                        "enum": [
                          "Text"
                        ]
                      },
                      {
                        "description": "Show an icon and text",
                        "type": "string",
                        "enum": [
                          "IconAndText"
                        ]
                      }
                    ]
                  }
                }
              }
            },
            "additionalProperties": false
          },
          {
            "type": "object",
            "required": [
              "Date"
            ],
            "properties": {
              "Date": {
                "type": "object",
                "required": [
                  "enable",
                  "format"
                ],
                "properties": {
                  "enable": {
                    "description": "Enable the Date widget",
                    "type": "boolean"
                  },
                  "format": {
                    "description": "Set the Date format",
                    "oneOf": [
                      {
                        "description": "Month/Date/Year format (09/08/24)",
                        "type": "string",
                        "enum": [
                          "MonthDateYear"
                        ]
                      },
                      {
                        "description": "Year-Month-Date format (2024-09-08)",
                        "type": "string",
                        "enum": [
                          "YearMonthDate"
                        ]
                      },
                      {
                        "description": "Date-Month-Year format (8-Sep-2024)",
                        "type": "string",
                        "enum": [
                          "DateMonthYear"
                        ]
                      },
                      {
                        "description": "Day Date Month Year format (8 September 2024)",
                        "type": "string",
                        "enum": [
                          "DayDateMonthYear"
                        ]
                      },
                      {
                        "description": "Custom format (https://docs.rs/chrono/latest/chrono/format/strftime/index.html)",
                        "type": "object",
                        "required": [
                          "Custom"
                        ],
                        "properties": {
                          "Custom": {
                            "type": "string"
                          }
                        },
                        "additionalProperties": false
                      }
                    ]
                  },
                  "label_prefix": {
                    "description": "Display label prefix",
                    "oneOf": [
                      {
                        "description": "Show no prefix",
                        "type": "string",
                        "enum": [
                          "None"
                        ]
                      },
                      {
                        "description": "Show an icon",
                        "type": "string",
                        "enum": [
                          "Icon"
                        ]
                      },
                      {
                        "description": "Show text",
                        "type": "string",
                        "enum": [
                          "Text"
                        ]
                      },
                      {
                        "description": "Show an icon and text",
                        "type": "string",
                        "enum": [
                          "IconAndText"
                        ]
                      }
                    ]
                  }
                }
              }
            },
            "additionalProperties": false
          },
          {
            "type": "object",
            "required": [
              "Komorebi"
            ],
            "properties": {
              "Komorebi": {
                "type": "object",
                "properties": {
                  "configuration_switcher": {
                    "description": "Configure the Configuration Switcher widget",
                    "type": "object",
                    "required": [
                      "configurations",
                      "enable"
                    ],
                    "properties": {
                      "configurations": {
                        "description": "A map of display friendly name => path to configuration.json",
                        "type": "object",
                        "additionalProperties": {
                          "type": "string"
                        }
                      },
                      "enable": {
                        "description": "Enable the Komorebi Configurations widget",
                        "type": "boolean"
                      }
                    }
                  },
                  "focused_window": {
                    "description": "Configure the Focused Window widget",
                    "type": "object",
                    "required": [
                      "enable"
                    ],
                    "properties": {
                      "display": {
                        "description": "Display format of the currently focused window",
                        "oneOf": [
                          {
                            "description": "Show only icon",
                            "type": "string",
                            "enum": [
                              "Icon"
                            ]
                          },
                          {
                            "description": "Show only text",
                            "type": "string",
                            "enum": [
                              "Text"
                            ]
                          },
                          {
                            "description": "Show an icon and text for the selected element, and text on the rest",
                            "type": "string",
                            "enum": [
                              "TextAndIconOnSelected"
                            ]
                          },
                          {
                            "description": "Show both icon and text",
                            "type": "string",
                            "enum": [
                              "IconAndText"
                            ]
