SETUP := ./setup.sh
COMMANDS := install uninstall link unlink

.PHONY: default
default:
	@echo "make: Nothing to make."
	@echo "make: Try one of the following:"
	@for c in $(COMMANDS); do printf '\t%s\n' "make $$c"; done

$(COMMANDS):
	@$(SETUP) $@

{
  "name": "bpkg",
  "version": "1.1.4",
  "description": "Lightweight bash package manager",
  "global": true,
  "repo": "bpkg/bpkg",
  "install": "bash setup.sh",
  "commands": {
    "lint": "command shellcheck **/*.sh",
    "list-sources": "echo ${BPKG_PACKAGE_SOURCES[@]} | sed 's/ /\n/g'"
  },
  "commands-description": {
    "lint": "Execute shellcheck against all .sh files in project",
    "list-sources": "Output package source files"
  }
}
