module.exports = {
  extends: [
    './packages/eslint-config/eslintrc.js',
    'plugin:prettier/recommended',
  ],
  plugins: ['prettier'],
  overrides: [
    {
      files: ['**/*.ts'],
      rules: {
        /*
         * The mocha plugin reports the following signature as a violation of
         * `mocha/handle-done-callback` (misinterpreting `this` as `done`).
         *
         * See https://github.com/lo1tuma/eslint-plugin-mocha/issues/270
         *
         * ```ts
         * before(async function setupApplication(this: Mocha.Context) {
         *   this.timeout(6000);
         *   // ...
         * }
         * ```
         */
        'mocha/handle-done-callback': 'off',
      },
    },
  ],
};

{
  "*.{js,ts}": [
    "node packages/build/bin/run-prettier --write",
    "node packages/build/bin/run-eslint --fix --report-unused-disable-directives --cache"
  ],
  "*.md": "node packages/build/bin/run-prettier --write"
}

// Copyright IBM Corp. and LoopBack contributors 2020. All Rights Reserved.
// Node module: loopback-next
// This file is licensed under the MIT License.
// License text available at https://opensource.org/licenses/MIT

const {mergeMochaConfigs} = require('./packages/build');
const defaultConfig = require('./packages/build/config/.mocharc.json');

const MONOREPO_CONFIG = {
  parallel: true,
  timeout: 10000,
};

module.exports = mergeMochaConfigs(
  defaultConfig,
  MONOREPO_CONFIG,
  // Apply Mocha config from packages that require custom Mocha setup
  require('./packages/cli/.mocharc.js'),
);

{
  "bracketSpacing": false,
  "singleQuote": true,
  "printWidth": 80,
  "trailingComma": "all",
  "arrowParens": "avoid",
  "overrides": [
    {
      "files": "**/*.md",
      "options": {
        "parser": "markdown",
        "proseWrap": "always"
      }
    }
  ]
}

