#!/usr/bin/env node
const { rm } = require('fs').promises;
const { applyPlugins } = require('./template/plugins');

applyPlugins().then(async () => {
  await rm('./plugins', { recursive: true });
});

module.exports = {
    placeholderName: 'Boilerplate',
    titlePlaceholder: 'TheCodingMachine React Native Boilerplate',
    templateDir: './template',
    postInitScript: './post-init.script.js',
};
