{
  "name": "wretch",
  "version": "2.11.0",
  "type": "module",
  "description": "A tiny wrapper built around fetch with an intuitive syntax.",
  "keywords": [
    "wretch",
    "fetch",
    "ajax",
    "request"
  ],
  "engines": {
    "node": ">=14"
  },
  "homepage": "https://elbywan.github.io/wretch",
  "repository": {
    "type": "git",
    "url": "https://github.com/elbywan/wretch"
  },
  "bugs": {
    "url": "https://github.com/elbywan/wretch/issues"
  },
  "main": "./dist/bundle/wretch.min.cjs",
  "unpkg": "./dist/bundle/wretch.min.js",
  "module": "./dist/index.js",
  "types": "./dist/index.d.ts",
  "typesVersions": {
    "*": {
      "*": [
        "dist/*",
        "dist/*/index.d.ts",
        "dist/index.d.ts"
      ]
    }
  },
  "exports": {
    ".": {
      "require": {
        "types": "./dist/cjs/index.d.ts",
        "default": "./dist/cjs/index.cjs"
      },
      "import": {
        "types": "./dist/index.d.ts",
        "default": "./dist/index.js"
      }
    },
    "./all": {
      "require": {
        "types": "./dist/cjs/index.all.d.ts",
        "default": "./dist/cjs/index.all.js"
      },
      "import": {
        "types": "./dist/index.all.d.ts",
        "default:": "./dist/index.all.js"
      }
    },
    "./addons": {
      "require": {
        "types": "./dist/cjs/addons/index.d.ts",
        "default": "./dist/cjs/addons/index.js"
      },
      "import": {
        "types": "./dist/addons/index.d.ts",
        "default": "./dist/addons/index.js"
      }
    },
    "./addons/*": {
      "require": {
        "types": "./dist/cjs/addons/*.d.ts",
        "default": "./dist/cjs/addons/*.js"
      },
      "import": {
        "types": "./dist/addons/*.d.ts",
        "default": "./dist/addons/*.js"
      }
    },
    "./middlewares": {
      "require": {
        "types": "./dist/cjs/middlewares/index.d.ts",
        "default": "./dist/cjs/middlewares/index.js"
      },
      "import": {
        "types": "./dist/middlewares/index.d.ts",
        "default": "./dist/middlewares/index.js"
      }
    },
    "./middlewares/*": {
      "require": {
        "types": "./dist/cjs/middlewares/*.d.ts",
        "default": "./dist/cjs/middlewares/*.js"
      },
      "import": {
        "types": "./dist/middlewares/*.d.ts",
        "default": "./dist/middlewares/*.js"
      }
    },
    "./package.json": "./package.json",
    "./*.mjs": {
      "default": "./dist/*.mjs"
    },
    "./*.cjs": {
      "default": "./dist/*.cjs"
    },
    "./*": {
      "default": "./dist/*.js"
    }
  },
  "files": [
    "dist"
  ],
  "sideEffects": false,
  "scripts": {
    "start": "npm run build && npm run test",
    "lint": "eslint '{src,test}/**/*.{js,ts}'",
    "lint:fix": "eslint '{src,test}/**/*.{js,ts}' --fix",
    "prebuild": "rimraf dist && rimraf coverage && npm run lint",
    "build": "tsc && tsc --project tsconfig.cjs.json && echo '{\"type\": \"commonjs\"}' > ./dist/cjs/package.json && rollup -c",
    "mock": "node scripts/mockServer.js",
    "mock:wait": "wait-on -t 10000 http://localhost:9876/ping",
    "test": "concurrently --success first -k 'npm run mock:wait && jest' 'npm run mock'",
    "test:karma": "concurrently --success first -k 'npm run mock:wait && karma start --single-run --browsers ChromeHeadless,FirefoxHeadless,SafariNative karma.conf.cjs' 'npm run mock'",
    "test:karma:watch": "concurrently --success first -k 'npm run mock:wait && karma start --auto-watch --browsers ChromeHeadless karma.conf.cjs' 'npm run mock'",
    "test:deno": "concurrently --success first -k 'npm run mock:wait && deno test --allow-net ./test/deno/wretch_test.ts' 'npm run mock'",
    "changelog": "conventional-changelog -p wretch -i CHANGELOG.md -s -r 0",
    "doc": "typedoc --out docs/api --readme none --excludeInternal src/index.ts 'src/addons/*.ts' 'src/middlewares/*.ts'",
    "prepare": "npm run build",
    "prepare:release": "npm run start && npm run doc && npm run changelog"
  },
  "author": "Julien Elbaz",
  "license": "MIT",
  "devDependencies": {
    "@eslint/js": "^9.4.0",
    "@fastify/basic-auth": "^5.1.0",
    "@fastify/cors": "^8.4.1",
    "@fastify/formbody": "^7.4.0",
    "@fastify/multipart": "^8.0.0",
    "@rollup/plugin-node-resolve": "^15.2.3",
    "@rollup/plugin-terser": "^0.4.4",
    "@rollup/plugin-typescript": "^11.1.5",
    "@types/eslint__js": "^8.42.3",
    "@types/form-data": "^2.5.0",
    "@types/jest": "^29.5.8",
    "@types/lodash-es": "^4.17.11",
    "@types/node": "^20.9.2",
    "abortcontroller-polyfill": "^1.7.5",
    "concurrently": "^8.2.2",
    "conventional-changelog-cli": "^2.2.2",
    "conventional-changelog-wretch": "file:scripts/conventional-changelog-wretch",
    "dtrace-provider": "^0.8.8",
    "eslint": "^8.57.0",
    "fastify": "^4.24.3",
    "form-data": "^4.0.0",
    "jasmine-core": "^5.1.1",
    "jest": "^29.7.0",
    "karma": "^6.4.2",
    "karma-chrome-launcher": "^3.2.0",
    "karma-firefox-launcher": "^2.1.2",
    "karma-jasmine": "^5.1.0",
    "karma-safari-launcher": "^1.0.0",
    "karma-safarinative-launcher": "^1.1.0",
    "lodash-es": "^4.17.21",
    "node-fetch": "^2",
    "querystring": "^0.2.1",
    "rimraf": "^5.0.5",
    "rollup": "^4.5.0",
    "ts-jest": "^29.1.1",
    "tslib": "^2.6.2",
    "typedoc": "^0.26.6",
    "typescript": "^5.4.5",
    "typescript-eslint": "^7.11.0",
    "wait-on": "^7.2.0",
    "whatwg-url": "^14.0.0"
  },
  "jest": {
    "testPathIgnorePatterns": [
      "/node_modules/",
      "/test/browser/",
      "/test/deno"
    ],
    "transform": {
      "^.+\\.tsx?$": [
        "ts-jest",
        {
          "tsconfig": "test/tsconfig.json"
        }
      ]
    },
    "resolver": "<rootDir>/test/resolver.cjs",
    "testRegex": "(/__tests__/.*|(\\.|/)(test|spec))\\.(jsx?|tsx?)$",
    "moduleFileExtensions": [
      "ts",
      "tsx",
      "js",
      "jsx",
      "json"
    ],
    "collectCoverage": true,
    "collectCoverageFrom": [
      "src/**/*.{js,ts}"
    ],
    "coveragePathIgnorePatterns": [
      "src/index.all.ts",
      "src/addons/index.ts"
    ]
  }
}

import typescript from '@rollup/plugin-typescript';
import terser from '@rollup/plugin-terser';
import { nodeResolve } from '@rollup/plugin-node-resolve';

const addons = ["abort", "basicAuth", "formData", "formUrl", "perfs", "queryString", "progress"]
const middlewares = ["dedupe", "delay", "retry", "throttlingCache"]

const common = {
  plugins: [
    typescript({
      importHelpers: true
    }),
    nodeResolve(),
    terser({
      ecma: 2018,
      output: {
        comments: false,
      },
      compress: {
        booleans_as_integers: true,
        passes: 2
      }
    })
  ],
  external: ["url"]
}

const formats = ["umd", "cjs", "esm"]
const outputs = output => formats.map(format => ({
  ...output,
  format,
  file:
    format === "cjs" ? output.file.replace(".js", ".cjs") :
      format === "esm" ? output.file.replace(".js", ".mjs") :
        output.file
}))

export default [
  {
    input: "./src/index.ts",
    output: outputs({
      file: "dist/bundle/wretch.min.js",
      format: "umd",
      name: "wretch",
      exports: "default",
      sourcemap: true
    }),
    ...common
  },
  {
    input: "./src/index.all.ts",
    output: [
      ...outputs({
        file: "dist/bundle/wretch.all.min.js",
        format: "umd",
        name: "wretch",
        exports: "default",
        sourcemap: true
      })
    ],
    ...common
  },
  ...addons.map(addon => ({
    input: `./src/addons/${addon}.ts`,
    output: outputs({
      file: `dist/bundle/addons/${addon}.min.js`,
      format: "umd",
      name: `wretch${addon.charAt(0).toLocaleUpperCase() + addon.slice(1)}Addon`,
      exports: "default",
      sourcemap: true
    }),
    ...common
  })),
  ...middlewares.map(middleware => ({
    input: `./src/middlewares/${middleware}.ts`,
    output: outputs({
      file: `dist/bundle/middlewares/${middleware}.min.js`,
      format: "umd",
      name: `wretch${middleware.charAt(0).toLocaleUpperCase() + middleware.slice(1)}Middleware`,
      exports: "named",
      sourcemap: true
    }),
    ...common
  }))
]
