				directory: ".",
				releaseGroups: {
					client: {
						include: [...fluidScopes, "fluid-framework", "@types/jest-environment-puppeteer"],
						rootPackageName: "client-release-group-root",
						defaultInterdependencyRange: "workspace:~",
