				directory: "server/gitrest",
				releaseGroups: {
					"gitrest": {
						include: [...fluidScopes, "@fluid-example"],
						rootPackageName: "gitrest-release-group-root",
						defaultInterdependencyRange: "workspace:~",
						adoPipelineUrl:
