function createProjectListByEnv(itemsMapRaw) {
  const {default: defaultProjects, ...itemsMap} = itemsMapRaw
  for (const envKey in itemsMap) {
    if (boolean(process.env[envKey])) {
      return createProjectList(itemsMap[envKey])
    }
  }
  return createProjectList(defaultProjects)
}

function createProjectList(items) {
  items = items.filter(Boolean)
  const list = []
  for (const item of items) {
    if (typeof item === 'string') {
      const project = Object.assign(
        {},
        createDefaultConfig(),
        {
          testMatch: [`<rootDir>/src/${item}/**/*.test.js`],
        },
        {
          displayName: item,
        },
      )
      list.push(project)
    } else {
      for (const key in item) {
        const val = item[key]
        const project = Object.assign(
          {},
          createDefaultConfig(),
          {
            testMatch: [`<rootDir>/src/${key}/**/*.test.js`],
          },
          val,
          {displayName: key},
        )
        list.push(project)
      }
    }
  }
  return list
}

function createCompatProjects(browsers) {
  const projects = {}
  for (const {
    name,
    effectorBuild,
    needPolyfill,
    capabilitiesTag,
    capabilities,
    noAsyncAwait = false,
  } of browsers) {
    projects[`compat/${name}`] = {
      automock: false,
      testEnvironment: 'node',
      testMatch: ['<rootDir>/src/compat/**/*.test.ts'],
      transform,
      testTimeout: 120e3,
      runner: './tools/remoteDeviceTestRunner/browserstackTestRunner.js',
      globals: {
        needForest: false,
        effectorBuild,
        needPolyfill,
        capabilitiesTag,
        capabilities,
        noAsyncAwait,
      },
    }
  }
  return projects
}

function boolean(value) {
  switch (value) {
    case 'no':
    case 'false':
    case false:
      return false
    case 'yes':
    case 'true':
    case true:
      return true
    case null:
    case undefined:
    default:
      return false
  }
}

