import { defineConfig } from 'cypress';
import { addMatchImageSnapshotPlugin } from 'cypress-image-snapshot/plugin';

export default defineConfig({
    component: {
        setupNodeEvents(on, config) {
            on('task', {
                log: (message: string) => {
                    // eslint-disable-next-line no-console
                    console.log(message);
                    return null;
                },
            });
            addMatchImageSnapshotPlugin(on, config);
        },
        devServer: {
            framework: 'react',
            bundler: 'vite',
        },
        specPattern: ['**/__tests__/**/*-{spec,test}.{ts,tsx}'],
        viewportWidth: 1000,
        viewportHeight: 600,
    },
});

/// <reference types="cypress"/>

declare module 'enzyme-adapter-react-16';
declare module 'es6-promise-polyfill' {
    export = { Promise };
}
declare module 'conventional-changelog' {
    import { Readable } from 'stream';

    function conventionalChangelog(...args: any[]): Readable;
    export = conventionalChangelog;
}
declare module 'conventional-changelog-conventionalcommits';
declare module 'postcss-discard-comments';
declare module '@alifd/doc-parser';
declare module '@alifd/api-extractor';
declare module 'markdown-it';
declare module 'markdown-it-anchor';
declare module 'remark';
declare module '@alifd/adaptor-helper';
declare module 'solarlunar';

declare module 'lodash.clonedeep' {
    export { cloneDeep as default } from 'lodash';
}

declare const mountNode: HTMLDivElement;

declare module 'react-lifecycles-compat' {
    import { ComponentType } from 'react';

    export function polyfill<C extends ComponentType>(Component: C): C;
}

var next = require('./index');

next.locales = {};
next.locales['en-us'] = require('./lib/locale/en-us.js');
next.locales['ja-ja'] = require('./lib/locale/ja-jp.js');
next.locales['ja-jp'] = require('./lib/locale/ja-jp.js');
next.locales['zh-cn'] = require('./lib/locale/zh-cn.js');
next.locales['zh-hk'] = require('./lib/locale/zh-hk.js');
next.locales['zh-tw'] = require('./lib/locale/zh-tw.js');
next.locales['vi-vn'] = require('./lib/locale/vi-vn.js');
next.locales['it-it'] = require('./lib/locale/it-it.js');
next.locales['pt-pt'] = require('./lib/locale/pt-pt.js');
next.locales['ko-kr'] = require('./lib/locale/ko-kr.js');
next.locales['ms-my'] = require('./lib/locale/ms-my.js');
