module.exports = next;

var next = require('./lib/index.js');

next.version = '1.27.31';

module.exports = next;

import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';

// https://vitejs.dev/config/
export default defineConfig({
    plugins: [
        react(),
        {
            name: 'my-polyfill',
            config() {
                return {
                    resolve: {
                        alias: [
                            {
                                find: /^moment$/,
                                replacement: 'moment/moment.js',
                            },
                        ],
                    },
                };
            },
        },
    ],
});
