{
	"$schema": "https://biomejs.dev/schemas/1.9.2/schema.json",
	"vcs": {
		"enabled": true,
		"clientKind": "git",
		"useIgnoreFile": true
	},
	"files": {
		"ignoreUnknown": false,
		"ignore": []
	},
	"formatter": {
		"enabled": true,
		"useEditorconfig": true,
		"formatWithErrors": false,
		"indentStyle": "tab",
		"indentWidth": 2,
		"lineEnding": "lf",
		"lineWidth": 110,
		"attributePosition": "auto",
		"bracketSpacing": true,
		"ignore": ["**/.next", "**/node_modules", "**/pnpm-lock.yaml"]
	},
	"organizeImports": {
		"enabled": true
	},
	"linter": {
		"enabled": true,
		"rules": {
			"recommended": false,
			"complexity": {
				"noBannedTypes": "error",
				"noUselessThisAlias": "error",
				"noUselessTypeConstraint": "error",
				"useLiteralKeys": "error",
				"useOptionalChain": "error"
			},
			"correctness": {
				"noEmptyPattern": "off",
				"noPrecisionLoss": "error",
				"noUnusedVariables": "off",
				"useArrayLiterals": "off"
			},
			"style": {
				"noDefaultExport": "off",
				"noInferrableTypes": "error",
				"noNamespace": "error",
				"useAsConstAssertion": "error",
				"useBlockStatements": "off",
				"useConsistentArrayType": "off",
				"useForOf": "error",
				"useImportType": "error",
				"useShorthandFunctionType": "error"
			},
			"suspicious": {
				"noEmptyBlockStatements": "off",
				"noEmptyInterface": "off",
				"noExplicitAny": "error",
				"noExtraNonNullAssertion": "error",
				"noMisleadingInstantiator": "error",
				"noUnsafeDeclarationMerging": "error",
				"useAwait": "off",
				"useNamespaceKeyword": "error"
			}
		},
		"ignore": ["**/*.js", "**/*.jsx", "**/*.mjs", "src/script/**/*.ts"]
	},
	"javascript": {
		"formatter": {
			"jsxQuoteStyle": "double",
			"quoteProperties": "asNeeded",
			"trailingCommas": "all",
			"semicolons": "always",
			"arrowParentheses": "always",
			"bracketSameLine": false,
			"quoteStyle": "double",
			"attributePosition": "auto",
			"bracketSpacing": true
		}
	},
	"overrides": [
		{
			"include": [
				"src/app/**/{page,layout,loading,route,not-found,error,global-error,default,robots,sitemap,opengraph-image}.ts?(x)",
				"src/i18n.ts",
				"*.d.ts",
				"tailwind.config.ts",
				"prettier.config.js",
				"middleware.ts",
				"commitlint.config.ts",
				"vitest.config.ts"
			],
			"linter": {
				"rules": {
					"style": {
						"noDefaultExport": "off"
					}
				}
			}
		}
	]
}

import type { UserConfig } from "@commitlint/types";

const Configuration: UserConfig = {
	extends: ["@commitlint/config-conventional"],
	rules: {
		"body-max-length": [1, "always", 100],
		"body-max-line-length": [1, "always", 100],
	},
};

export default Configuration;

type NonFalsy<T> = T extends false | 0 | "" | null | undefined | 0n ? never : T;

interface Array<T> {
	includes(searchElement: unknown, fromIndex?: number): searchElement is T;
}

interface ReadonlyArray<T> {
	includes(searchElement: unknown, fromIndex?: number): searchElement is T;
}

interface Body {
	json(): Promise<unknown>;
}

interface Array<T> {
	filter(predicate: BooleanConstructor, thisArg?: unknown): NonFalsy<T>[];
}

interface ReadonlyArray<T> {
	filter(predicate: BooleanConstructor, thisArg?: unknown): NonFalsy<T>[];
}

interface ArrayConstructor {
	isArray(arg: unknown): arg is unknown[];
}

interface JSON {
	/**
	 * Converts a JavaScript Object Notation (JSON) string into an object.
	 * @param text A valid JSON string.
	 * @param reviver A function that transforms the results. This function is called for each member of the object.
	 * If a member contains nested objects, the nested objects are transformed before the parent object is.
	 */
	parse(text: string, reviver?: (this: unknown, key: string, value: unknown) => unknown): unknown;
}

interface Set<T> {
	has(value: unknown): value is T;
}

import type en from "./messages/en-US.json";

type Messages = typeof en;

declare global {
	// Use type safe message keys
	interface IntlMessages extends Messages {}
}

import type { MDXComponents } from "mdx/types";
import Link, { type LinkProps } from "next/link";

export function useMDXComponents(components: MDXComponents): MDXComponents {
	return {
		a: (props) => <Link {...(props as LinkProps)} />,
		...components,
	};
}

