module.exports = {
  root: true,
  extends: ["plugin:prettier/recommended"],
  parserOptions: {
    ecmaVersion: 2018,
    sourceType: "module"
  },
  overrides: [
    {
      parser: "@typescript-eslint/parser",
      files: ["*.ts", "*.tsx"],
      plugins: ["@typescript-eslint"],
      extends: [
        "plugin:@typescript-eslint/recommended", // Uses the recommended rules from the @typescript-eslint/eslint-plugin
        "prettier", // Uses eslint-config-prettier to disable ESLint rules from @typescript-eslint/eslint-plugin that would conflict with prettier
        "plugin:prettier/recommended" // Enables eslint-plugin-prettier and eslint-config-prettier. This will display prettier errors as ESLint errors. Make sure this is always the last configuration in the extends array.
      ],
      rules: {
        "prefer-const": "off", // Disabled in tests
        "@typescript-eslint/no-explicit-any": "off", // Disabled in tests
        "@typescript-eslint/no-var-requires": "off", // Used extensively for page requires
        "@typescript-eslint/ban-ts-ignore": "off",
        "@typescript-eslint/ban-ts-comment": "off"
      }
    },
    {
      files: ["*.ts", "*.tsx"],
      excludedFiles: ["**/tests/**/*.ts"],
      rules: {
        "prefer-const": "error", // Disabled in tests
        "@typescript-eslint/no-explicit-any": "warn" // Disabled in tests
      }
    },
    {
      files: ["*.ts", "*.tsx"],
      excludedFiles: [
        "**/cypress/integration/*.ts",
        "**/cypress/custom-commands.ts"
      ],
      plugins: ["promise"],
      rules: {
        "require-await": "error",
        "promise/catch-or-return": "error"
      }
    },
    {
      parser: "@typescript-eslint/parser",
      files: ["**/cypress/**/*.ts"],
      plugins: ["@typescript-eslint"],
      rules: {
        "@typescript-eslint/no-namespace": "off"
      }
    }
  ]
};

{
  "trailingComma": "none",
  "endOfLine": "auto"
}

module.exports = {
  presets: [
    ["@babel/preset-env", { targets: { node: "current" } }],
    "@babel/preset-typescript"
  ],
  plugins: ["@babel/plugin-proposal-class-properties"]
};

const Sequencer = require("@jest/test-sequencer").default;

class CustomSequencer extends Sequencer {
  constructor() {
    super();
  }

  sort(tests) {
    // Test structure information
    // https://github.com/facebook/jest/blob/6b8b1404a1d9254e7d5d90a8934087a9c9899dab/packages/jest-runner/src/types.ts#L17-L21
    const copyTests = Array.from(tests);
    return copyTests.sort((testA, testB) => {
      // FIXME: figure out why this test started failing if run after another test
      if (testA.path.includes("serverless-trace.test")) {
        return -1;
      }
      if (testB.path.includes("serverless-trace.test")) {
        return 1;
      }
      return testA.path > testB.path ? 1 : -1;
    });
  }
}

module.exports = CustomSequencer;

{
  "testMatch": ["**/integration/**/*.test.[jt]s?(x)"],
  "testPathIgnorePatterns": ["/cypress/", "/sharp_node_modules/"],
  "watchPathIgnorePatterns": ["/fixture/"],
  "modulePathIgnorePatterns": [
    "/sharp_node_modules/"
  ]
}

{
  "version": "3.8.0-alpha.0",
  "npmClient": "yarn",
  "useWorkspaces": true,
  "command": {
    "publish": {
      "ignoreChanges": [
        "*.md"
      ],
      "message": "chore(release): publish %s"
    },
    "bootstrap": {
      "ignore": "component-*",
      "npmClientArgs": [
        "--no-package-lock"
      ]
    }
  },
  "packages": [
    "packages/compat-layers/*",
    "packages/serverless-components/*",
    "packages/libs/*",
    "packages/deprecated/*"
  ]
}

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "automerge": true,
  "automergeType": "pr",
  "ignorePaths": [
    "packages/deprecated/**",
    "packages/serverless-components/nextjs-component/examples/**",
    "packages/serverless-components/nextjs-component/**",
    "packages/serverless-components/nextjs-cdk-construct/examples/**",
    "packages/serverless-components/nextjs-component/**",
    "packages/libs/core/sharp_node_modules/**",
    "**/tests/**"
  ],
  "packageRules": [
    {
      "matchUpdateTypes": ["minor", "patch", "pin", "digest"],
      "automerge": true
    },
    {
      "packagePatterns": ["@aws-cdk"],
      "groupName": "aws-cdk"
    },
    {
      "packagePatterns": ["eslint"],
      "groupName": "eslint"
    },
    {
      "packagePatterns": ["react"],
      "groupName": "react"
    },
    {
      "packagePatterns": ["@aws-sdk"],
      "groupName": "aws-sdk"
    },
    {
      "packagePatterns": ["@cypress", "cypress"],
      "groupName": "cypress"
    },
    {
      "packagePatterns": ["@babel"],
      "groupName": "babel"
    },
    {
      "packagePatterns": ["@docusaurus"],
      "groupName": "docusaurus"
    },
    {
      "packagePatterns": ["@jest", "jest"],
      "groupName": "jest"
    }
  ],
  "ignoreDeps": [
    "@rollup/plugin-commonjs",
    "@rollup/plugin-node-resolve",
    "fs-extra",
    "sharp",
    "globby",
    "node-fetch",
    "@types/node-fetch",
    "next",
    "cimg/node"
  ],
  "prHourlyLimit": 5,
  "prConcurrentLimit": 10,
  "rangeStrategy": "bump",
  "rebaseWhen": "conflicted"
}
