{
	"name": "peer",
	"version": "1.0.2",
	"keywords": [
		"peerjs",
		"webrtc",
		"p2p",
		"rtc"
	],
	"description": "PeerJS server component",
	"homepage": "https://peerjs.com",
	"bugs": {
		"url": "https://github.com/peers/peerjs-server/issues"
	},
	"repository": {
		"type": "git",
		"url": "https://github.com/peers/peerjs-server"
	},
	"license": "MIT",
	"contributors": [],
	"type": "module",
	"exports": {
		".": {
			"import": {
				"types": "./dist/peer.d.ts",
				"default": "./dist/module.mjs"
			},
			"require": {
				"types": "./dist/peer.d.ts",
				"default": "./dist/index.cjs"
			}
		}
	},
	"main": "dist/index.cjs",
	"module": "dist/module.mjs",
	"source": "src/index.ts",
	"binary": "dist/bin/peerjs.js",
	"types": "dist/peer.d.ts",
	"bin": {
		"peerjs": "dist/bin/peerjs.js"
	},
	"funding": {
		"type": "opencollective",
		"url": "https://opencollective.com/peer"
	},
	"collective": {
		"type": "opencollective",
		"url": "https://opencollective.com/peer"
	},
	"files": [
		"dist/"
	],
	"engines": {
		"node": ">=14"
	},
	"targets": {
		"binary": {
			"source": "bin/peerjs.ts"
		},
		"main": {},
		"module": {}
	},
	"scripts": {
		"format": "prettier --write .",
		"format:check": "prettier --check .",
		"build": "parcel build",
		"lint": "eslint --ext .js,.ts . && npm run check",
		"check": "tsc --noEmit",
		"test": "npm run lint && jest",
		"coverage": "jest --coverage",
		"start": "node dist/bin/peerjs.js --port ${PORT:=9000}",
		"dev": "nodemon --watch src -e ts --exec 'npm run build && npm run start'",
		"semantic-release": "semantic-release"
	},
	"dependencies": {
		"@types/express": "^4.17.3",
		"@types/ws": "^7.2.3 || ^8.0.0",
		"cors": "^2.8.5",
		"express": "^4.17.1",
		"node-fetch": "^3.3.0",
		"ws": "^7.2.3 || ^8.0.0",
		"yargs": "^17.6.2"
	},
	"devDependencies": {
		"@codedependant/semantic-release-docker": "^5.0.3",
		"@parcel/core": "~2.13.0",
		"@parcel/packager-ts": "~2.13.0",
		"@parcel/transformer-typescript-types": "~2.13.0",
		"@semantic-release/changelog": "^6.0.1",
		"@semantic-release/git": "^10.0.1",
		"@swc/core": "^1.3.35",
		"@swc/helpers": "^0.5.1",
		"@swc/jest": "^0.2.24",
		"@tsconfig/node16": "^16.1.0",
		"@tsconfig/strictest": "^2.0.1",
		"@types/cors": "^2.8.6",
		"@types/jest": "^29.4.0",
		"@types/node": "^14.18.33",
		"@types/yargs": "^17.0.19",
		"@typescript-eslint/eslint-plugin": "^6.0.0",
		"@typescript-eslint/parser": "^6.0.0",
		"eslint": "^8.0.0",
		"jest": "^29.4.2",
		"mock-socket": "^9.1.5",
		"parcel": "~2.13.0",
		"prettier": "^3.0.0",
		"semantic-release": "^22.0.0",
		"typescript": "^5.1.6"
	}
