  return browserify({
      basedir: '.',
      debug: true,
      entries: ['app/src/index.ts'],
      cache: {},
      packageCache: {}
    })
    .plugin(tsify)
    .bundle()
    .pipe(source('bundle.js'))
    .pipe(gulp.dest("dist/js"));
});

