{
	"root": true,
	"parser": "@typescript-eslint/parser",
	"parserOptions": {
		"ecmaVersion": 12,
		"sourceType": "module"
	},
	"plugins": [
		"@typescript-eslint",
		"surveyjs"
	],
	"extends": [
		"eslint:recommended",
		"plugin:@typescript-eslint/recommended"
	],
	"rules": {
		"surveyjs/no-test-only": 2,
		"surveyjs/no-test-debug": 2,
		"surveyjs/no-imports-from-entries": 2,
		"no-console": 2, // Remember, this means error!
		"indent": [
			"error",
			2,
			{
				"SwitchCase": 1,
				"MemberExpression": 1,
				"CallExpression": {
					"arguments": 1
				}
			}
		],
		"no-trailing-spaces": "error",
		"no-multi-spaces": "error",
		"block-spacing": "error",
		"comma-spacing": "error",
		"key-spacing": "error",
		"semi-spacing": "error",
		"object-curly-spacing": [
			"error",
			"always"
		],
		"space-before-blocks": [
			"error",
			"always"
		],
		"space-in-parens": [
			"error",
			"never"
		],
		"array-bracket-spacing": [
			"error",
			"never"
		],
		"computed-property-spacing": "error",
		// "keyword-spacing": ["error", { 
		// 	"overrides": { 
		// 		"if": { "after": false }, 
		// 		"for": { "after": false }, 
		// 		"while": { "after": false }, 
		// 		"switch": { "after": false },
		// 		//"this": { "after": false, "before": false },
		// 		"new": { "before": false },
		// 		"function":  { "after": false, "before": false },
		// 		"catch": { "after": false }
		// 	} 
		// }],
		"no-multiple-empty-lines": [
			"error",
			{
				"max": 1
			}
		],
		"no-whitespace-before-property": "error",
		// sub group: semicolons
		"semi": [
			"error",
			"always"
		],
		"semi": "error",
		"no-extra-semi": [
			"error"
		],
		"@typescript-eslint/member-delimiter-style": [
			"error",
			{
				"multiline": {
					"delimiter": "comma",
					"requireLast": true
				},
				"singleline": {
					"delimiter": "comma",
					"requireLast": false
				},
				"overrides": {
					"interface": {
						"multiline": {
							"delimiter": "semi",
							"requireLast": true
						}
					}
				}
			}
		],
		"no-case-declarations": "off",
		"quotes": [
			"error",
			"double",
			{
				"avoidEscape": true
			}
		],
		"no-useless-escape": "off",
		// group: add in future
		"@typescript-eslint/no-var-requires": "off", // TODO add ignores
		"no-unexpected-multiline": "off",
		"no-constant-condition": "off",
		"@typescript-eslint/explicit-function-return-type": "off",
		"@typescript-eslint/no-this-alias": "off",
		"@typescript-eslint/class-name-casing": "off",
		"@typescript-eslint/camelcase": "off",
		"no-extra-boolean-cast": "off",
		//sub group: js native functions
		"no-prototype-builtins": "off",
		"prefer-spread": "off",
		//sub group: var definition:
		"no-var": "off",
		"prefer-const": "off",
		"@typescript-eslint/no-use-before-define": "off",
		"no-unsafe-finally": "off",
		//"curly": [ "error", "multi-line", "consistent" ], 
		//"eqeqeq": ["error", "allow-null"],
		"@typescript-eslint/no-unused-vars": "off",
		// group: add in very far future
		//"no-eval": "error", 
		//"no-extend-native": "error", 
		"@typescript-eslint/interface-name-prefix": "off",
		"prefer-rest-params": "off",
		"@typescript-eslint/no-explicit-any": "off",
		"@typescript-eslint/consistent-type-assertions": "off",
		"@typescript-eslint/no-inferrable-types": "off",
		// group: usless rules
		"no-empty": "off",
		"@typescript-eslint/no-empty-interface": "off",
		"@typescript-eslint/no-empty-function": "off",
		"@typescript-eslint/ban-types": "off",
		// group: ok
		//"@typescript-eslint/consistent-type-assertions": [ "error", { "assertionStyle": "angle-bracket" }],
		"linebreak-style": [
			"off",
			"windows"
		]
	},
	"overrides": [
		{
			"files": [
				"src/*.ts"
			],
			"rules": {
				"no-restricted-properties": [
					"error",
					{
						"object": "window",
						"property": "document"
					}
				],
				"no-restricted-globals": [
					"error",
					{
						"name": "document",
						"message": "Do not use document into survey-core. Use methods from DomDocumentHelper"
					},
					{
						"name": "window",
						"message": "Do not use window into survey-core. Use method from DomWindowHelper"
					}
				]
			}
		},
		{
			"files": [
				"*.jsx"
			],
			"parserOptions": {
				"ecmaFeatures": {
					"jsx": true
				}
			},
			"plugins": [
				"@typescript-eslint",
				"react"
			],
			"extends": [
				"eslint:recommended",
				"plugin:@typescript-eslint/recommended",
				"plugin:react/recommended"
			]
		},
		{
			"files": [
				"*.js"
			],
			"rules": {
				"no-undef": "off"
			}
		}
	]
}

