const OFF = 'off';
const ERROR = 'error';
const NEVER = 'never';

const baseExtends = [
  'airbnb',
  'plugin:@next/next/recommended',
  'plugin:eslint-comments/recommended',
  'plugin:jest/recommended',
  'plugin:jsx-a11y/recommended',
  'plugin:react/recommended',
  'prettier',
];

const basePlugins = [
  'eslint-comments',
  'eslint-plugin-gestalt',
  'import',
  'jest',
  'jsx-a11y',
  'react',
  'react-compiler',
  'react-hooks',
  'simple-import-sort',
  'testing-library',
  'validate-jsx-nesting',
];

const baseRules = {
  'eslint-comments/no-unused-disable': ERROR,
  'gestalt/only-valid-tokens': ERROR,
  'import/extensions': [
    ERROR,
    {
      css: 'always',
      js: 'never',
      ts: 'never',
      tsx: 'never',
      json: 'always',
      mjs: 'never',
    },
  ],
  'import/first': ERROR,
  'import/newline-after-import': ERROR,
  'import/no-anonymous-default-export': ERROR,
  'import/no-duplicates': ERROR,
  'import/no-extraneous-dependencies': OFF,
  'import/no-namespace': ERROR,
  'import/no-relative-parent-imports': OFF,
  'import/no-unresolved': OFF,
  'import/order': OFF,
  'jsx-a11y/label-has-associated-control': [
    ERROR,
    {
      'labelComponents': ['Label'],
      'labelAttributes': ['htmlFor'],
      'controlComponents': ['CheckBox'],
      'depth': 3,
    },
  ],
  'jsx-a11y/label-has-for': [ERROR, { 'components': ['label'], 'allowChildren': true }],
  'no-await-in-loop': OFF,
  'no-constant-condition': ERROR,
  'no-return-await': OFF,
  'prefer-arrow-callback': OFF,
  'react-hooks/exhaustive-deps': ERROR,
  'react-hooks/rules-of-hooks': ERROR,
  'react/destructuring-assignment': OFF,
  'react/prefer-exact-props': OFF,
  'react/prop-types': OFF, // Already covered by TypeScript
  'react/jsx-filename-extension': OFF,
  'react/jsx-fragments': [ERROR, 'element'],
  'react/jsx-key': [ERROR, { 'checkFragmentShorthand': true }],
  'react/jsx-props-no-spreading': OFF,
  'react/jsx-sort-props': ERROR,
  'react/no-access-state-in-setstate': ERROR,
  'react/no-array-index-key': ERROR,
  'react/react-in-jsx-scope': OFF,
  'react/require-default-props': OFF,
  'react/sort-comp': OFF,
  'react/state-in-constructor': [ERROR, NEVER],
  'react/static-property-placement': [ERROR, 'static public field'],
  'simple-import-sort/imports': [
    ERROR,
    {
      groups: [
        // Groups are an array of arrays, and the outer array adds blank lines between groups
        [
          '^\\u0000', // side-effect imports
          // eslint-disable-next-line global-require
          `^(${require('module').builtinModules.join('|')})(/|$)`, // node builtins
          '^react',
          '^', // catch-all for other packages
          '^gestalt',
          '^\\.', // relative paths for "siblings" (e.g. ./Button)
          '^\\.\\.', // relative paths for "parents" (e.g. ../Button)
        ],
      ],
    },
  ],
  'simple-import-sort/exports': ERROR,
  'validate-jsx-nesting/no-invalid-jsx-nesting': ERROR,
};

module.exports = {
  'extends': [...baseExtends],
  'env': {
    'browser': true,
    'es6': true,
  },
  'parser': '@typescript-eslint/parser',
  'plugins': [...basePlugins, '@typescript-eslint'],
  'settings': {
    'next': {
      'rootDir': 'docs/',
    },
  },
  'rules': {
    ...baseRules,
  },
  'overrides': [
    {
      files: ['**/*.{ts,tsx}'],
      extends: [...baseExtends, 'plugin:@typescript-eslint/recommended'],
      rules: {
        ...baseRules,
        'no-shadow': 'off',
        '@typescript-eslint/no-shadow': 'error',
        '@typescript-eslint/prefer-as-const': 'off',
        '@typescript-eslint/no-this-alias': 'off',
        '@typescript-eslint/no-explicit-any': 'off',
        '@typescript-eslint/no-unnecessary-type-constraint': 'off',
        '@typescript-eslint/no-loss-of-precision': 'off',
        '@typescript-eslint/no-unused-vars': [
          'error',
          { 'args': 'after-used', 'argsIgnorePattern': '^_' },
        ],
      },
    },
    {
      files: ['**/*.js'],
      extends: [...baseExtends],
      parser: '@babel/eslint-parser',
      plugins: [...basePlugins],
      rules: {
        ...baseRules,
        'no-unused-vars': [ERROR, { 'args': 'after-used', 'argsIgnorePattern': '^_' }],
      },
    },
    {
      'files': ['packages/gestalt-design-tokens/**/*.{js,ts,tsx}'],
      'rules': {
        'gestalt/only-valid-tokens': OFF,
      },
    },
    {
      'files': ['scripts/templates/*.{js,ts,tsx}'],
      'rules': {
        'import/no-unresolved': OFF,
      },
    },
    {
      'files': ['packages/gestalt-codemods/**/*.{js,ts,tsx}'],
      'rules': {
        'react/jsx-sort-props': OFF,
      },
    },
    {
      'files': ['packages/**/*.{js,ts,tsx}'],
      'rules': {
        '@next/next/no-img-element': OFF,
        'react-compiler/react-compiler': ERROR,
      },
    },
    {
      'files': ['docs/examples/**/*.{js,ts,tsx}'],
      'rules': {
        // Allows us to avoid directing the user off our docs site in examples
        'jsx-a11y/anchor-is-valid': OFF,
        '@next/next/no-img-element': OFF,
      },
    },
    {
      'files': ['**/*.test.{js,ts,tsx}'],
      'env': {
        'jest': true,
      },
      'extends': ['plugin:testing-library/react'],
      'globals': {
        'page': true,
        'browser': true,
      },
      'rules': {
        'import/no-namespace': OFF,
        'jest/expect-expect': [
          ERROR,
          { 'assertFunctionNames': ['expect', 'runInlineTest', 'runTest'] },
        ],
      },
    },
    {
      'files': ['playwright/**/*.ts', 'scripts/templates/*.spec.ts'],
      'extends': ['plugin:playwright/playwright-test'],
      'rules': {
        'playwright/missing-playwright-await': ERROR,
        'playwright/no-element-handle': ERROR,
        'playwright/no-eval': ERROR,
        'playwright/no-focused-test': ERROR,
        'playwright/no-force-option': ERROR,
        'playwright/no-page-pause': ERROR,
        'playwright/no-skipped-test': OFF, // Can be turned on when what's new is removed
        'playwright/no-wait-for-timeout': ERROR,
        'jest/expect-expect': OFF,
        'jest/no-done-callback': OFF,
        'jest/valid-expect': OFF,
      },
    },
  ],
};

{
  "singleQuote": true,
  "overrides": [
    {
      "files": ["*.js", "*.ts", "*.tsx", "*.d.ts"],
      "options": {
        "arrowParens": "always",
        "printWidth": 100,
        "quoteProps": "preserve",
        "tabWidth": 2,
        "trailingComma": "all"
      }
    },
    {
      "files": "*.css",
      "options": {
        "parser": "css",
        "singleQuote": false
      }
    },
    {
      "files": "*.md",
      "options": {
        "parser": "markdown"
      }
    }
  ]
}

{
  "extends": ["stylelint-config-standard", "stylelint-config-prettier"],
  "ignoreFiles": [
    "packages/*/dist/**/*.css",
    "packages/**/*.js",
    "packages/**/*.ts",
    "packages/**/*.tsx",
    "packages/stylelint-plugin-gestalt/src/__fixtures__/**/*.css"
  ],
  "plugins": [
    "stylelint-value-no-unknown-custom-properties",
    "stylelint-order",
    "./packages/stylelint-plugin-gestalt/src/no-invalid-design-tokens.js"
  ],
  "rules": {
    "stylelint-gestalt-plugin/no-invalid-design-tokens": true,
    "alpha-value-notation": null,
    "color-named": [
      "never",
      {
        "ignoreProperties": ["composes"]
      }
    ],
    "custom-property-pattern": "g|color|font|elevation|space|opacity|motion|font-.+",
    "value-keyword-case": [
      "lower",
      {
        "ignoreProperties": ["composes"],
        "camelCaseSvgKeywords": true
      }
    ],
    "declaration-block-no-duplicate-properties": [
      true,
      {
        "ignore": ["consecutive-duplicates"]
      }
    ],
    "declaration-no-important": true,
    "function-url-quotes": "always",
    "max-nesting-depth": 0,
    "media-feature-name-no-unknown": [
      true,
      {
        "ignoreMediaFeatureNames": ["inverted-colors"]
      }
    ],
    "no-descending-specificity": true,
    "no-duplicate-selectors": true,
    "no-unknown-animations": true,
    "csstools/value-no-unknown-custom-properties": [
      true,
      {
        "importFrom": [
          "packages/gestalt-design-tokens/dist/css/vr-theme-web-mapping/variables.css",
          "packages/gestalt-design-tokens/dist/css/classic/variables.css",
          "packages/gestalt-design-tokens/dist/css/classic/variables.css",
          "packages/gestalt/src/Column.css",
          "packages/gestalt/src/Borders.css"
        ]
      }
    ],
    "number-max-precision": 5,
    "order/properties-order": [
      ["composes"],
      {
        "unspecified": "bottomAlphabetical"
      }
    ],
    "property-no-unknown": [
      true,
      {
        "ignoreProperties": ["composes"]
      }
    ],
    "property-no-vendor-prefix": true,
    "rule-empty-line-before": [
      "always",
      {
        "except": ["first-nested"]
      }
    ],
    "selector-class-pattern": "[a-z]+((\\d)|([A-Z0-9][a-z0-9]+))*([A-Z])?",
    "selector-max-id": 0,
    "selector-no-vendor-prefix": true,
    "selector-pseudo-class-no-unknown": [
      true,
      { "ignorePseudoClasses": ["global"] }
    ],
    "unit-disallowed-list": ["em"],
    "value-no-vendor-prefix": true
  }
}

module.exports = {
  presets: [
    ['@babel/preset-env', { modules: process.env.NODE_ENV === 'production' ? false : 'auto' }],
    ['@babel/react', { 'runtime': 'automatic' }],
    '@babel/preset-typescript',
  ],
  plugins: [
    '@babel/proposal-class-properties',
    // '@babel/transform-typescript',
    [
      process.env.NODE_ENV === 'development'
        ? '@babel/plugin-transform-react-jsx-self'
        : '@babel/plugin-transform-react-jsx',
      {
        runtime: 'automatic',
        useBuiltIns: true,
      },
    ],
  ],
};
