// @ts-check
/**
 * @type {import('prettier').Config}
 */
export default {
  tabWidth: 2,
  singleQuote: true,
  trailingComma: 'all',
};

import type { UserConfig } from '@commitlint/types';

export default {
  extends: ['@commitlint/config-conventional'],
  rules: {
    'subject-case': [0],
  },
} as UserConfig;

import eslint from '@eslint/js';
import tsEslint from 'typescript-eslint';
import unusedImports from 'eslint-plugin-unused-imports';
import eslintConfigPrettier from 'eslint-config-prettier';

export default tsEslint.config(
  eslint.configs.recommended,
  ...tsEslint.configs.recommended,
  eslintConfigPrettier,
  {
    plugins: {
      'unused-imports': unusedImports,
    },
  },
  {
    rules: {
      quotes: ['error', 'single', { allowTemplateLiterals: false }],
      'no-unused-vars': 'off',
      '@typescript-eslint/no-unused-vars': 'off',
      'unused-imports/no-unused-imports': 'error',
      'unused-imports/no-unused-vars': 'error',
    },
  },
);

{
  "name": "@aisouler/gkd_subscription",
  "version": "0.0.176",
  "type": "module",
  "gkd": {
    "outDir": "dist",
    "file": "AIsouler_gkd.json5",
    "versionFile": "AIsouler_gkd.version.json5",
    "changelog": "CHANGELOG.md",
    "README.md": "README.md"
  },
  "scripts": {
    "postinstall": "simple-git-hooks",
    "format": "prettier --cache --write ./**/*.{js,cjs,mjs,ts,jsx,tsx,json}",
    "lint": "eslint --cache --fix ./**/*.{js,cjs,mjs,ts,jsx,tsx}",
    "check": "tsc --noEmit && tsx ./scripts/check.ts",
    "build": "tsc --noEmit && tsx ./scripts/build.ts"
  },
  "simple-git-hooks": {
    "pre-commit": "pnpm exec lint-staged",
    "commit-msg": "pnpm exec commitlint --config commitlint.config.ts --edit ${1}",
    "pre-push": "pnpm run check"
  },
  "lint-staged": {
    "*.{js,cjs,mjs,ts,jsx,tsx}": [
      "eslint --cache --fix",
      "prettier --cache --write"
    ],
    "*.json": [
      "prettier --cache --write"
    ]
  },
  "publishConfig": {
    "access": "public",
    "registry": "https://registry.npmjs.org/"
  },
  "files": [
    "./dist"
  ],
  "dependencies": {
    "@commitlint/cli": "19.6.0",
    "@commitlint/config-conventional": "19.6.0",
    "@commitlint/types": "19.5.0",
    "@eslint/js": "9.15.0",
    "@gkd-kit/api": "0.6.0",
    "@gkd-kit/define": "0.0.1",
    "@gkd-kit/tools": "0.5.2",
    "@types/eslint__js": "8.42.3",
    "@types/node": "22.10.1",
    "eslint": "9.15.0",
    "eslint-config-prettier": "9.1.0",
    "eslint-plugin-unused-imports": "4.1.4",
    "json5": "2.2.3",
    "lint-staged": "15.2.10",
    "prettier": "3.4.1",
    "simple-git-hooks": "2.11.1",
    "tsx": "4.19.2",
    "typescript": "5.7.2",
    "typescript-eslint": "8.16.0"
  },
  "volta": {
    "node": "20.13.1"
  },
  "packageManager": "pnpm@9.11.0",
  "engineStrict": true,
  "engines": {
    "node": ">=22"
  }
}

