import { defineConfig, type PlaywrightTestConfig } from "@playwright/test"

const CI = !!process.env.CI

type InferServer<T> = Exclude<T extends Array<infer U> ? U : T, undefined>

type WebServer = InferServer<PlaywrightTestConfig["webServer"]>

export function getWebServer(): WebServer {
  const framework = process.env.FRAMEWORK || "react"

  const frameworks: Record<string, WebServer> = {
    react: {
      cwd: "./examples/next-ts",
      command: "cross-env PORT=3000 pnpm dev",
      url: "http://localhost:3000",
      reuseExistingServer: !CI,
    },
    vue: {
      cwd: "./examples/vue-ts",
      command: "pnpm vite --port 3001",
      url: "http://localhost:3001",
      reuseExistingServer: !CI,
    },
    solid: {
      cwd: "./examples/solid-ts",
      command: "pnpm vite --port 3002",
      url: "http://localhost:3002",
      reuseExistingServer: !CI,
    },
    svelte: {
      cwd: "./examples/svelte-ts",
      command: "pnpm vite --port 3003",
      url: "http://localhost:3003",
      reuseExistingServer: !CI,
    },
  }

  return frameworks[framework]
}

const webServer = getWebServer()

export default defineConfig({
  testDir: "./e2e",
  outputDir: "./e2e/results",
  testMatch: "*.e2e.ts",
  fullyParallel: !CI,
  timeout: 30_000,
  expect: { timeout: 10_000 },
  forbidOnly: !!CI,
  reportSlowTests: null,
  retries: process.env.CI ? 2 : 1,
  workers: process.env.CI ? 1 : "50%",
  reporter: [
    process.env.CI ? ["github", ["junit", { outputFile: "e2e/junit.xml" }]] : ["list"],
    ["html", { outputFolder: "e2e/report", open: "never" }],
  ],
  webServer,
  use: {
    baseURL: webServer.url,
    trace: "retain-on-failure",
    screenshot: "only-on-failure",
    video: "retain-on-failure",
    locale: "en-US",
    timezoneId: "GMT",
  },
})

declare global {
  namespace NodeJS {
    interface ProcessEnv {
      FRAMEWORK: string
    }
  }
}

const camelCase = (str) => {
  return str.replace(/[-_]([a-z])/g, (g) => g[1].toUpperCase())
}

const capitalize = (str) => {
  return str.charAt(0).toUpperCase() + str.slice(1)
}

const multiCapitalize = (str) => {
  return str.split("-").map(capitalize).join(" ")
}

/**
 * @param {import("plop").NodePlopAPI} plop
 */
module.exports = function main(plop) {
  plop.setHelper("camelize", camelCase)
  plop.setHelper("capitalize", capitalize)
  plop.setHelper("multiCapitalize", multiCapitalize)

  plop.setGenerator("machine", {
    description: "Generates a new ui machine",
    prompts: [
      {
        type: "input",
        name: "machine",
        message: "Enter machine name (e.g. menu, popover):",
      },
    ],
    actions(answers) {
      const actions = []

      if (!answers) return actions

      const { machine } = answers

      actions.push({
        type: "addMany",
        templateFiles: "plop/machine/**",
        destination: `packages/machines/{{dashCase machine}}`,
        base: "plop/machine/",
        data: { machine, name: machine },
        abortOnFail: true,
      })

      actions.push({
        type: "addMany",
        templateFiles: "plop/machine-examples/**",
        destination: `examples`,
        base: "plop/machine-examples/",
        data: { machine, name: machine },
        abortOnFail: true,
      })

      actions.push({
        type: "modify",
        path: "shared/src/routes.ts",
        pattern: /\= \[/,
        template: '= [\n  { label: "{{multiCapitalize machine}}", path: "/{{machine}}" },',
      })

      return actions
    },
  })

  plop.setGenerator("utility", {
    description: "Generates a utils package",
    prompts: [
      {
        type: "input",
        name: "name",
        message: "Enter package name:",
      },
      {
        type: "input",
        name: "description",
        message: "The description of this package:",
      },
    ],
    actions(answers) {
      const actions = []

      if (!answers) return actions

      const { name, description } = answers

      actions.push({
        type: "addMany",
        templateFiles: "plop/utility/**",
        destination: `packages/utilities/{{dashCase name}}`,
        base: "plop/utility/",
        data: { description, name },
        abortOnFail: true,
      })

      return actions
    },
  })
}

