{
    "env": {
        "browser": true,
        "es6": true
    },
    "extends": [
        "eslint:recommended",
        "plugin:@typescript-eslint/eslint-recommended"
    ],
    "globals": {
        "Atomics": "readonly",
        "SharedArrayBuffer": "readonly"
    },
    "parser": "@typescript-eslint/parser",
    "parserOptions": {
        "ecmaVersion": 2018,
        "sourceType": "module"
    },
    "plugins": [
        "@typescript-eslint"
    ],
    "rules": {
        "indent": [
            0
        ],
        "linebreak-style": [
            "error",
            "unix"
        ],
        "quotes": [
            0
        ],
        "no-extra-semi": [
            0
        ]
    }
}

{
    "exclude": [
        "src/**/*.js"
    ],
    "include": [
        "dist/index-cli.js",
        "bin/*.js"
    ],
    "extension": [
        ".js"
    ],
    "reporter": [
        "json",
        "html",
        "text-summary"
    ],
    "all": true
}

{
    "extends": "@istanbuljs/nyc-config-typescript"
}

{
    "ignoreDeps": ["os-name", "pdfjs-dist", "marked", "chai", "eslint"]
}

