{
  "name": "pinyin-pro",
  "version": "3.25.0",
  "description": "准确率和性能最优异的汉字转拼音库。获取中文拼音、韵母、声母、声调、首字母，支持拼音匹配",
  "main": "./dist/index.js",
  "module": "./dist/index.mjs",
  "typings": "./types/index.d.ts",
  "scripts": {
    "test": "vitest run --coverage",
    "build": "rollup -c && rollup -c rollup.esm.config.js",
    "commit": "git-cz",
    "lint": "eslint ."
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/zh-lx/pinyin-pro.git"
  },
  "files": [
    "dist",
    "types"
  ],
  "keywords": [
    "拼音",
    "pinyin",
    "汉字",
    "中文",
    "首字母",
    "韵母",
    "声母",
    "音调"
  ],
  "author": "zh-lx",
  "license": "MIT",
  "bugs": {
    "url": "https://github.com/zh-lx/pinyin-pro/issues"
  },
  "homepage": "https://pinyin-pro.cn",
  "exports": {
    ".": {
      "import": {
        "types": "./types/index.d.ts",
        "default": "./dist/index.mjs"
      },
      "require": {
        "types": "./types/index.d.ts",
        "default": "./dist/index.js"
      }
    },
    "./dist/*": "./dist/*",
    "./package.json": "./package.json"
  },
  "devDependencies": {
    "@commitlint/cli": "^11.0.0",
    "@commitlint/config-conventional": "^11.0.0",
    "@pinyin-pro/data": "1.0.3",
    "@rollup/plugin-commonjs": "^17.1.0",
    "@rollup/plugin-json": "^4.1.0",
    "@rollup/plugin-node-resolve": "^11.2.0",
    "@types/jest": "^26.0.20",
    "@typescript-eslint/eslint-plugin": "^4.26.0",
    "@typescript-eslint/parser": "^4.26.0",
    "@vitest/coverage-v8": "^1.5.0",
    "babel-eslint": "^10.1.0",
    "commitizen": "^4.2.2",
    "eslint": "^7.22.0",
    "rollup": "2.60.0",
    "rollup-plugin-alias": "^2.2.0",
    "rollup-plugin-cleanup": "^3.2.1",
    "rollup-plugin-terser": "^7.0.2",
    "rollup-plugin-typescript2": "^0.34.1",
    "typescript": "^4.2.3",
    "vitest": "^1.5.0"
  },
  "config": {
    "commitizen": {
      "path": "node_modules/cz-conventional-changelog"
    }
  },
  "sideEffects": false
}

import path from 'path';
import json from '@rollup/plugin-json';
import { terser } from 'rollup-plugin-terser';
import { nodeResolve } from '@rollup/plugin-node-resolve';
import commonjs from '@rollup/plugin-commonjs'; // commonjs模块转换插件
import cleanup from 'rollup-plugin-cleanup';
import ts from 'rollup-plugin-typescript2';
import alias from 'rollup-plugin-alias';

const plugins = [
  cleanup(),
  json(),
  nodeResolve(),
  ts({
    tsconfig: path.resolve(__dirname, './tsconfig.json'), // 导入本地ts配置
    clean: true,
    useTsconfigDeclarationDir: true,
  }),
  commonjs(),
  alias({
    entries: [{ find: '@', replacement: './lib' }],
  }),
  terser(),
];

module.exports = {
  input: path.resolve('./lib/index.ts'),
  output: [
    {
      exports: 'auto',
      file: path.resolve(__dirname, './dist/index.js'),
      format: 'umd',
      name: 'pinyinPro',
    },
  ],
  plugins,
};

import path from 'path';
import json from '@rollup/plugin-json';
import { nodeResolve } from '@rollup/plugin-node-resolve';
import commonjs from '@rollup/plugin-commonjs'; // commonjs模块转换插件
import ts from 'rollup-plugin-typescript2';
import alias from 'rollup-plugin-alias';

const plugins = [
  json(),
  nodeResolve(),
  ts({
    tsconfig: path.resolve(__dirname, './tsconfig.json'), // 导入本地ts配置
    clean: true,
    useTsconfigDeclarationDir: true,
  }),
  commonjs(),
  alias({
    entries: [{ find: '@', replacement: './lib' }],
  }),
];

module.exports = {
  input: path.resolve('./lib/index.ts'),
  output: [
    {
      exports: 'auto',
      file: path.resolve(__dirname, './dist/index.mjs'),
      format: 'es',
      sourcemap: false,
    },
  ],
  plugins,
};

import { defineConfig } from 'vitest/config';
import path from 'path';

export default defineConfig({
  test: {
    alias: [
      {
        find: '@',
        replacement: path.resolve(__dirname, './lib'),
      },
    ],
    coverage: {
      include: ['lib/**'],
      exclude: ['lib/data/dict1-pinyin.ts']
    }
  },
})
