global.storage = storage;

for (const [key, value] of Object.entries(globalVariables)) {
  Object.defineProperty(global, key, {
    get: () => value,
    set: () => {
      logger.debug(`[shim]: Trying to override internal Rainbow var ${key}`);
    },
  });
