SHELL := /bin/bash
.SILENT:
.PHONY: print-manifests  build


help:
	@grep -E '^[1-9a-zA-Z_-]+:.*?## .*$$|(^#--)' $(MAKEFILE_LIST) \
	| awk 'BEGIN {FS = ":.*?## "}; {printf "\033[32m %-43s\033[0m %s\n", $$1, $$2}' \
	| sed -e 's/\[32m #-- /[33m/'

print-manifests: ## print out build target manifests
	@ls ./build/jiffyReader-* -t |grep -E '.(xpi|zip|crx)' | xargs -I {} sh -c 'unzip -p "{}" manifest.json | jq -r ". | \"Version: \(.version), Name: \(.name), Version Name: \(.version_name), Target: \(.target), Tag: \(.tag)  {}\""'

test-build-manifests: ## test build manifests
	@if make -s print-manifests | grep -E '(\W-\W|null)'; then exit 1; fi



test: ## test 
	@make test-build-manifests
	@declare -A counts ;\
	counts[expectedCount]=8 ;\
	counts[actualCount]=$$(make -s print-manifests | wc -l) ;\
	echo -e "\n$${!counts[@]}\n$${counts[@]}\n" | awk '{print $$1 "\t" $$2}'; \
	if [ $${counts[actualCount]} -ne $${counts[expectedCount]} ] ; then echo 'test failed'; exit 1; else echo 'passed'; fi # fail if unequal;



build: ## build latest using version in bump.txt
	@rm -rf build; 
	@pnpm gh:build;
	# @echo "sleep 5 seconds"; seq 5 | xargs -I{} bash -c 'echo ".";sleep 1;'; 
	@pnpm gh:build:development
	@sleep 5;make -s test

extract: ## extract files
	rm -rf build/extracts
	mkdir build/extracts
	find build -name jiffyReader* | awk -F / '{print $$2}' | xargs -I{} sh -c "unzip build/{} -d build/extracts/{}"

import eslintConfigPrettier from 'eslint-config-prettier';
import eslintPluginImport from 'eslint-plugin-import';
import eslintPluginOrganizeImports from 'eslint-plugin-organize-imports';
import pathAlias from 'eslint-plugin-path-alias';
import globals from 'globals';

/** @type {import('eslint').Linter.Config[]} */
export default [
	{
		languageOptions: {
			globals: { ...globals['shared-node-browser'], ...globals.node },
			parserOptions: {
				ecmaVersion: 'latest',
				sourceType: 'module',
			},
		},
	},
	eslintConfigPrettier,
	{
		plugins: {
			'path-alias': pathAlias,
			'organize-imports': eslintPluginOrganizeImports,
			import: eslintPluginImport,
		},
		rules: {
			'path-alias/no-relative': ['error', { exceptions: ['*.module.css'] }],
			'import/first': 'error',
			'import/newline-after-import': 'error',
			'import/no-duplicates': 'error',
			'import/no-relative-packages': 'error',
			'no-undef': ['error'],
			'no-unused-vars': 'error',
		},
	},
];

import type defaultPrefs from '~services/preferences';

type Prefs = typeof defaultPrefs;
type PrefRecords = Record<string, Pref>;

interface PrefStore {
	global: Prefs;
	local: PrefRecords;
}

interface TabSession {
	brMode: boolean;
	origin?: string;
	tabID?;
}

type TabSessionStore = Record<string, TabSession>;

type UpdateCallback = (tabSessions: PrefRecords) => PrefRecords;

type SetPrefsExternal = (getOrigin: () => Promise<string>, scope: string, newPrefs: Prefs, deleteOldLocal?: boolean) => Promise<void>;

type removeTabSession = (getTab: () => Promise<chrome.tabs.Tab>) => Promise<void>;

interface AppConfigPref {
	displayColorMode: DisplayColorMode;
}

declare namespace NodeJS {
	interface ProcessEnv {
		PLASMO_PUBLIC_DEBUG: string;
		PLASMO_PUBLIC_TARGET: string;
		PLASMO_PUBLIC_SHORTCUT: string;
		PLASMO_PUBLIC_VERSION: string;
		PLASMO_PUBLIC_NAME: string;
		PLASMO_PUBLIC_HOME_URL: string;
		PLASMO_PUBLIC_ENABLE_TRACKING: string
	}
}

declare module '*.scss';

