  "version": "2.3.0",
  "description": "Electron build tooling based on Vite",
  "type": "module",
  "main": "dist/index.cjs",
  "module": "dist/index.mjs",
  "types": "dist/index.d.ts",
  "exports": {
    ".": {
      "types": "./dist/index.d.ts",
      "import": "./dist/index.mjs",
      "require": "./dist/index.cjs"
    },
    "./node": {
      "types": "./node.d.ts"
    },
    "./package.json": "./package.json"
  },
  "bin": {
    "electron-vite": "bin/electron-vite.js"
  },
  "files": [
    "bin",
    "dist",
    "node.d.ts"
  ],
  "engines": {
    "node": "^18.0.0 || >=20.0.0"
  },
  "packageManager": "pnpm@8.6.10",
  "author": "Alex Wei<https://github.com/alex8088>",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "git+https://github.com/alex8088/electron-vite.git"
  },
  "bugs": {
    "url": "https://github.com/alex8088/electron-vite/issues"
  },
  "homepage": "https://electron-vite.org",
  "keywords": [
    "electron",
    "vite",
    "cli",
    "plugin"
  ],
  "scripts": {
    "format": "prettier --write .",
    "lint": "eslint --ext .ts src/**",
    "typecheck": "tsc --noEmit",
    "build": "pnpm run lint && rollup -c rollup.config.ts --configPlugin typescript"
  },
  "simple-git-hooks": {
    "pre-commit": "npx lint-staged",
    "commit-msg": "node scripts/verifyCommit.js $1"
  },
  "lint-staged": {
    "*.js": [
      "prettier --write"
    ],
    "*.ts?(x)": [
      "eslint",
      "prettier --parser=typescript --write"
    ]
  },
  "peerDependencies": {
    "@swc/core": "^1.0.0",
    "vite": "^4.0.0 || ^5.0.0"
  },
  "peerDependenciesMeta": {
    "@swc/core": {
      "optional": true
    }
  },
  "devDependencies": {
    "@rollup/plugin-json": "^6.1.0",
    "@rollup/plugin-node-resolve": "^15.2.3",
    "@rollup/plugin-typescript": "^11.1.6",
    "@swc/core": "^1.6.5",
    "@types/node": "^18.19.39",
    "@typescript-eslint/eslint-plugin": "^7.13.1",
    "@typescript-eslint/parser": "^7.13.1",
    "eslint": "^8.57.0",
    "eslint-config-prettier": "^9.1.0",
    "eslint-plugin-prettier": "^5.1.3",
    "lint-staged": "^15.2.7",
