    "rollup-plugin-dts": "^6.1.1",
    "rollup-plugin-rm": "^1.0.2",
    "simple-git-hooks": "^2.11.1",
    "tslib": "^2.6.3",
    "typescript": "^5.3.3",
    "vite": "^5.3.1"
  },
  "dependencies": {
    "@babel/core": "^7.24.7",
    "@babel/plugin-transform-arrow-functions": "^7.24.7",
    "cac": "^6.7.14",
    "esbuild": "^0.21.5",
    "magic-string": "^0.30.10",
    "picocolors": "^1.0.1"
  }
