    rules: {},
  },

  //
  // test jest file linting
  //
  {
    files: ['test/**/*.spec.ts', 'packages/*/__tests__/**/*.spec.ts'],
    ...jestPlugin.configs['flat/recommended'],
    rules: {},
  },

  // after all eslint plugins configs to override, see https://github.com/prettier/eslint-config-prettier
