import os from 'os';

export const ignores: RegExp[] = [];

if (os.platform() !== 'win32') {
  ignores.push(/NativeWin32\.node$/);
}

if (os.platform() !== 'darwin') {
  ignores.push(/NativeMacOS\.node$/);
}

if (os.platform() !== 'linux') {
  ignores.push(/NativeX11\.node$/);
  ignores.push(/NativeWLR\.node$/);
  ignores.push(/NativeHypr\.node$/);
}

// SPDX-FileCopyrightText: Simon Schneegans <code@simonschneegans.de>
// SPDX-License-Identifier: CC0-1.0

import type { Configuration } from 'webpack';

import { rules } from './webpack.rules';
import { ignores } from './webpack.ignores';

export const mainConfig: Configuration = {
  /**
   * This is the main entry point for your application, it's the first file that runs in
   * the main process.
   */
  entry: './src/main.ts',
  // Put your normal webpack config below here
  module: {
    rules,
  },
  externals: ignores,
  resolve: {
    extensions: ['.js', '.ts', '.jsx', '.tsx', '.css', '.json'],
  },
};

// SPDX-FileCopyrightText: Simon Schneegans <code@simonschneegans.de>
// SPDX-License-Identifier: CC0-1.0

import os from 'os';
import { NormalModuleReplacementPlugin, DefinePlugin } from 'webpack';
import CopyPlugin from 'copy-webpack-plugin';

// eslint-disable-next-line @typescript-eslint/no-explicit-any
export const plugins: any[] = [
  // We do not need the otf, ttf, eot and woff files of the SimpleIcons package. They are
  // replaced by the woff2 file below. Maybe there is a more elegant way to do this, but
  // this works for now.
  new NormalModuleReplacementPlugin(
    /SimpleIcons\.(otf|ttf|eot|woff)$/,
    'SimpleIcons.woff2'
  ),
  // We use this in order to know in the renderer process which operating system we are
  // running on.
  new DefinePlugin({
    // output from process.platform is the same as os.platform()
    cIsMac: process.platform === 'darwin',
    cIsWindows: process.platform === 'win32',
    cIsLinux: process.platform === 'linux',
  }),
];

// The macOS tray icons are loaded dynamically, so we copy them directly to the output
// directory.
if (os.platform() === 'darwin') {
  plugins.push(
    new CopyPlugin({
      patterns: [{ from: 'trayTemplate*.png', to: 'assets/', context: 'assets/icons/' }],
    })
  );
}

// Some resources are not bundled with webpack. We copy them to the output directory.
plugins.push(
  new CopyPlugin({
    patterns: [
      { from: 'menu-themes', to: 'assets/menu-themes', context: 'assets/' },
      { from: 'locales', to: '../main/locales' },
    ],
  })
);

// SPDX-FileCopyrightText: Simon Schneegans <code@simonschneegans.de>
// SPDX-License-Identifier: CC0-1.0

import type { Configuration } from 'webpack';

import { rules } from './webpack.rules';
import { plugins } from './webpack.plugins';
import { ignores } from './webpack.ignores';

rules.push({
  test: /\.s[ac]ss$/i,
  use: [{ loader: 'style-loader' }, { loader: 'css-loader' }, { loader: 'sass-loader' }],
});

export const rendererConfig: Configuration = {
  module: {
    rules,
  },
  plugins,
  externals: ignores,
  resolve: {
    extensions: ['.js', '.ts', '.jsx', '.tsx', '.css', '.scss'],
  },
};

// SPDX-FileCopyrightText: Simon Schneegans <code@simonschneegans.de>
// SPDX-License-Identifier: CC0-1.0

import type { ModuleOptions } from 'webpack';

export const rules: Required<ModuleOptions>['rules'] = [
  // Add support for native node modules
  {
    // We're specifying native_modules in the test because the asset relocator loader generates a
    // "fake" .node file which is really a cjs file.
    test: /native_modules[/\\].+\.node$/,
    use: 'node-loader',
  },
  {
    test: /[/\\](node_modules|build)[/\\].+\.(m?js|node)$/,
    parser: { amd: false },
    use: {
      loader: '@vercel/webpack-asset-relocator-loader',
      options: {
        outputAssetBase: 'native_modules',
      },
    },
  },
  {
    // The dbus-final module imports some native modules which are not actually used. We
    // can safely ignore them.
    test: /[/\\]node_modules[/\\]dbus-final[/\\]lib[/\\].+\.js$/,
    use: {
      loader: 'string-replace-loader',
      options: {
        multiple: [
          { search: "require\\('usocket'\\)", replace: 'undefined', flags: 'g' },
          { search: "require\\('x11'\\)", replace: 'undefined', flags: 'g' },
        ],
      },
    },
  },
  {
    test: /\.tsx?$/,
    exclude: /(node_modules|\.webpack)/,
    use: {
      loader: 'ts-loader',
      options: {
        transpileOnly: true,
      },
    },
  },
  {
    test: /\.(png|svg|jpg|jpeg|gif|mp4|ttf|woff2)$/i,
    type: 'asset/resource',
    generator: {
      filename: 'assets/[name].[hash:8][ext]',
    },
  },
  {
    test: /\.hbs$/i,
    use: 'handlebars-loader',
  },
];
