{
  "extends": [
    ".eslintrc.autogenerated.cjs",
    "plugin:@typescript-eslint/recommended",
    "plugin:prettier/recommended",
    "plugin:chai-expect/recommended",
    "plugin:chai-friendly/recommended"
  ],
  "root": true,
  "plugins": [
    "unused-imports",
    "mocha",
    "header",
    "chai-expect",
    "chai-friendly"
  ],
  "ignorePatterns": [
    "*.d.ts*",
    "garden-sea/tmp/**/*"
  ],
  "rules": {
    "@typescript-eslint/no-floating-promises": "error",
    "@typescript-eslint/no-shadow": [
      "warn",
      {
        "hoist": "all"
      }
    ],
    "@typescript-eslint/quotes": [
      "error",
      "double",
      {
        "avoidEscape": true,
        "allowTemplateLiterals": true
      }
    ],
    // We allow empty functions for cases where callbacks are required but unneeded
    "@typescript-eslint/no-empty-function": "warn",
    // We use `{}` everywhere.
    // To make this safe, we need to actually go through all the cases and see
    // if it's supposed to be a plain object, any object, or really means "anything".
    "@typescript-eslint/ban-types": "warn",
    "arrow-body-style": "off",
    "jsdoc/check-indentation": "off",
    "jsdoc/newline-after-description": "off",
    "no-console": "error",
    "no-unneeded-ternary": "error",
    "no-unused-expressions": "off",
    "chai-friendly/no-unused-expressions": [
      "error",
      {
        "allowShortCircuit": true,
        "allowTernary": true
      }
    ],
    "max-len": [
      "warn",
      {
        "code": 120,
        "ignoreStrings": true,
        "ignoreComments": true,
        "ignoreTemplateLiterals": true
      }
    ],
    "mocha/no-skipped-tests": "warn",
    "mocha/no-exclusive-tests": "error",
    "unused-imports/no-unused-imports": "error",
    "header/header": [
      2,
      "block",
      [
        "",
        " * Copyright (C) 2018-2024 Garden Technologies, Inc. <info@garden.io>",
        " *",
        " * This Source Code Form is subject to the terms of the Mozilla Public",
        " * License, v. 2.0. If a copy of the MPL was not distributed with this",
        " * file, You can obtain one at http://mozilla.org/MPL/2.0/.",
        " "
      ]
    ],
    "@typescript-eslint/no-unused-vars": [
      "error",
      {
        "argsIgnorePattern": "^_",
        "varsIgnorePattern": "^_",
        "destructuredArrayIgnorePattern": "^_"
      }
    ],
    "@typescript-eslint/consistent-type-imports": "error",
    "@typescript-eslint/no-explicit-any": "warn"
  }
}

module.exports = {
  extends: ['@commitlint/config-conventional'],
  rules: {
    // Extend to add improvement key
    "type-enum": [2, "always", [
      "chore",
      "ci",
      "docs",
      "feat",
      "fix",
      "improvement",
      "perf",
      "refactor",
      "revert",
      "style",
      "test",
      "tool",
    ]]
  }
};

{
  "retryOn429": true,
  "timeout": "120s",
  "ignorePatterns": [
    {
      "pattern": "local.demo.garden",
      "reason": "Ignore example URL"
    },
    {
      "pattern": "^http://my-service",
      "reason": "Ignore example URL"
    },
    {
      "pattern": "^https://docs\\.github\\.com/en/developers/webhooks-and-events/webhooks/webhook-events-and-payloads$",
      "reason": "Ignore this particular GitHub URL, it fails consistently with HTTP 403 code despite the link works fine"
    },
    {
      "pattern": "^https://support.apple.com/en-us/HT211861$",
      "reason": "Ignore this particular URL to Apple Rosetta guide. CI reports it as a dead one quite regularly despite the link is alive."
    }
  ]
}

