{
    "$schema": "./node_modules/nx/schemas/nx-schema.json",
    "defaultProject": "demo",
    "nxCloudAccessToken": "NWEyY2I2MGUtYjZhNS00ODQ3LTg2OTgtZDQyMDA5YmQyMjNifHJlYWQtd3JpdGU=",
    "parallel": 3,
    "canTrackAnalytics": true,
    "showUsageWarnings": true,
    "useLightClient": false,
    "namedInputs": {
        "sharedGlobals": [
            "{workspaceRoot}/projects/testing/**/*.ts",
            "{workspaceRoot}/nx.json",
            "{workspaceRoot}/package-lock.json",
            "{workspaceRoot}/tsconfig.*.json",
            "{workspaceRoot}/tsconfig.json",
            "{workspaceRoot}/scripts/**/*",
            "{workspaceRoot}/*.yml",
            "{workspaceRoot}/*.md"
        ],
        "default": ["{projectRoot}/**/*", "sharedGlobals"],
        "production": ["default"]
    },
    "targetDefaults": {
        "prerender": {
            "inputs": ["default", "^default"],
            "cache": true
        },
        "build": {
            "inputs": ["default", "^default"],
            "cache": true
        },
        "test": {
            "inputs": [
                "default",
                "production",
                "^production",
                "{workspaceRoot}/setup-jest.ts",
                "{workspaceRoot}/jest.config.ts"
            ],
            "cache": true
        },
        "e2e": {
            "inputs": ["default", "^default"]
        }
    },
    "workspaceLayout": {
        "libsDir": "projects",
        "appsDir": "projects"
    },
    "cli": {
        "analytics": "ce6ae05d-f6ae-4271-89e4-40e616f7090f"
    },
    "defaultBase": "origin/main"
}

