module.exports = {
  extends: ["eslint:recommended"],
  env: {
    commonjs: true,
    es2021: true,
    node: true,
  },
  parserOptions: {
    ecmaVersion: 12,
  },
  globals: {
    Atomics: "readonly",
    SharedArrayBuffer: "readonly",
  },
  extends: ["plugin:jest/recommended", "prettier"],
  plugins: ["jest"],
  rules: {
    "no-console": ["error", { allow: ["warn", "error"] }],
  },
  overrides: [
    {
      // enable the rule specifically for TypeScript files
      files: ["*.ts", "*.tsx"],
      env: {
        es6: true,
        node: true,
      },
      extends: [
        "plugin:@typescript-eslint/eslint-recommended",
        "plugin:@typescript-eslint/recommended",
        "plugin:jest/recommended",
        "prettier",
        "prettier/@typescript-eslint",
      ],
      parser: "@typescript-eslint/parser",
      parserOptions: {
        ecmaVersion: 2018,
        sourceType: "module",
      },
      plugins: ["@typescript-eslint", "jest"],
    },
  ],
};

{
  "git": {
    "commitMessage": "chore(release): 🤖 v${version}",
    "requireCommits": false,
    "tagName": "v${version}",
    "push": true
  },
  "github": {
    "release": false
  },
  "npm": { "publish": false },
  "plugins": {
    "@release-it/conventional-changelog": {
      "preset": "angular",
      "infile": "CHANGELOG.md"
    }
  }
}

const fs = require("fs");
const unified = require("unified");
const headings = require("rehype-autolink-headings");

const dictionary = fs.readFileSync("./dictionary.txt");
const plugins = [
  ["remark-frontmatter", "yaml"],
  [
    "remark-retext",
    unified()
      .use(require("retext-english"))
      .use(require("retext-syntax-urls"))
      .use(require("retext-syntax-mentions"))
      .use(require("retext-emoji"))
      .use(require("retext-spell"), {
        dictionary: require("dictionary-en"),
        personal: dictionary,
      })
      .use(require("retext-diacritics"))
      .use(require("retext-indefinite-article"))
      .use(require("retext-redundant-acronyms"))
      .use(require("retext-sentence-spacing")),
  ],
  "remark-preset-lint-recommended",
  "remark-preset-lint-markdown-style-guide",
  ["remark-lint-maximum-line-length", false],
  ["remark-lint-maximum-heading-length", false],
  ["remark-lint-list-item-indent", false],
  ["lint-no-multiple-toplevel-headings", false],
  ["remark-lint-no-emphasis-as-heading", false],
  // ["remark-lint-no-duplicate-headings", false],
  ["validate-links"],
];
if (process.env.NODE_ENV === "production") {
  plugins.push([
    "@theowenyoung/remark-lint-no-dead-urls",
    {
      checkIsOnline: false,
      skipUrlPatterns: [
        "https://github.com/actionsflow/actionsflow-workflow-default/generate",
        "https://trello.com/app-key",
        "http://localhost",
      ],
    },
  ]);
}
module.exports = {
  plugins,
};

module.exports = {
  extends: [
    "@commitlint/config-conventional",
    "@commitlint/config-lerna-scopes",
  ],
  rules: {
    "scope-enum": [2, "always", ["release"]],
  },
};

module.exports = {
  hooks: {
    "pre-commit": "lerna run hooks:pre-commit && lint-staged",
    "commit-msg": "commitlint -E HUSKY_GIT_PARAMS",
    "pre-push": "lerna run hooks:pre-push",
  },
};

// eslint-disable-next-line @typescript-eslint/no-var-requires
const glob = require(`glob`);

const pkgs = glob
  .sync(`./packages/*`)
  .map((p) => p.replace(/^\./, `<rootDir>`));
const ignoreDistPkgs = glob
  .sync(`./packages/*/dist`)
  .map((p) => p.replace(/^\./, `<rootDir>`));
module.exports = {
  preset: "ts-jest",
  testEnvironment: "node",
  roots: pkgs,
  modulePathIgnorePatterns: ignoreDistPkgs,
  testPathIgnorePatterns: [`__tests__/fixtures`, ".util.ts"],
  verbose: true,
  testTimeout: 10000,
  setupFiles: ["dotenv/config"],
  collectCoverage: true,
  coverageReporters: ["html", "lcov"],
};

{
  "packages": ["packages/*", "examples/actionsflow-workflow-example"],
  "version": "independent",
  "command": {
    "create": {
      "license": "MIT"
    },
    "publish": {
      "bump": "patch",
      "conventionalCommits": true,
      "message": "chore(release): 🤖 publish package"
    }
  },
  "npmClient": "npm",
  "registry": "https://registry.npmjs.org/",
  "ignoreChanges": [
    "CHANGELOG.md",
    "*.md",
    "**/__tests__/**",
    "**/__mocks__/**",
    "**/__testfixtures__/**"
  ]
}

// eslint-disable-next-line @typescript-eslint/no-var-requires
const micromatch = require("micromatch");

module.exports = {
  "*.{js,ts}": [
    "eslint --cache --ext .js,.ts --fix",
    "prettier --write",
    "git add",
  ],
  "*.{md,yaml,yml,json}": ["prettier --write", "git add"],
  "*.md": (files) => {
    // from `files` filter those _NOT_ matching `*test.js`
    const match = micromatch.not(files, "**/CHANGELOG.md");
    if (match.length > 0) {
      return `remark --quiet ${match.join(" ")}`;
    } else {
      return "true";
    }
  },
  "./README.md": (files) => {
    if (files.length > 0) {
      return `npm run copy:readme`;
    } else {
      return "true";
    }
  },
};

