import Benchmark from 'benchmark';

import { createDocument } from './dist/server-lib.mjs';
const doc = createDocument();

var suite = new Benchmark.Suite();
suite
  .add('createElement', function () {
    doc.createElement('test-elem');
  })
  .on('complete', function () {
    const results = Array.from(this);
    console.log(results.join('\n'));
  })
  .run();

{
  "name": "@ampproject/worker-dom",
  "version": "0.34.0",
  "description": "A facsimile of a modern DOM implementation intended to run in a Web Worker.",
  "main": "dist/main",
  "files": [
    "dist"
  ],
  "repository": {
    "type": "git",
    "url": "https://github.com/ampproject/worker-dom.git"
  },
  "engines": {
    "node": ">=10.14"
  },
  "author": "The AMP HTML Authors",
