    "predemo": "cross-env DEBUG_BUNDLE=true npm-run-all ~rollup",
    "demo": "node -r esm demo/server.js",
    "build": "cross-env MINIFY_BUNDLE=true npm-run-all ~rollup",
    "presize": "npm-run-all build",
    "size": "filesize",
    "release": "HUSKY_SKIP_HOOKS=1 np --no-2fa",
