import type { PlaywrightTestConfig } from "@playwright/test"
import { devices } from "@playwright/test"

/**
 * Read environment variables
 */
import "dotenv-defaults/config"

const SECOND = 1e3
const CI_ENV = typeof process.env.CI === "string"
const FORK = process.env.USE_MAINNET_FORK === "true"

/**
 * See https://playwright.dev/docs/test-configuration.
 */
const config: PlaywrightTestConfig = {
  testDir: FORK ? "./e2e-tests/fork-based" : "./e2e-tests/regular",
  /* Maximum time one test can run for. */
  timeout: 240 * SECOND,
  expect: {
    /**
     * Maximum time expect() should wait for the condition to be met.
     * For example in `await expect(locator).toHaveText();`
     */
    timeout: (CI_ENV ? 60 : 15) * SECOND,
  },
  /* Run tests in files in parallel */
  fullyParallel: false,
  /* Fail the build on CI if you accidentally left test.only in the source code. */
  forbidOnly: CI_ENV,
  /* Retry on CI only */
  retries: CI_ENV ? 2 : 0,
  /**
   * Opt out of parallel tests since we interact with real APIs during testing
   */
  workers: 1,
  /* Reporter to use. See https://playwright.dev/docs/test-reporters */
  reporter: "html",
  /* Shared settings for all the projects below. See https://playwright.dev/docs/api/class-testoptions. */
  use: {
    /* Maximum time each action such as `click()` can take. Defaults to 0 (no limit). */
    actionTimeout: 0,
    /* Base URL to use in actions like `await page.goto('/')`. */
    // baseURL: 'http://localhost:3000',

    /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
    trace: "retain-on-failure",

    /* The trace will include screenshots so we don't need them */
    // screenshot: 'only-on-failure',

    /* Setting video here does not work in our case. See https://github.com/microsoft/playwright/issues/11833 */
    // video: 'retain-on-failure',

    /*
    contextOptions: {
      recordVideo: {
        dir: './videos', // Or wherever you want the videos to be saved.
      }
    }
    */

    /* For debugging purposes */
    // launchOptions: {
    //   slowMo: 1000,
    // },

    /* Needed to verify the content of the clipboard */
    permissions: ["clipboard-read"],
  },

  /* Configure projects for major browsers */
  projects: [
    {
      name: "chromium",
      use: {
        ...devices["Desktop Chrome"],
        // See `utils.ts` for additional options passed to setup extension
      },
    },
  ],

  /* Folder for test artifacts such as screenshots, videos, traces, etc. */
  // outputDir: 'test-results/',
}

export default config

/* Reset IndexedDB between tests */
beforeEach(() => {
  global.indexedDB = new IDBFactory()
})

/* Reset IndexedDB between tests */
afterEach(() => {
  global.indexedDB = new IDBFactory()
})

it.flaky = function checkFlaky(label: string, testCase: () => unknown): void {
  // eslint-disable-next-line no-only-tests/no-only-tests
  it.only(label, () => {
    const results = []
    for (let i = 0; i < 2000; i += 1) {
      results.push(testCase())
    }
    return Promise.all(results)
  })
}

// eslint-disable-next-line @typescript-eslint/no-namespace, @typescript-eslint/no-unused-vars
declare namespace jest {
  interface It {
    /**
     * Used for debugging flaky tests
     */
    flaky: (label: string, testCase: () => unknown) => void
  }
}

import * as util from "util"
import Dexie from "dexie"
import logger, { LogLevel } from "@tallyho/tally-background/lib/logger"
import { readFileSync } from "fs"
import { webcrypto } from "crypto"

const IS_CI = process.env.CI === "true"

// When running tests, Jest will point to each expectation that failed for failed tests in the
// console output. For this reason, we want to minimize the amount of messages logged during
// CI workflows to get an overview of both failed expectations and possible errors.
// This is not the case during development, hence, we set the minimum log level to warning
// as it helps with debugging while writing new tests.
logger.logLevel = IS_CI ? LogLevel.error : LogLevel.warn

// ref: https://jestjs.io/docs/manual-mocks#mocking-methods-which-are-not-implemented-in-jsdom
// ref: https://github.com/jsdom/jsdom/issues/2524
Object.defineProperty(window, "TextEncoder", {
  writable: true,
  value: util.TextEncoder,
})
Object.defineProperty(window, "TextDecoder", {
  writable: true,
  value: util.TextDecoder,
})

Object.defineProperty(window.navigator, "usb", {
  writable: true,
  value: {
    getDevices: () => [],
    addEventListener: () => {},
  },
})

// Prevent Dexie from caching indexedDB global so fake-indexeddb
// can reset properly.
Object.defineProperty(Dexie.dependencies, "indexedDB", {
  get: () => indexedDB,
})

// Stub fetch calls but allow wasm files to be loaded.
Object.defineProperty(window, "fetch", {
  writable: true,
  value: async (
    url: string,
  ): Promise<{ status: number; body: string | Buffer } | undefined> => {
    if (url.endsWith(".wasm")) {
      const file = readFileSync(url)
      return {
        status: 200,
        body: file,
      }
    }
    // eslint-disable-next-line no-console
    console.warn("Uncaught fetch call to: \n", url)
    return undefined
  },
})

// Below, we replace any existing crypto.subtle implementation with the Node
// one. jsdom ships with an ad hoc, informally-specified, bug-ridden
// implementation of half of WebCrypto, but that includes securing the `crypto`
// variable to prevent it from being replaced, which manes we have to dig into
// the object instead of being able to replace the top-level variable.
Object.defineProperty(globalThis.crypto, "subtle", {
  writable: true,
  value: (webcrypto as unknown as Crypto).subtle,
})

import path from "path"
import webpack, {
  Configuration,
  DefinePlugin,
  WebpackOptionsNormalized,
  WebpackPluginInstance,
} from "webpack"
import { merge as webpackMerge } from "webpack-merge"
import Dotenv from "dotenv-webpack"
import SizePlugin from "size-plugin"
import TerserPlugin from "terser-webpack-plugin"
import LiveReloadPlugin from "webpack-livereload-plugin"
import CopyPlugin, { ObjectPattern } from "copy-webpack-plugin"
import ForkTsCheckerWebpackPlugin from "fork-ts-checker-webpack-plugin"
import WebExtensionArchivePlugin from "./build-utils/web-extension-archive-webpack-plugin"
import "dotenv-defaults/config"

const supportedBrowsers = ["chrome"]

if (process.env.SUPPORT_BROWSER === "firefox") {
  supportedBrowsers.push("firefox")
}

// Replicated and adjusted for each target browser and the current build mode.
const baseConfig: Configuration = {
  devtool: "source-map",
  stats: "errors-only",
  entry: {
    popup: "./src/popup.ts",
    tab: "./src/tab.ts",
    background: "./src/background.ts",
    "window-provider": "./src/window-provider.ts",
    "provider-bridge": "./src/provider-bridge.ts",
  },
  module: {
    noParse: /\.wasm$/,
    rules: [
      {
        test: /\.wasm$/,
        loader: "base64-loader",
        type: "javascript/auto",
      },
      {
        test: /\.(tsx|ts|jsx)?$/,
        exclude: /node_modules(?!\/@tallyho)|webpack/,
        use: [
          {
            loader: "babel-loader",
            options: {
              cacheDirectory: true,
            },
          },
        ],
      },
    ],
  },
  output: {
    // path: is set browser-specifically below
    filename: "[name].js",
  },
  resolve: {
    extensions: [".tsx", ".ts", ".js", ".jsx"],
    fallback: {
      fs: false,
      path: false,
      stream: require.resolve("stream-browserify"),
      process: require.resolve("process/browser"),
      // these are required for @tallyho/keyring-controller
      crypto: require.resolve("crypto-browserify"),
    },
  },
  plugins: [
    new Dotenv({
      defaults: true,
      systemvars: true,
      safe: true,
    }),
    new ForkTsCheckerWebpackPlugin({
      typescript: {
        diagnosticOptions: {
          semantic: true,
          syntactic: true,
        },
        mode: "write-references",
      },
    }),
    // polyfill the process and Buffer APIs
    new webpack.ProvidePlugin({
      Buffer: ["buffer", "Buffer"],
      process: ["process"],
    }),
    new SizePlugin({}),
    new CopyPlugin({
      patterns: [
        {
          from: "node_modules/@tallyho/tally-ui/public/",
        },
      ],
      // FIXME Forced cast below due to an incompatibility between the webpack
      // FIXME version refed in @types/copy-webpack-plugin and our local
      // FIXME webpack version.
    }) as unknown as WebpackPluginInstance,
    new DefinePlugin({
      "process.env.VERSION": JSON.stringify(process.env.npm_package_version),
    }),
  ],
  optimization: {
    splitChunks: { automaticNameDelimiter: "-" },
  },
}

// Configuration adjustments for specific build modes, customized by browser.
const modeConfigs: {
  [mode: string]: (browser: string) => Partial<Configuration>
} = {
  development: () => ({
    plugins: [
      new LiveReloadPlugin({}),
      new CopyPlugin({
        patterns: ["dev-utils/*.js"],
        // FIXME Forced cast below due to an incompatibility between the webpack
        // FIXME version refed in @types/copy-webpack-plugin and our local
        // FIXME webpack version.
      }) as unknown as WebpackPluginInstance,
      new CopyPlugin({
        patterns: [
          {
            from: "node_modules/@tallyho/tally-ui/public/",
            force: true,
            priority: 1,
            transform: (content, fileName) => {
              // Inject devtools in every static HTML file
              if (fileName.endsWith(".html")) {
                const port = process.env.REACT_DEVTOOLS_DEFAULT_PORT

                return content
                  .toString("utf8")
                  .replace(
                    "<!-- INSERT_REACT_DEV_TOOLS_HERE -->",
                    `<script src="http://localhost:${port}"></script>`,
                  )
              }

              return content
            },
          },
        ],
        // FIXME Forced cast below due to an incompatibility between the webpack
        // FIXME version refed in @types/copy-webpack-plugin and our local
        // FIXME webpack version.
      }) as unknown as WebpackPluginInstance,
    ],
    optimization: {
      minimizer: [
        new TerserPlugin({
          terserOptions: {
            mangle: false,
            compress: false,
            output: {
              beautify: true,
              indent_level: 2, // eslint-disable-line camelcase
            },
          },
        }),
      ],
    },
  }),
  production: (browser) => ({
    // For some reason, every source map variant embeds absolute paths, and
    // Firefox reproducibility is required for Firefox add-on store submission.
    // As such, in production, no source maps for firefox for full
    // reproducibility.
    //
    // Ideally, we would figure out a way not to have absolute paths in source
    // maps.
    devtool: browser === "firefox" ? false : "source-map",
    plugins: [
      new WebExtensionArchivePlugin({
        filename: browser,
      }),
    ],
    optimization: {
      minimizer: [
        // Firefox imposes filesize limits in the add-on store, so Firefox
        // builds are mangled and compressed. In a perfect world, we would
        // never do this so that users can inspect the code running on their
        // system more easily.
        new TerserPlugin({
          terserOptions: {
            mangle: browser === "firefox",
            compress: browser === "firefox",
            output:
              browser === "firefox"
                ? undefined
                : {
                    beautify: true,
                    indent_level: 2, // eslint-disable-line camelcase
                  },
          },
        }),
      ],
    },
  }),
}

// One config per supported browser, adjusted by mode.
export default (
  _: unknown,
  { mode }: WebpackOptionsNormalized,
): webpack.Configuration[] =>
  supportedBrowsers.map((browser) => {
    const distPath = path.join(__dirname, "dist", browser)

    // Try to find a build mode config adjustment and call it with the browser.
    const modeSpecificAdjuster =
      typeof mode !== "undefined" ? modeConfigs[mode] : undefined
    const modeSpecificAdjustment =
      typeof modeSpecificAdjuster !== "undefined"
        ? modeSpecificAdjuster(browser)
        : {}

    return webpackMerge(baseConfig, modeSpecificAdjustment, {
      name: browser,
      output: {
        path: distPath,
      },
      plugins: [
        // Handle manifest adjustments. Adjustments are looked up and merged:
        //  - by mode (`manifest.<mode>.json`)
        //  - by browser (`manifest.<browser>.json`)
        //  - by mode and browser both (`manifest.<mode>.<browser>.json`)
        //
        // Files that don't exist are ignored, while files with invalid data
        // throw an exception. The merge order means that e.g. a mode+browser
        // adjustment will override a browser adjustment, which will override a
        // mode adjustment in turn.
        //
        // Merging currently only supports adding keys, overriding existing key
        // values if their values are not arrays, or adding entries to arrays.
        // It does not support removing keys or array values. webpackMerge is
        // used for this.
        new CopyPlugin({
          patterns: [
            {
              from: `manifest/manifest(|.${mode}|.${browser}|.${browser}.${mode}).json`,
              to: "manifest.json",
              transformAll: (
                assets: { data: Buffer; sourceFilename: string }[],
              ) => {
                const getPriority = (filename: string) => {
                  switch (true) {
                    case filename.endsWith(`manifest.${browser}.${mode}.json`):
                      return 8
                    case filename.endsWith(`manifest.${browser}.json`):
                      return 4
                    case filename.endsWith(`manifest.${mode}.json`):
                      return 2
                    case filename.endsWith("manifest.json"):
                      return 1
                    default:
                      return 0
                  }
                }

                const combinedManifest = webpackMerge(
                  {},
                  ...assets
                    .sort(
                      (a, b) =>
                        getPriority(a.sourceFilename) -
                        getPriority(b.sourceFilename),
                    )
                    .map((asset) => asset.data.toString("utf8"))
                    // JSON.parse chokes on empty strings
                    .filter((assetData) => assetData.trim().length > 0)
                    .map((assetData) => JSON.parse(assetData)),
                )

                return JSON.stringify(combinedManifest, null, 2)
              },
            } as unknown as ObjectPattern, // ObjectPattern doesn't include transformAll in current types
          ],
          // FIXME Forced cast below due to an incompatibility between the webpack
          // FIXME version refed in @types/copy-webpack-plugin and our local
          // FIXME webpack version.
        }) as unknown as WebpackPluginInstance,
      ],
    })
  })
