{
  "parser": "@typescript-eslint/parser",
  "extends": [
    "eslint:recommended",
    "plugin:import/recommended",
    "plugin:react/recommended",
    "plugin:jsx-a11y/recommended",
    "plugin:jest/recommended",
    "plugin:cypress/recommended"
  ],
  "plugins": ["jsdoc", "react-hooks"],
  "rules": {
    "react-hooks/rules-of-hooks": "error", // Checks rules of Hooks
    "react-hooks/exhaustive-deps": "error", // Checks effect dependencies
    "react/jsx-uses-react": "off",
    "react/react-in-jsx-scope": "off",
    "react/prop-types": "off",
    "import/named": "off",
    "jsdoc/check-examples": "error",
    "jsdoc/check-param-names": "off",
    "jsdoc/check-syntax": "error",
    "jsdoc/check-types": "error",
    "jsdoc/implements-on-classes": "error",
    "jsdoc/match-description": "error",
    "jsdoc/newline-after-description": "error",
    "jsdoc/no-types": "error",
    "jsdoc/no-undefined-types": "error",
    "jsdoc/require-description": [
      "error",
      {
        "contexts": ["any"]
      }
    ],
    "jsdoc/require-description-complete-sentence": "error",
    "jsdoc/require-param": [
      "error",
      {
        "checkDestructured": false
      }
    ],
    "jsdoc/require-param-description": "error",
    "jsdoc/require-param-name": "error",
    "jsdoc/require-param-type": "off",
    "jsdoc/require-returns": "error",
    "jsdoc/require-returns-check": "error",
    "jsdoc/require-returns-description": "error",
    "jsdoc/require-returns-type": "off",
    "jsdoc/valid-types": "error",
    "jsdoc/require-jsdoc": [
      "error",
      {
        "require": {
          "ClassDeclaration": true,
          "MethodDefinition": true,
          "FunctionDeclaration": true,
          "ArrowFunctionExpression": false
        },
        "contexts": [
          "TSInterfaceDeclaration",
          "TSTypeAliasDeclaration",
          "TSEnumDeclaration",
          "TSPropertySignature",
          "VariableDeclarator > ArrowFunctionExpression"
        ]
      }
    ]
  },
  "env": {
    "browser": true,
    "node": true,
    "es6": true,
    "cypress/globals": true
  },
  "settings": {
    "import/resolver": {
      "node": {
        "extensions": [".js", ".jsx", ".ts", ".tsx"]
      },
      "typescript": {
        "alwaysTryTypes": true
      }
    }
  },
  "overrides": [
    {
      "files": ["**/*.ts", "**/*.tsx"],
      "plugins": ["eslint-plugin-tsdoc"],
      "extends": ["plugin:@typescript-eslint/recommended"],
      "rules": {
        "@typescript-eslint/indent": "off",
        "@typescript-eslint/explicit-function-return-type": "off",
        "@typescript-eslint/no-explicit-any": "off",
        "@typescript-eslint/no-unused-vars": [
          "error",
          { "argsIgnorePattern": "^_", "ignoreRestSiblings": true }
        ],
        "tsdoc/syntax": "error",
        // The option `allowTypedFunctionExpressions` doesn't work with
        // nested typed functions, so this rule is disabled.
        // Bug: https://github.com/typescript-eslint/typescript-eslint/issues/2183
        // Docs: https://github.com/typescript-eslint/typescript-eslint/blob/v4.6.0/packages/eslint-plugin/docs/rules/explicit-module-boundary-types.md#allowtypedfunctionexpressions
        "@typescript-eslint/explicit-module-boundary-types": "off"
      }
    },
    // Overrides for all the tests files.
    {
      "files": [
        "**/__tests__/*.{js,ts,jsx,tsx}",
        "**/integration/**/*.spec.{js,ts}"
      ],
      "rules": {
        "jsdoc/require-jsdoc": "off",
        "jsdoc/require-description": "off",
        "tsdoc/syntax": "off",
        "jest/expect-expect": [
          "error",
          {
            "assertFunctionNames": ["expect", "cy.**"]
          }
        ]
      }
    },
    // Overrides for Cypress test files.
    {
      "files": ["**/integration/**/*.spec.{js,ts}"],
      "rules": {
        "jest/valid-expect-in-promise": "off",
        "jest/expect-expect": "off"
      }
    },
    {
      "files": [
        "**/__tests__/*types*{js,ts,jsx,tsx}",
        "**/types/**/__tests__/*.{js,ts,jsx,tsx}"
      ],
      "rules": {
        "jest/expect-expect": "off",
        "@typescript-eslint/no-unused-vars": "off",
        "@typescript-eslint/no-empty-function": "off"
      }
    },
    // Overrides for Selenium files
    {
      "files": ["**/selenium/**"],
      "rules": {
        "jest/expect-expect": [
          "error",
          {
            "assertFunctionNames": ["assert.**", "assert"]
          }
        ]
      },
      "globals": {
        "driver": "writable",
        "baseUrl": "writable"
      }
    }
  ]
}

module.exports = {
  presets: [
    ["@babel/preset-env", { targets: { node: "current" } }],
    ["@babel/preset-react", { runtime: "automatic" }],
  ],
  plugins: [
    [
      "@emotion/babel-plugin",
      {
        autoLabel: "never",
      },
    ],
  ],
};

// Removes the warning that we are using an unsupported version of TypeScript.
process.env.TS_JEST_DISABLE_VER_CHECKER = true;

module.exports = {
  transform: {
    "^.+\\.tsx?$": "ts-jest",
    "^.+\\.jsx?$": ["babel-jest", { configFile: "../../jest.babel.config.js" }],
  },
  testMatch: ["**/__tests__/**/*.test?(s).[jt]s?(x)"],
  moduleFileExtensions: ["ts", "tsx", "js", "jsx"],
  testPathIgnorePatterns: ["/mocks/", "/__utilities__/"],
};

const lintStaged = require("lint-staged");

/**
 * Run lint-staged programatically to catch the errors because we don't want
 * eslint errors to prevent the commit. Eslint will be checked again using a
 * GitHub action once the code is pushed.
 *
 * We use two separate lint-staged executions to prevent lint-staged from
 * reverting the prettier changes if eslint fails.
 */
(async () => {
  try {
    /**
     * This script adds a linebreak before each TSDocs comment. Unnecessary
     * linebreaks and double linebreaks will be removed by prettier in the next
     * step.
     */
    await lintStaged({
      allowEmpty: true,
      config: {
        "*.{js,jsx,ts,tsx}":
          'replace-in-files --regex="\\/\\*\\*\\s*\n" --replacement="\n/**\n"',
      },
    });

    /**
     * Prettier.
     */
    await lintStaged({
      allowEmpty: true,
      config: {
        "*.{js,jsx,ts,tsx}": "prettier --write",
      },
    });

    /**
     * Eslint.
     */
    await lintStaged({
      allowEmpty: true,
      config: {
        "*.{js,jsx,ts,tsx}": "eslint --fix",
      },
    });
  } catch (e) {
    // Failed to load configuration.
    console.error(e);
  }
})();

