{
  "parser": "@typescript-eslint/parser",
  "extends": ["plugin:sonarjs/recommended", "plugin:@typescript-eslint/recommended", "prettier", "plugin:prettier/recommended"],

  "overrides": [
    {
      "files": ["*.ts"],
      "rules": {
        "@typescript-eslint/no-namespace": "off",
        "@typescript-eslint/interface-name-prefix": "off",
        "@typescript-eslint/no-parameter-properties": "off",
        "@typescript-eslint/no-require-imports": "warn",
        "@typescript-eslint/no-var-requires": "warn",
        "@typescript-eslint/semi": ["error"],
        "@typescript-eslint/member-ordering": ["off"],
        "@typescript-eslint/comma-dangle": "off",

        "@typescript-eslint/explicit-function-return-type": [
          "warn",
          {
            "allowExpressions": true
          }
        ],

        "@typescript-eslint/explicit-member-accessibility": [
          "error",
          {
            "accessibility": "explicit",
            "overrides": {
              "constructors": "no-public"
            }
          }
        ],

        "@typescript-eslint/no-unused-vars": [
          "warn",
          {
            "args": "after-used",
            "ignoreRestSiblings": false,
            "vars": "all",
            "argsIgnorePattern": "^_",
            "varsIgnorePattern": "^_"
          }
        ],

        "@typescript-eslint/no-empty-interface": [
          "error",
          {
            "allowSingleExtends": false
          }
        ],

        "@typescript-eslint/member-delimiter-style": [
          "error",
          {
            "multiline": {
              "delimiter": "semi",
              "requireLast": true
            },
            "singleline": {
              "delimiter": "semi",
              "requireLast": true
            }
          }
        ],
        "@typescript-eslint/indent": [
          "error",
          2,
          {
            "SwitchCase": 1
          }
        ],

        "no-trailing-spaces": [
          "warn",
          {
            "ignoreComments": true
          }
        ],

        "sonarjs/cognitive-complexity": ["warn"],

        "indent": "off",
        "no-useless-constructor": "error",
        "no-unreachable": "error",
        "no-warning-comments": "warn",
        "no-console": "warn",
        "no-return-await": "warn",
        "no-await-in-loop": "off",
        "eol-last": ["warn", "always"],
        "linebreak-style": ["error", "unix"],
        "eqeqeq": "error",
        "semi": "off",
        "quotes": ["error", "single"],
        "comma-dangle": "off"
      }
    },
    {
      "files": ["*.js"],
      "rules": {
        "@typescript-eslint/no-namespace": "off",
        "@typescript-eslint/interface-name-prefix": "off",
        "@typescript-eslint/no-parameter-properties": "off",
        "@typescript-eslint/no-require-imports": "warn",
        "@typescript-eslint/explicit-function-return-type": "off",
        "@typescript-eslint/explicit-member-accessibility": "off",
        "@typescript-eslint/no-unused-vars": "off",
        "@typescript-eslint/no-var-requires": "off",
        "@typescript-eslint/no-empty-interface": "off",
        "@typescript-eslint/semi": "off",
        "@typescript-eslint/member-ordering": "off",
        "@typescript-eslint/member-delimiter-style": "off",
        "@typescript-eslint/indent": "off",
        "@typescript-eslint/comma-dangle": "off",

        "no-trailing-spaces": [
          "warn",
          {
            "ignoreComments": true
          }
        ],

        "sonarjs/cognitive-complexity": ["warn"],

        "indent": "off",
        "no-useless-constructor": "error",
        "no-unreachable": "error",
        "no-warning-comments": "warn",
        "no-console": "warn",
        "no-return-await": "warn",
        "no-await-in-loop": "off",
        "eol-last": ["warn", "always"],
        "linebreak-style": ["error", "unix"],
        "eqeqeq": "error",
        "semi": "off",
        "quotes": ["error", "single"],
        "comma-dangle": "off"
      }
    }
  ]
}

import { mkdirSync, cpSync, rmSync, writeFileSync } from 'fs';

// Start fresh by clearing folder
rmSync('.vercel/output', { recursive: true, force: true });

// Create folders
mkdirSync('.vercel/output/static', { recursive: true });

// Copy images and CSS files
console.log('Copying static files from example...');
cpSync('./example', '.vercel/output/static', { recursive: true });

// Define version for Build Output API
// https://vercel.com/docs/build-output-api/v3
writeFileSync('.vercel/output/config.json', `{"version": 3}`);

console.log('âœ… Done copying static files');

