/** @type {import('eslint').Linter.Config} */
const baseRules = {
  'prefer-const': ['error', { destructuring: 'all' }],

  'unicorn/better-regex': 'error',
  'unicorn/catch-error-name': 'error',
  'unicorn/consistent-destructuring': 'error',
  'unicorn/error-message': 'error',
  'unicorn/escape-case': 'error',
  'unicorn/expiring-todo-comments': 'off',
  'unicorn/explicit-length-check': 'error',
  'unicorn/filename-case': ['error', { case: 'kebabCase' }],
  'unicorn/import-style': 'error',
  'unicorn/new-for-builtins': 'error',
  'unicorn/no-abusive-eslint-disable': 'error',
  'unicorn/no-array-reduce': 'error',
  'unicorn/no-console-spaces': 'error',
  'unicorn/no-for-loop': 'error',
  'unicorn/no-hex-escape': 'error',
  'unicorn/no-instanceof-array': 'error',
  'unicorn/no-lonely-if': 'error',
  'unicorn/no-new-array': 'error',
  'unicorn/no-new-buffer': 'error',
  'unicorn/no-process-exit': 'error',
  'unicorn/no-unreadable-array-destructuring': 'error',
  'unicorn/no-useless-undefined': 'error',
  'unicorn/no-zero-fractions': 'error',
  'unicorn/number-literal-case': 'error',
  'unicorn/numeric-separators-style': 'error',
  'unicorn/prefer-add-event-listener': 'error',
  'unicorn/prefer-array-find': 'error',
  'unicorn/prefer-array-flat-map': 'error',
  'unicorn/prefer-array-index-of': 'error',
  'unicorn/prefer-array-some': 'error',
  'unicorn/prefer-date-now': 'error',
  'unicorn/prefer-default-parameters': 'error',
  'unicorn/prefer-dom-node-append': 'error',
  'unicorn/prefer-dom-node-remove': 'error',
  'unicorn/prefer-dom-node-text-content': 'error',
  'unicorn/prefer-includes': 'error',
  'unicorn/prefer-keyboard-event-key': 'error',
  'unicorn/prefer-math-trunc': 'error',
  'unicorn/prefer-modern-dom-apis': 'error',
  'unicorn/prefer-negative-index': 'error',
  'unicorn/prefer-number-properties': 'error',
  'unicorn/prefer-optional-catch-binding': 'error',
  'unicorn/prefer-query-selector': 'error',
  'unicorn/prefer-reflect-apply': 'error',
  'unicorn/prefer-regexp-test': 'error',
  'unicorn/prefer-set-has': 'error',
  'unicorn/prefer-spread': 'error',
  'unicorn/prefer-string-slice': 'error',
  'unicorn/prefer-string-starts-ends-with': 'error',
  'unicorn/prefer-string-trim-start-end': 'error',
  'unicorn/prefer-ternary': 'error',
  'unicorn/prefer-type-error': 'error',
  'unicorn/throw-new-error': 'error',
  'unicorn/no-array-push-push': 'error',

  'unicorn/no-keyword-prefix': 'off',
  'no-nested-ternary': 'off',
  'unicorn/no-nested-ternary': 'off',
  'unicorn/no-unsafe-regex': 'off',
  'unicorn/no-unused-properties': 'off',
  'unicorn/string-content': 'off',
  'unicorn/custom-error-definition': 'off',
  'unicorn/empty-brace-spaces': 'off',
};

const importRules = {
  'sort-imports': 'off',

  // Use nice import and export rules
  'simple-import-sort/exports': ['warn'], // TODO switch on after release
  'simple-import-sort/imports': [
    'warn',
    {
      groups: [
        // Side effect imports.
        ['^\\u0000'],

        // Packages that are not scoped to `remirror`
        // Things that start with a letter (or digit or underscore), or `@` followed by a letter.
        ['^(?!@remirror)@?\\w', '^@remirror'],

        // Scoped packages
        // [],

        // Absolute imports and other imports such as Vue-style `@/foo`.
        // Anything that does not start with a dot.
        ['^[^.]'],

        // Relative imports.
        // Anything that starts with a dot.
        ['^\\.'],
      ],
    },
  ],
};

let config = {
  parser: '@typescript-eslint/parser',
  ignorePatterns: ['*.d.ts'],
  plugins: [
    'jest',
    'jest-formatting',
    '@typescript-eslint',
    'react-hooks',
    'react',
    'react-native',
    'unicorn',
    'jsx-a11y',
    'simple-import-sort',

    // For `remirror__lit-components`
    'lit',
    'lit-a11y',
  ],
  extends: [
    'eslint:recommended',
    'plugin:@typescript-eslint/recommended',
    'plugin:react/recommended',
    'prettier',
    'plugin:jest-formatting/recommended',
    'plugin:jest/recommended',
    'plugin:jest/style',
    'plugin:lit/recommended',
    'plugin:lit-a11y/recommended',
  ],

  parserOptions: {
    ecmaVersion: 2021,
    sourceType: 'module',
    ecmaFeatures: {
      jsx: true,
    },
    project: 'tsconfig.eslint.json',
    tsconfigRootDir: __dirname,
  },
  settings: {
    react: {
      version: 'detect',
    },
  },
  env: {
    browser: true,
    node: true,
    jest: true,
    es6: true,
  },
  rules: {
    ...baseRules,
    ...importRules,
    'jest/no-test-return-statement': 'off',
    'jest/prefer-strict-equal': 'off',
    'jest/no-export': 'off',
    'jest/consistent-test-it': ['error'],
    'jest/prefer-spy-on': ['warn'],
    'jest/prefer-todo': ['warn'],
    'jest/prefer-hooks-on-top': ['error'],
    'jest/no-large-snapshots': ['warn', { maxSize: 20 }],
    'jest/no-duplicate-hooks': ['error'],
    'jest/no-if': ['error'],
    'jest/no-restricted-matchers': [
      'error',
      { toBeTruthy: 'Avoid `toBeTruthy`', toBeFalsy: 'Avoid `toBeFalsy`' },
    ],

    '@typescript-eslint/no-unused-expressions': [
      'error',
      { allowTernary: true, allowShortCircuit: true },
    ],

    '@typescript-eslint/no-empty-function': 'off',
    '@typescript-eslint/no-empty-interface': 'off',
    '@typescript-eslint/no-var-requires': 'off',
    '@typescript-eslint/explicit-function-return-type': 'off',
    '@typescript-eslint/no-explicit-any': 'off',
    '@typescript-eslint/no-namespace': 'off',
    '@typescript-eslint/no-unused-vars': ['off'],
    '@typescript-eslint/naming-convention': [
      'warn',
      { selector: 'typeParameter', format: ['StrictPascalCase'] },
    ],
    '@typescript-eslint/no-non-null-assertion': 'warn',
    '@typescript-eslint/no-inferrable-types': 'warn',
    '@typescript-eslint/ban-types': [
      'warn',
      {
        extendDefaults: false,
        types: {
          String: {
            message: 'Use string instead',
            fixWith: 'string',
          },
          Boolean: {
            message: 'Use boolean instead',
            fixWith: 'boolean',
          },
          Number: {
            message: 'Use number instead',
            fixWith: 'number',
          },
          Symbol: {
            message: 'Use symbol instead',
            fixWith: 'symbol',
          },
          Function: {
            message: [
              'The `Function` type accepts any function-like value.',
              'It provides no type safety when calling the function, which can be a common source of bugs.',
              'It also accepts things like class declarations, which will throw at runtime as they will not be called with `new`.',
              'If you are expecting the function to accept certain arguments, you should explicitly define the function shape.',
            ].join('\n'),
          },

          // object typing
          Object: {
            message: [
              'The `Object` type actually means "any non-nullish value", so it is marginally better than `unknown`.',
              '- If you want a type meaning "any object", you probably want `Record<string, unknown>` instead.',
              '- If you want a type meaning "any value", you probably want `unknown` instead.',
            ].join('\n'),
          },
          '{}': {
            message: [
              '`{}` actually means "any non-nullish value".',
              '- If you want a type meaning "object", you probably want `object` instead.',
              '- If you want a type meaning "any value", you probably want `unknown` instead.',
            ].join('\n'),
            fixWith: 'object',
          },
        },
      },
    ],

    /**
     * @todo Set `explicit-module-boundary-types` lint rule to `error` once all
     * issues are resolved
     * @block All exported methods that are exposed to end users should be
     * explicitly typed. It makes for better readability when contributing since
     * inference requires more work to determine the return types, and also it
     * forces deliberate planning.
     */
    '@typescript-eslint/explicit-module-boundary-types': [
      'warn',
      {
        allowedNames: ['name', 'createHelpers', 'createCommands', 'createExtensions', 'createTags'],
      },
    ],

    // Turning off as it leads to code with bad patterns, where implementation
    // details are placed before the actual meaningful code.
    '@typescript-eslint/no-use-before-define': 'off',
    '@typescript-eslint/member-ordering': [
      'warn',
      { default: ['signature', 'static-field', 'static-method', 'field', 'constructor', 'method'] },
    ],
    '@typescript-eslint/method-signature-style': 'warn',
    '@typescript-eslint/prefer-function-type': 'error',
    '@typescript-eslint/array-type': [
      'error',
      { default: 'array-simple', readonly: 'array-simple' },
    ],

    // React Rules

    'react/no-multi-comp': 'off',
    'react/prop-types': 'off',
    'react/display-name': 'warn',
    'react/no-unescaped-entities': 'error',
    'react/no-unused-state': 'error',
    'react/no-children-prop': 'error',
    'react/self-closing-comp': 'error',

    // React Hooks

    'react-hooks/exhaustive-deps': 'error',
    'react-hooks/rules-of-hooks': 'error',

    // Built in eslint rules
    'no-constant-condition': 'off', // To many false positives
    'no-empty': 'warn',
    'no-useless-escape': 'warn',
    'default-case': 'off',
    'default-case-last': 'error',
    'prefer-template': 'warn',
    'guard-for-in': 'warn',
    'prefer-object-spread': 'warn',
    curly: ['warn', 'all'],
    'no-invalid-regexp': 'error',
    'no-multi-str': 'error',
    'no-extra-boolean-cast': 'error',
    radix: 'error',
    'no-return-assign': ['error', 'except-parens'],
    eqeqeq: ['error', 'always', { null: 'ignore' }],
    'prefer-exponentiation-operator': 'error',
    'prefer-arrow-callback': ['error', { allowNamedFunctions: true }],
    'padding-line-between-statements': [
      'warn',
      {
        blankLine: 'always',
        prev: '*',
        next: ['if', 'switch', 'for', 'do', 'while', 'class', 'function'],
      },
      {
        blankLine: 'always',
        prev: ['if', 'switch', 'for', 'do', 'while', 'class', 'function'],
        next: '*',
      },
    ],
  },
  overrides: [
    {
      files: ['*.ts', '*.tsx'],
      rules: {
        '@typescript-eslint/no-extra-non-null-assertion': ['error'],
        '@typescript-eslint/prefer-optional-chain': ['error'],
        '@typescript-eslint/consistent-type-definitions': ['error', 'interface'],
        '@typescript-eslint/no-dynamic-delete': ['error'],
        '@typescript-eslint/no-var-requires': 'error',
      },
    },

    // Rules exclusive to published packages.
    {
      files: ['packages/*/src/**'],
      rules: {
        'no-console': 'error',
      },
    },
    {
      files: [
        '*.spec.{ts,tsx}*',
        'packages/storybook-react/**',
        'support/**',
        'website/**',
        '**/__dts__/**',
        '**/*.test.ts',
      ],
      rules: {
        'unicorn/consistent-destructuring': 'off',
        '@typescript-eslint/await-thenable': 'off',
        '@typescript-eslint/ban-ts-comment': 'off',
        '@typescript-eslint/ban-ts-ignore': 'off', // Often you need to use @ts-ignore in tests
        '@typescript-eslint/no-non-null-assertion': 'off', // Makes writing tests more convenient
        '@typescript-eslint/no-use-before-define': 'off',
        'react/display-name': 'off',
      },
    },
    {
      files: [
        '**/*.d.ts',
        '**/__mocks__/**',
        'packages/remirror__i18n/**/*.ts',
        'docs/**',
        'support/**',
        'website/**',
        '**/__fixtures__/**',
        '**/*.stories.tsx',
        '**/*.stories.ts',
        'packages/remirror__cli/**',
        'packages/remirror__extension-emoji/src/data/*.ts',
        'packages/remirror__svelte/rollup.config.js',
        'packages/testing/**',
        'packages/remirror__core-utils/src/keyboard-utils.ts',
        '**/__tests__/**',
      ],
      rules: {
        '@typescript-eslint/explicit-module-boundary-types': 'off',
      },
    },
    {
      files: ['**/*.d.ts', '**/__mocks__/**'],
      rules: { 'import/no-duplicates': 'off', '@typescript-eslint/no-namespace': 'off' },
    },
    {
      files: ['**/__dts__/**'],
      rules: {
        '@typescript-eslint/no-namespace': 'off',
        '@typescript-eslint/no-unused-vars': 'off',
        '@typescript-eslint/no-unused-vars-experimental': 'off',
      },
    },
    {
      files: ['support/**', 'packages/testing/**/*.{js,ts}'],
      rules: {
        '@typescript-eslint/ban-ts-comment': 'off',
        '@typescript-eslint/no-var-requires': 'off',
        'unicorn/no-process-exit': 'off',
        'unicorn/no-unreadable-array-destructuring': 'off',
      },
    },
    {
      files: ['packages/remirror__i18n/**/messages.ts'],
      rules: {
        'eslint-comments/disable-enable-pair': 'off',
        'eslint-comments/no-unlimited-disable': 'off',
        'unicorn/no-abusive-eslint-disable': 'off',
        'eslint-comments/no-unused-disable': 'off',
      },
    },
    {
      files: ['packages/testing/**'],
      rules: { '@typescript-eslint/no-var-requires': 'off' },
    },
    {
      files: [
        '**/*extension.ts',
        '**/*extension.tsx',
        'packages/remirror__core/src/manager/remirror-manager.ts',
        'packages/remirror__core/src/framework/*.ts',
        'packages/remirror__core/src/extension/extension-base.ts',
      ],
      rules: { '@typescript-eslint/method-signature-style': 'off' },
    },
    {
      files: ['packages/remirror__styles/src/*.tsx'],
      // Turn these rules off to import `react` for TypeScript
      rules: {
        '@typescript-eslint/no-unused-vars-experimental': 'off',
        '@typescript-eslint/no-unused-vars': 'off',
      },
    },
    {
      files: [
        'examples/with-react-native/**/*.{ts,tsx}',
        'packages/remirror__react-native/**/*.{ts,tsx}',
      ],
      rules: {
        'react-native/no-unused-styles': ['error'],
        'react-native/split-platform-components': ['error'],
        'react-native/no-inline-styles': ['error'],
        'react-native/no-color-literals': ['error'],
        // 'react-native/no-raw-text': ['error'],
        'react-native/no-single-element-style-arrays': ['error'],
      },
    },
    {
      files: ['website/src/theme/**'],
      rules: {
        'unicorn/filename-case': ['off'],
      },
    },
  ],
};

// Only apply TypeScript specific rules when in TS Mode. This is a hack for now
// due to the issue raised
// https://github.com/typescript-eslint/typescript-eslint/issues/2373
if (process.env.FULL_ESLINT_CHECK) {
  const rules = {
    '@typescript-eslint/prefer-readonly': 'warn',
    '@typescript-eslint/await-thenable': 'warn',
    '@typescript-eslint/no-unnecessary-type-arguments': 'warn',
    '@typescript-eslint/restrict-plus-operands': 'warn',
    '@typescript-eslint/no-misused-promises': 'warn',

    // TODO: re-enable the following rule again. -- ocavue 2022-06-12
    // '@typescript-eslint/no-unnecessary-type-assertion': 'error',
  };

  const rulesOff = {};

  for (const rule of Object.keys(rules)) {
    rulesOff[rule] = 'off';
  }

  config = {
    ...config,
    plugins: [...config.plugins, 'import'],
    extends: [...config.extends, 'plugin:import/typescript'],
    rules: {
      ...config.rules,
      'import/no-default-export': 'off',
      'import/first': 'error',
      'import/no-duplicates': 'error',
      // 'import/no-cycle': 'error', // Conflicts with `react-native`.
      'import/no-self-import': 'error',
      'import/newline-after-import': 'error',

      // Turn off conflicting import rules
      // 'import/order': 'off',
    },
    overrides: [
      {
        parserOptions: { project: [require.resolve('./support/tsconfig.lint.json')] },
        files: ['**/!(*.{md,mdx})/*.ts', '**/!(*.{md,mdx})/*.tsx'],
        rules,
      },
      {
        files: [
          '**/__tests__/**',
          '**/__fixtures__/**',
          'support/**',
          'website/**',
          '**/__dts__/**',
          '**/*.test.ts',
        ],
        // Switch off rules for test files.
        rules: rulesOff,
      },
      ...config.overrides,
    ],
  };
} else if (process.env.MD_ESLINT_CHECK) {
  config = {
    ...config,
    // Apply the markdown plugin
    plugins: [...config.plugins, 'markdown'],

    parserOptions: {
      ecmaVersion: 2021,
      sourceType: 'module',
      ecmaFeatures: {
        jsx: true,
      },
    },

    // Only apply markdown rules when not in TypeScript mode, since they are
    // currently incompatible.
    overrides: [
      { files: ['*.mdx', '*.md'], processor: 'markdown/markdown' },
      {
        // Lint code blocks in markdown
        files: ['**/*.{md,mdx}/*.{ts,tsx,js}'],

        // Set up rules to be excluded in the markdown blocks.
        rules: {
          ...importRules,
          'simple-import-sort/exports': 'warn',
          'simple-import-sort/imports': 'warn',
          'unicorn/filename-case': 'off',
          '@typescript-eslint/explicit-module-boundary-types': 'off',
          '@typescript-eslint/no-unused-vars-experimental': 'off',
          '@typescript-eslint/no-unused-vars': 'off',
          'react-hooks/rules-of-hooks': 'off',
          'react/jsx-uses-react': 'off',
          'react/react-in-jsx-scope': 'off',
        },
      },
    ],
  };
}

module.exports = config;

/** @type {import('prettier').Options} */
const config = {
  bracketSpacing: true,
  endOfLine: 'lf',
  jsxSingleQuote: true,
  plugins: [require.resolve('prettier-plugin-packagejson')],
  printWidth: 100,
  proseWrap: 'never',
  semi: true,
  singleQuote: true,
  tabWidth: 2,
  trailingComma: 'all',
  useTabs: false,
};

module.exports = config;

{
  "jsc": {
    "parser": {
      "syntax": "typescript",
      "jsx": true,
      "decorators": true,
      "decoratorsBeforeExport": true
    },
    "transform": {
      "decoratorVersion": "2022-03"
    },
    "target": "es2020"
  }
}

{
  "outputTarget": "markdown",
  "plugins": [
    {
      "packageName": "doc-plugin-remirror",
      "enabledFeatureNames": ["remirror-feature"]
    }
  ]
}

const config = require('./support/base.babel');

module.exports = {
  ...config,
  babelrcRoots: [
    '.',
    'packages/remirror__*',
    'packages/*',
    'website/.babelrc.js',
    'support/storybook/.babelrc.js',
  ],
  sourceType: 'unambiguous',
};

// https://github.com/conventional-changelog/commitlint/blob/v13.1.0/docs/reference-rules.md
module.exports = {
  extends: ['@commitlint/config-angular'],
  rules: {
    'type-enum': [
      2,
      'always',
      [
        'build',
        'chore',
        'ci',
        'docs',
        'feat',
        'fix',
        'perf',
        'refactor',
        'revert',
        'style',
        'test',

        // Custom
        'wip',
      ],
    ],
    'header-max-length': [2, 'always', 200],
  },
};

/**
 * Identifies whether this is an e2e test
 */
declare const __E2E__: boolean;

/**
 * A constant injected by jest to identify when this is a test run.
 */
declare const __TEST__: boolean;

/**
 * A constant injected by the build process to identify the version of the
 * currently running package. This is currently only available in public
 * packages with a types field.
 */
declare const __VERSION__: string;

/**
 * A constant injected by the build process which is true when this is a
 * development build.
 */
declare const __DEV__: boolean;

// @ts-check

const { E2E_DEBUG, E2E_COVERAGE, E2E_BROWSER = 'chromium' } = process.env;

/** @type {?} */
const browsers = E2E_BROWSER.split(',');
const collectCoverage = E2E_COVERAGE === 'true';
const debug = E2E_DEBUG === 'true';

/**
 * @type {import('jest-playwright-preset').JestPlaywrightConfig}
 */
const config = {
  launchOptions: {
    headless: !debug,
    timeout: 120_000,
    slowMo: debug ? 10 : undefined,
  },
  browsers,
  collectCoverage,
};

module.exports = config;

const { server: __SERVER__ } = require('testing/playwright');
const config = require('./support/jest/jest.config');
const { jestSupportDir } = require('./support/jest/helpers');

const { E2E_ENVIRONMENT = 'playwright' } = process.env;

const environment = {
  playwright: {
    globalSetup: jestSupportDir('./jest.playwright.setup.ts'),
    globalTeardown: jestSupportDir('./jest.playwright.teardown.ts'),
    testRunner: 'jest-circus/runner',
    preset: 'jest-playwright-preset',
    testEnvironment: jestSupportDir('./jest.playwright.environment.js'),
    setupFilesAfterEnv: [
      'expect-playwright',
      'jest-playwright-preset/lib/extends.js',
      jestSupportDir('jest.framework.ts'),
      jestSupportDir('jest.framework.e2e.ts'),
    ],
  },
}[E2E_ENVIRONMENT];

const {
  clearMocks,
  verbose,
  moduleFileExtensions,
  globals,
  transform,
  testPathIgnorePatterns,
  moduleNameMapper,
} = config;

/** @type Partial<import("@jest/types").Config.InitialOptions> */
module.exports = {
  clearMocks,
  verbose,
  moduleFileExtensions,
  transform,
  testPathIgnorePatterns,
  moduleNameMapper,
  globals: { ...globals, __SERVER__, __E2E__: true },
  cacheDirectory: '<rootDir>/.jest',
  modulePathIgnorePatterns: ['node_modules'],
  watchPlugins: ['jest-watch-typeahead/filename', 'jest-watch-typeahead/testname'],
  displayName: {
    name: 'remirror:e2e',
    color: 'black',
  },
  testMatch: __SERVER__.testMatch,
  ...environment,
};

const reporters = ['default'];

if (process.env.CI === 'true') {
  reporters.push('jest-github-reporter');
}

module.exports = {
  ...require('./support/jest/jest.config'),
  coverageThreshold: {
    global: {
      statements: 50,
      functions: 50,
      lines: 50,
    },
  },
  collectCoverageFrom: [
    '**/*.{ts,tsx}',
    '!**/*.d.ts',
    '!**/*.dts.ts',
    '!**/theme-styles.ts',
    '!**/styles.ts',
    '!**/__mocks__/**',
    '!**/__tests__/**',
    '!**/__dts__/**',
    '!**/__fixtures__/**',
    '!support/**',
    '!examples/**',
    '!website/**',

    // Currently can't be tested with JSDOM
    '!packages/remirror__extension-epic-mode/**',

    // Refactor upcoming for emoji which will change the structure.
    '!packages/remirror__extension-emoji/src/data/**',

    // Positions can't be tested properly in JSDOM. This will be addressed again
