/* eslint-env node */
module.exports = {
  root: true,
  parser: '@typescript-eslint/parser',
  plugins: ['@typescript-eslint'],
  extends: [
    'eslint:recommended',
    'plugin:@typescript-eslint/recommended',
    'plugin:import/recommended',
    'plugin:import/typescript',
    'plugin:react/recommended',
    'plugin:react/jsx-runtime',
    'plugin:react-hooks/recommended',
    'plugin:jsx-a11y/recommended',
    'prettier',
  ],
  ignorePatterns: ['dist/', 'website/'],
  settings: {
    'import/resolver': {
      typescript: true,
    },
    react: { version: 'detect' },
  },
  rules: {
    'import/no-unresolved': ['error', { ignore: ['react-tracked'] }],
    '@typescript-eslint/no-unused-vars': [
      'error',
      {
        args: 'all',
        argsIgnorePattern: '^_',
        caughtErrors: 'all',
        caughtErrorsIgnorePattern: '^_',
        destructuredArrayIgnorePattern: '^_',
        varsIgnorePattern: '^_',
        ignoreRestSiblings: true,
      },
    ],
  },
};

{
  "compilerOptions": {
    "strict": true,
    "target": "es2018",
    "esModuleInterop": true,
    "module": "nodenext",
    "skipLibCheck": true,
    "allowJs": true,
    "verbatimModuleSyntax": true,
    "noUncheckedIndexedAccess": true,
    "exactOptionalPropertyTypes": true,
    "jsx": "react-jsx",
    "baseUrl": ".",
    "paths": {
      "react-tracked": ["./src/index.js"]
    }
  },
  "exclude": ["dist", "examples"]
}

/// <reference types="vitest" />

import { resolve } from 'node:path';
import { defineConfig } from 'vite';

const { DIR, PORT = '8080' } = process.env;

export default defineConfig(({ mode }) => {
  if (mode === 'test') {
    return {
      resolve: { alias: { 'react-tracked': resolve('src') } },
      test: {
        environment: 'happy-dom',
        setupFiles: ['./tests/vitest-setup.js'],
      },
    };
  }
  if (!DIR) {
    throw new Error('DIR environment variable is required');
  }
  return {
    root: resolve('examples', DIR),
    server: { port: Number(PORT) },
    resolve: { alias: { 'react-tracked': resolve('src') } },
  };
});

import { resolve } from 'node:path';
import { defineWorkspace } from 'vitest/config';

export default defineWorkspace([
  {
    resolve: { alias: { 'react-tracked': resolve('src') } },
    test: {
      name: 'spec',
      environment: 'happy-dom',
      setupFiles: ['./tests/vitest-setup.js'],
    },
  },
  {
    test: {
      name: 'e2e',
      include: ['./tests/e2e/*.ts'],
      testTimeout: 15 * 1000,
    },
  },
]);
