module.exports = {
  root: true,
  parserOptions: {
    ecmaVersion: 2020,
    project: './tsconfig.json',
  },
  extends: [
    'airbnb/hooks',
    'airbnb-typescript',
    'prettier',
  ],
  env: {
    browser: true,
    jest: true,
  },
  plugins: ['compat', 'import', 'jsx-a11y', 'react', 'react-hooks'],
  rules: {
    'compat/compat': 'error',
    'import/prefer-default-export': 'off',
    // 'no-underscore-dangle': 'off',
    // 'react/jsx-filename-extension': 'off',
    'react/jsx-props-no-spreading': 'off',
    // 'react/no-array-index-key': 'off',
    'react/require-default-props': 'off',
    'react/prop-types': 'off',
    // 'jsx-a11y/click-events-have-key-events': 'off',
    // 'jsx-a11y/label-has-associated-control': 'off',
    // 'jsx-a11y/label-has-for': 'off',
  },
  settings: {
    polyfills: ['IntersectionObserver', 'Promise'],
  },
};

module.exports = (api) => {
  const env = api.env();
  api.cache.using(() => env === 'development');

  return {
    presets: ['@babel/preset-env', '@babel/preset-typescript', '@babel/preset-react'],
    plugins: [
      '@babel/plugin-transform-runtime',

      // Stage 3
      '@babel/plugin-proposal-class-properties',
    ],
    env: {
      esm: {
        presets: [
          [
            '@babel/preset-env',
            {
              modules: false,
            },
          ],
        ],
        plugins: [
          [
            '@babel/plugin-transform-runtime',
            {
              useESModules: true,
            },
          ],
        ],
      },
      umd: {
        presets: [
          [
            '@babel/preset-env',
            {
              targets: {
                android: '4.4',
                ios: '9',
              },
              useBuiltIns: 'usage',
              corejs: 3,
            },
          ],
        ],
        plugins: [['@babel/plugin-transform-runtime', { corejs: 3 }]],
      },
    },
    ignore: [
      '**/*.test.ts',
      '**/*.test.tsx',
    ]
  };
};

const cssnanoConfig = {
  preset: [
    'default',
    {
      calc: false,
      discardComments: { removeAll: true },
    },
  ],
};

const pxtoremConfig = {
  propList: [
    '*',
    '!border*',
    '!box-shadow',
  ],
  selectorBlackList: [':root'],
};

module.exports = (ctx) => ({
  map: ctx.env === 'development' && ctx.options ? ctx.options.map : false,
  plugins: {
    autoprefixer: {},
    'postcss-pxtorem': pxtoremConfig,
    cssnano: ctx.env === 'production' ? cssnanoConfig : false,
  },
});

import commonjs from '@rollup/plugin-commonjs';
import resolve from '@rollup/plugin-node-resolve';
import babel from '@rollup/plugin-babel';
import { terser } from 'rollup-plugin-terser';
import pkg from './package.json';

const name = 'ChatUI';
const extensions = ['.js', '.jsx', '.ts', '.tsx'];

export default {
  input: './src/index.ts',
  external: ['react', 'react-dom'],
  plugins: [
    resolve({ extensions }),
    commonjs(),
    babel({
      extensions,
      babelHelpers: 'runtime',
      include: ['src/**/*'],
    }),
    terser({
      output: { comments: false },
      compress: { drop_console: true },
    }),
  ],
  output: {
    file: pkg.browser,
    format: 'umd',
    name,
    globals: {
      react: 'React',
      'react-dom': 'ReactDOM',
    },
    intro: `exports.version = '${pkg.version}';`,
  },
};
