{
  "path": "@commitlint/prompt"
}

{
  "root": true,
  "ignorePatterns": ["projects/**/*"],
  "overrides": [
    {
      "files": ["*.ts"],
      "parserOptions": {
        "project": ["tsconfig.json"],
        "createDefaultProgram": true
      },
      "extends": [
        "plugin:@angular-eslint/recommended",
        "plugin:@angular-eslint/template/process-inline-templates"
      ],
      "rules": {
        "@angular-eslint/directive-selector": [
          "error",
          {
            "type": "attribute",
            "prefix": "mwl",
            "style": "camelCase"
          }
        ],
        "@angular-eslint/component-selector": [
          "error",
          {
            "type": "element",
            "prefix": "mwl",
            "style": "kebab-case"
          }
        ]
      }
    },
    {
      "files": ["*.html"],
      "extends": ["plugin:@angular-eslint/template/recommended"],
      "rules": {}
    }
  ]
}

{
  "singleQuote": true
}

{
  "extends": [
    "stylelint-config-standard",
    "stylelint-config-standard-scss",
    "stylelint-config-prettier"
  ],
  "rules": {
    "no-descending-specificity": null,
    "at-rule-no-unknown": null,
    "annotation-no-unknown": [
      true,
      {
        "ignoreAnnotations": "default"
      }
    ]
  }
}

{
  "$schema": "./node_modules/@angular/cli/lib/config/schema.json",
  "version": 1,
  "newProjectRoot": "projects",
  "projects": {
    "demos": {
      "root": "",
      "sourceRoot": "projects/demos",
      "projectType": "application",
      "prefix": "mwl",
      "schematics": {
        "@schematics/angular:component": {
          "style": "scss",
          "skipTests": true
        },
        "@schematics/angular:class": {
          "skipTests": true
        },
        "@schematics/angular:directive": {
          "skipTests": true
        },
        "@schematics/angular:guard": {
          "skipTests": true
        },
        "@schematics/angular:module": {},
        "@schematics/angular:pipe": {
          "skipTests": true
        },
        "@schematics/angular:service": {
          "skipTests": true
        }
      },
      "architect": {
        "build": {
          "builder": "@angular-devkit/build-angular:browser",
          "options": {
            "outputPath": "dist/demos",
            "index": "projects/demos/index.html",
            "main": "projects/demos/main.ts",
            "polyfills": "projects/demos/polyfills.ts",
            "tsConfig": "projects/demos/tsconfig.app.json",
            "assets": [
              "projects/demos/favicon.ico",
              "projects/demos/sw.js",
              "projects/demos/assets"
            ],
            "styles": ["projects/demos/styles.scss"],
            "scripts": [],
            "vendorChunk": true,
            "extractLicenses": false,
            "buildOptimizer": false,
            "sourceMap": true,
            "optimization": false,
            "namedChunks": true
          },
          "configurations": {
            "production": {
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "fileReplacements": [
                {
                  "replace": "projects/demos/environments/environment.ts",
                  "with": "projects/demos/environments/environment.prod.ts"
                }
              ],
              "optimization": true,
              "outputHashing": "all",
              "sourceMap": false,
              "namedChunks": false,
              "extractLicenses": true,
              "vendorChunk": false
            }
          },
          "defaultConfiguration": ""
        },
        "serve": {
          "builder": "@angular-devkit/build-angular:dev-server",
          "options": {
            "browserTarget": "demos:build"
          },
          "configurations": {
            "production": {
              "browserTarget": "demos:build:production"
            }
          }
        },
        "lint": {
          "builder": "@angular-eslint/builder:lint",
          "options": {
            "lintFilePatterns": [
              "projects/demos/**/*.ts",
              "projects/demos/**/*.html"
            ]
          }
        },
        "deploy": {
          "builder": "angular-cli-ghpages:deploy",
          "options": {
            "noSilent": true,
            "noBuild": true
          }
        }
      }
    },
    "angular-calendar": {
      "root": "projects/angular-calendar",
      "sourceRoot": "projects/angular-calendar/src",
      "projectType": "library",
      "prefix": "mwl",
      "architect": {
        "build": {
          "builder": "@angular-devkit/build-angular:ng-packagr",
          "options": {
            "tsConfig": "projects/angular-calendar/tsconfig.lib.json",
            "project": "projects/angular-calendar/ng-package.json"
          },
          "configurations": {
            "production": {
              "tsConfig": "projects/angular-calendar/tsconfig.lib.prod.json"
            }
          }
        },
        "test": {
          "builder": "@angular-devkit/build-angular:karma",
          "options": {
            "main": "projects/angular-calendar/src/test-entry.ts",
            "tsConfig": "projects/angular-calendar/tsconfig.spec.json",
            "karmaConfig": "projects/angular-calendar/karma.conf.ts",
            "sourceMap": true,
            "styles": ["projects/angular-calendar/src/angular-calendar.scss"]
          }
        },
        "lint": {
          "builder": "@angular-eslint/builder:lint",
          "options": {
            "lintFilePatterns": [
              "projects/angular-calendar/**/*.ts",
              "projects/angular-calendar/**/*.html"
            ]
          }
        }
      }
    }
  },
  "cli": {
    "schematicCollections": ["@angular-eslint/schematics"],
    "analytics": false,
    "packageManager": "pnpm"
  },
  "schematics": {
    "@angular-eslint/schematics:application": {
      "setParserOptionsProject": true
    },
    "@angular-eslint/schematics:library": {
      "setParserOptionsProject": true
    }
  }
}

const autoprefixer = require('autoprefixer');

module.exports = () => ({
  plugins: [
    autoprefixer({
      overrideBrowserslist: [
        '> 1%',
        'last 4 versions',
        'last 20 Chrome versions',
        'last 20 Firefox versions',
      ],
    }),
  ],
});

{
  "compileOnSave": false,
  "compilerOptions": {
    "baseUrl": "./",
    "outDir": "./dist/out-tsc",
    "sourceMap": true,
    "declaration": false,
    "module": "es2020",
    "moduleResolution": "node",
    "experimentalDecorators": true,
    "target": "ES2022",
    "importHelpers": true,
    "typeRoots": ["node_modules/@types"],
    "lib": ["es2017", "dom"],
    "paths": {
      "angular-calendar": ["./projects/angular-calendar/src"],
      "angular-calendar/date-adapters/date-fns": [
        "./projects/angular-calendar/src/date-adapters/date-fns"
      ],
      "angular-calendar/date-adapters/moment": [
        "./projects/angular-calendar/src/date-adapters/moment"
      ]
    },
    "allowSyntheticDefaultImports": true,
    "useDefineForClassFields": false
  },
  "angularCompilerOptions": {
    "strictTemplates": true,
    "strictInjectionParameters": true
  }
}
