module.exports = {
    root: true,
    env: {
        browser: true,
        es2021: true,
        node: true,
    },
    extends: ['airbnb-typescript', 'eslint:recommended', 'plugin:@typescript-eslint/recommended'],
    parser: '@typescript-eslint/parser',
    parserOptions: {
        ecmaVersion: 13,
        sourceType: 'module',
        tsconfigRootDir: __dirname,
        project: ['./tsconfig.root.json', './lib/*/tsconfig.json', './docs/tsconfig.json', './exts/*/*/tsconfig.json', './exts/*/tsconfig.json'],
    },
    plugins: ['import', '@typescript-eslint'],
    ignorePatterns: ['./build/**/*', './dist/**/*', './lib/*/public/**/*', './exts/*/*/public/**/*'],
    rules: {
        'semi-spacing': 'error',
        'indent': ['error', 4, {'SwitchCase': 1}],
        'object-curly-spacing': ['error', 'never'],
        '@typescript-eslint/indent': 'off',
        '@typescript-eslint/object-curly-spacing': 'off',
        '@typescript-eslint/no-var-requires': 'off',
        'react/jsx-filename-extension': 'off',
        'import/no-extraneous-dependencies': 'off',
        '@typescript-eslint/no-non-null-assertion': 'off',
        'no-fallthrough': 'off',
    },
    overrides: [
        {
            files: ['**/*.{ts,tsx}'],
            rules: {
                '@typescript-eslint/no-unused-vars': 'warn',
                '@typescript-eslint/no-var-requires': 'error',
                '@typescript-eslint/indent': ['error', 4, {'SwitchCase': 1}],
                '@typescript-eslint/object-curly-spacing': ['error', 'never'],
                '@typescript-eslint/no-use-before-define': ['error', {functions: false}],
                '@typescript-eslint/ban-types': ['error', {types: {'{}': false}}],
            },
        },
    ],
};

const minimist = require('minimist');

const argv = minimist(process.argv.slice(4));
const extraPlugins = [];
const cssnanoFlag = argv.cssnano || process.env.POSTCSS_CSSNANO;
if (cssnanoFlag !== 'no' && (process.env.NODE_ENV === 'production' || cssnanoFlag)) {
    extraPlugins.push(require('cssnano'));
}
if (argv.rem2px || process.env.POSTCSS_REM2PX) {
    extraPlugins.push(require('postcss-rem-to-pixel')({propList: ['*']}));
}

module.exports = {
    plugins: [
        require('postcss-import'),
        require('tailwindcss'),
        require('postcss-inset')(),
        require('autoprefixer'),
        ...extraPlugins,
    ],
};

const minimist = require('minimist');
const colorVariable = require('@mertasan/tailwindcss-variables/colorVariable');
const defaultTheme = require('./config/tailwind-theme/index.cjs');

/** @type {import('tailwindcss').Config} */
const config = {
    darkMode: 'media',
    content: process.env.NODE_ENV === 'development' ? [
        './index.html',
        './index.md',
        './src/**/*.{vue,js,ts,jsx,tsx}',
        './lib/*/index.html',
        './lib/*/README.md',
        './lib/*/src/**/*.{vue,js,ts,jsx,tsx}',
        './exts/*/*/src/**/*.{vue,js,ts,jsx,tsx}',
    ] : [{raw: ''}],
    safelist: ['dark'],
    theme: defaultTheme,
    plugins: [
        require('@mertasan/tailwindcss-variables')({
            colorVariables: true,
            darkToRoot: false,
        }),
    ],
    prefix: '-',
};

function colorToVars(colorObject, parentName = 'color', vars = {}) {
    Object.keys(colorObject).forEach(name => {
        const value = colorObject[name];
        if (!value || ['transparent', 'inherit', 'currentColor'].includes(value)) {
            return;
        }
        const varName = String(name).replace(/([A-Z])/g, '-$1').toLowerCase();
        if (typeof value === 'object') {
            vars[name] = colorToVars(value, `${parentName}-${varName}`);
        } else {
            vars[name] = value.startsWith('#') ? colorVariable(`--${parentName}-${varName}`) : `var(--${parentName}-${varName})`;
        }
    });
    return vars;
}

const argv = minimist(process.argv.slice(4));

let tailwind = argv.tailwind || process.env.TAILWIND_CONFIG;
if (tailwind) {
    const mergePresets = (preset) => {
        if (typeof preset === 'function') {
            mergePresets(preset({config, colorToVars}));
        } else if (Array.isArray(preset)) {
            preset.forEach(mergePresets);
        } else if (typeof preset === 'object') {
            if (!config.presets) {
                config.presets = [];
            }
            config.presets.push(preset);
        }
    };
    if (typeof tailwind === 'string') {
        tailwind = tailwind.split(',');
    }
    if (Array.isArray(tailwind)) {
        tailwind.forEach(tailwindPath => {
            mergePresets(require(tailwindPath));
        });
    }
}

if (argv.noPreflightStyle || process.env.TAILWIND_NO_PREFLIGHT) {
    config.corePlugins = {
        preflight: false,
    };
}

[config.theme, config.theme.extend].forEach(theme => {
    const varsSafelist = [];
    ['variables', 'darkVariables'].forEach(key => {
        const variables = theme[key];
        if (typeof variables === 'object') {
            Object.keys(variables).forEach(varKey => {
                if (varKey.startsWith('.')) {
                    varsSafelist.push(varKey.substring(1));
                }
            });
        }
    });
    if (varsSafelist.length) {
        config.safelist = [...new Set([...(config.safelist || []), ...varsSafelist])];
    }
});

module.exports = config;

import Path, {dirname} from 'path';
import fs from 'fs-extra';
import {defineConfig, mergeConfig, type UserConfig, type LibraryOptions} from 'vite';
import {blue} from 'colorette';
import eslint from 'vite-plugin-eslint';
import {viteZip} from 'vite-plugin-zip-file';
import preact from '@preact/preset-vite';
import configDevServer from './scripts/dev/config-server';
import {getLibs} from './scripts/libs/query';
import {LibInfo} from './scripts/libs/lib-info';
import packageJson from './package.json';

function getLibByPath(path: string, libsCache: Record<string, LibInfo>): LibInfo | undefined {
    const nodeModulesFlag = `${Path.sep}node_modules${Path.sep}`;
    const nodeModulesIndex = path.indexOf(nodeModulesFlag);
    if (nodeModulesIndex > -1) {
        const nodeModulePath = path.substring(nodeModulesIndex + nodeModulesFlag.length);
        return Object.values(libsCache).find((x) => nodeModulePath.startsWith(`${x.name}${Path.sep}`));
    }
    return Object.values(libsCache).find((x) => path.startsWith(`${x.zui.path}${Path.sep}`));
}

export default defineConfig(async ({mode}) => {
    const buildLibs = process.env.BUILD_LIBS ?? 'buildIn';
    const libsCache: Record<string, LibInfo> | undefined = await getLibs(buildLibs.split(','));

    const configFile = process.env.VITE_EXTRA_CONFIG;
    let extraBuildConfig: Partial<UserConfig> | undefined;
    let libFileName = 'zui';
    if (configFile) {
        const configFromFile = Path.isAbsolute(configFile) ? configFile : Path.resolve(__dirname, configFile);
        extraBuildConfig = await fs.readJSON(configFromFile);
        const lib = extraBuildConfig?.build?.lib;
        if (lib && typeof lib.fileName === 'string') {
            libFileName = lib.fileName || libFileName;
        }
        console.log(blue('merged extra vite config file:'), '\n', Path.relative(__dirname, configFromFile) + '\n');
    }

    let viteConfig: UserConfig = {
        base: './',
        build: {
            outDir: 'dist/dev',
            rollupOptions: {
                output: {
                    assetFileNames: (chunkInfo) => {
                        if (chunkInfo.name == 'style.css' && viteConfig.build?.lib) {
                            return `${libFileName}.css`;
                        }
                        return chunkInfo.name ?? 'noname';
                    },
                },
            },
            assetsInlineLimit: 256,
            sourcemap: true,
            cssMinify: false,
        },
        esbuild: {
            jsxFactory: 'h',
            jsxFragment: 'Fragment',
            jsxInject: 'import {h} from \'preact\'',
        },
        resolve: {
            preserveSymlinks: true,
            alias: [
                {find: /^@zui\/(.*)/, replacement: `${__dirname}/lib/$1`},
                {find: 'zui-dev', replacement: `${__dirname}/dev`},
                {find: 'zui-config', replacement: `${__dirname}/config`},
                {find: '~/', replacement: `${__dirname}/`},
                {find: '@/', replacement: '/', customResolver: (source, importer) => {
                    if (!importer) {
                        return;
                    }
                    const lib = getLibByPath(importer, libsCache);
                    if (!lib) {
                        return Path.join(__dirname, source);
                    }
                    if (source.startsWith('/public/') && mode !== 'development') {
                        return `/${lib.zui.publicPath || lib.zui.name}/${source.replace('/public/', '')}`;
                    }
                    return Path.join(lib.zui.path, source);
                }},
                ...Object.values(libsCache).reduce<{find: string, replacement: string}[]>((aliasList, info) => {
                    if (info.zui.sourceType === 'exts') {
                        aliasList.push({find: info.name, replacement: info.zui.path});
                        if (info.zui.replace) {
                            aliasList.push({find: info.zui.replace, replacement: info.zui.path});
                        }
                    }
                    return aliasList;
                }, []),
            ],
        },
        define: {
            'process.env.NODE_ENV': JSON.stringify(mode),
            __BUILD_TIME__: Date.now(),
            __APP_VERSION__: JSON.stringify(packageJson.version),
        },
        experimental: {
            renderBuiltUrl(filename: string, {type}: {hostId: string, hostType: 'js' | 'css' | 'html', type: 'public' | 'asset'}) {
                if (type === 'public') {
                    return `./${filename}`;
                }
                return {relative: true};
            },
        },
    };

    if (extraBuildConfig) {
        viteConfig = mergeConfig(viteConfig, extraBuildConfig);
        const lib = viteConfig.build!.lib as LibraryOptions;
        lib.fileName = (format) => {
            if (format === 'umd') {
                return `${libFileName}.js`;
            }
            if (format === 'es') {
                return `${libFileName}.esm.js`;
            }
            return `${libFileName}.${format}.js`;
        };
    }

    viteConfig = mergeConfig(viteConfig, {
        plugins: [
            eslint(),
            ...(mode === 'development' ? [
                preact(),
                configDevServer({
                    rootPath: __dirname,
                }),
            ] : []),
            ...(process.env.ZIP ? [
                viteZip({
                    folderPath: viteConfig.build!.outDir,
                    outPath: process.env.ZIP_OUT ?? dirname(viteConfig.build!.outDir!),
                    zipName: process.env.ZIP,
                }),
            ] : []),
        ],
    });

    return viteConfig;
});
