// eslint-disable-next-line import/no-unresolved, n/no-missing-require
const a11yReport = require('./test-reports/lighthouse/a11y.report.json');

const a11yScore = a11yReport.categories.accessibility.score;
const a11yScoreFormatted = `${a11yScore ? a11yScore * 100 : 0}%`;

/* eslint-disable no-console */
console.log('*************************');
console.log('Lighthouse accessibility score: ', a11yScoreFormatted);
console.log('*************************');
/* eslint-enable no-console */

if (a11yScore !== 1) {
  // fail build
  // eslint-disable-next-line no-restricted-syntax
  throw new Error(
    'NOTE: Lighthouse accessibility audit score must be 100% to pass this build step.',
  );
}

const path = require('path');

module.exports = (api) => {
  const isTest = api.env('test');
  const isProduction = api.env('production');

  return {
    presets: [
      '@babel/react',
      '@babel/preset-typescript',
      [
        '@babel/env',
        isTest ? { targets: { node: 'current' } } : { loose: true },
      ],
    ].filter(Boolean),
    plugins: [
      [
        'babel-plugin-module-resolver',
        {
          alias: {
            '@hello-pangea/dnd':
              process.env.USE_PRODUCTION_BUILD === 'true'
                ? path.resolve(__dirname, './dist/dnd.esm')
                : path.resolve(__dirname, './src/index.ts'),
          },
        },
      ],
      '@babel/plugin-transform-object-assign',
      ['@babel/plugin-transform-class-properties', { loose: true }],
      ['@babel/plugin-transform-private-methods', { loose: true }],
      ['@babel/plugin-transform-private-property-in-object', { loose: true }],
      // used for stripping out the `invariant` messages in production builds
      isProduction ? 'babel-plugin-dev-expression' : false,
    ].filter(Boolean),
    comments: false,
  };
};

const childProcess = require('child_process');
const path = require('path');
const waitOn = require('wait-on');
const ports = require('./server-ports');

const storybook = childProcess.spawn(process.execPath, [
  path.resolve(
    __dirname,
    'node_modules',
    'cross-env',
    'src',
    'bin',
    'cross-env-shell.js',
  ),
  'DISABLE_HMR=true',
  'USE_PRODUCTION_BUILD=true',
  path.resolve(__dirname, 'node_modules', '.bin', 'storybook'),
  'dev',
  '--ci',
  '-p',
  `${ports.storybook}`,
]);

const cspServer = childProcess.spawn(process.execPath, [
  path.resolve(
    __dirname,
    'node_modules',
    'cross-env',
    'src',
    'bin',
    'cross-env-shell.js',
  ),
  'USE_PRODUCTION_BUILD=true',
  path.resolve(__dirname, 'csp-server', 'start.sh'),
  `${ports.cspServer}`,
]);

process.on('exit', () => {
  storybook.kill();
  cspServer.kill();
});

const cmdArgsMap = {
  accessibility: ['test:accessibility'],
  browser: ['test:browser:ci'],
};

waitOn({
  resources: [
    `http://localhost:${ports.storybook}`,
    `http://localhost:${ports.cspServer}`,
  ],
  timeout: 60000,
})
  .then(() => {
    if (!process.argv[2]) {
      // eslint-disable-next-line no-restricted-syntax
      throw new Error('Started servers but no command supplied to run after');
    }

    const args = cmdArgsMap[process.argv[2]];

    if (!args) {
      // eslint-disable-next-line no-restricted-syntax
      throw new Error('Unknown argument provided');
    }

    const child = childProcess.spawn('pnpm', args, {
      stdio: 'inherit',
    });

    process.on('exit', () => {
      child.kill();
    });

    child.on('exit', (code) => {
      // eslint-disable-next-line n/no-process-exit
      process.exit(code);
    });
  })
  .catch((error) => {
    storybook.kill();
    cspServer.kill();

    // eslint-disable-next-line no-console
    console.error(error);
    // eslint-disable-next-line no-restricted-syntax
    throw new Error('Unable to spin up standalone servers');
  });

import { defineConfig } from 'cypress';

export default defineConfig({
  pageLoadTimeout: 10000,
  retries: 1,
  viewportHeight: 600,
  viewportWidth: 800,

  e2e: {
    baseUrl: 'http://localhost:9002',
    specPattern: 'cypress/**/*.spec.ts',
  },
});

/* eslint-disable @typescript-eslint/triple-slash-reference */
/// <reference path="./test/typings/environment.d.ts" />

import type { Config } from 'jest';
import isRunningInCI from './test/util/is-running-in-ci';

const config: Config = {
  clearMocks: true,
  modulePathIgnorePatterns: ['/dist/'],
  resetMocks: true,
  resetModules: true,
  restoreMocks: true,

  setupFiles: ['./test/setup/env-setup.ts'],
  setupFilesAfterEnv: ['./test/setup/test-setup.ts'],
  testEnvironment: './test/setup/environment.ts',
  // node_modules is default.
  testPathIgnorePatterns: ['/node_modules/', '/cypress/'],
  transform: {
    '\\.[jt]sx?$': 'babel-jest',
  },
  verbose: true,
  watchPlugins: [
    'jest-watch-typeahead/filename',
    'jest-watch-typeahead/testname',
  ],
};

if (isRunningInCI()) {
  config.maxWorkers = 2;
}

export default config;

module.exports = {
  extends: 'lighthouse:default',
  settings: {
    onlyCategories: ['accessibility'],
    skipAudits: ['bypass'],
  },
  passes: [
    {
      passName: 'defaultPass',
      recordTrace: false,
      gatherers: ['accessibility'],
    },
  ],
};

{
  extends: ["config:base", ":prConcurrentLimitNone"],

  timezone: "America/New_York",
  schedule: ["before 6am"],

  commitMessageAction: "update",
  commitMessagePrefix: "chore(deps):",
  labels: ["dependencies"],

  packageRules: [
    {
      matchCategories: ["ci"],
      commitMessagePrefix: "ci:",
    },
    {
      matchManagers: ["npm"],
      matchDepTypes: ["dependencies"],
      rangeStrategy: "bump",
    },
    {
      matchManagers: ["npm"],
      matchDepTypes: ["devDependencies"],
      matchUpdateTypes: ["major"],
      commitMessagePrefix: "chore(dev-deps):",
    },
    {
      groupName: "bump all non-major development dependencies",
      groupSlug: "all-dev-minor-patch",
      matchManagers: ["npm"],
      matchDepTypes: ["devDependencies"],
      matchUpdateTypes: ["minor", "patch"],
      matchPackagePatterns: ["*"],
      commitMessagePrefix: "chore(dev-deps):",
    },
    {
      groupName: "bump eslint dependencies",
      groupSlug: "all-eslint-major",
      matchManagers: ["npm"],
      matchDepTypes: ["devDependencies"],
      matchUpdateTypes: ["major"],
      packagePatterns: [".*eslint.*"],
      commitMessagePrefix: "chore(dev-deps):",
    },
    {
      matchManagers: ["npm"],
      matchDepTypes: ["peerDependencies"],
      rangeStrategy: "widen",
      commitMessagePrefix: "chore(peer-deps):",
    },
  ],
}

import babel from '@rollup/plugin-babel';
import commonjs from '@rollup/plugin-commonjs';
import json from '@rollup/plugin-json';
import resolve from '@rollup/plugin-node-resolve';
import replace from '@rollup/plugin-replace';
import strip from '@rollup/plugin-strip';
import terser from '@rollup/plugin-terser';
import dts from 'rollup-plugin-dts';
import { readFileSync } from 'node:fs';

// Rollup 3 reads this file as an ESModule which requires a workaround to read json files
// See https://rollupjs.org/command-line-interface/#caveats-when-using-native-node-es-modules
const pkg = JSON.parse(
  readFileSync(new URL('./package.json', import.meta.url)),
);

const input = './src/index.ts';
const extensions = ['.ts', '.tsx'];

// Treat as externals all not relative and not absolute paths
// e.g. 'react'
const excludeAllExternals = (id) => !id.startsWith('.') && !id.startsWith('/');

const getBabelOptions = ({ useESModules }) => ({
  extensions,
  exclude: 'node_modules/**',
  babelHelpers: 'runtime',
  plugins: [['@babel/plugin-transform-runtime', { useESModules }]],
});

const commonjsArgs = {
  include: 'node_modules/**',
};

export default [
  // Universal module definition (UMD) build
  // - including console.* statements
  // - conditionally used to match snapshot size
  {
    input,
    output: {
      file: 'dist/dnd.js',
      format: 'umd',
      name: 'ReactBeautifulDnd',
      globals: { react: 'React', 'react-dom': 'ReactDOM' },
    },
    // Only deep dependency required is React
    external: ['react', 'react-dom'],
    plugins: [
      json(),
      babel(getBabelOptions({ useESModules: true })),
      resolve({ extensions }),
      commonjs(commonjsArgs),
      replace({
        preventAssignment: true,
        'process.env.NODE_ENV': JSON.stringify('development'),
      }),
    ],
  },

  // Minified UMD build
  {
    input,
    output: {
      file: 'dist/dnd.min.js',
      format: 'umd',
      name: 'ReactBeautifulDnd',
      globals: { react: 'React', 'react-dom': 'ReactDOM' },
    },
    // Only deep dependency required is React
    external: ['react', 'react-dom'],
    plugins: [
      json(),
      babel(getBabelOptions({ useESModules: true })),
      resolve({ extensions }),
      commonjs(commonjsArgs),
      strip(),
      replace({
        preventAssignment: true,
        'process.env.NODE_ENV': JSON.stringify('production'),
      }),
      terser(),
      // Useful for debugging: you can see what code is dropped
      // terser({
      //   mangle: false,
      //   compress: false,
      //   keep_fnames: true,
      //   output: { beautify: true },
      // }),
    ],
  },

  // CommonJS (cjs) build
  // - Keeping console.log statements
  // - All external packages are not bundled
  {
    input,
    output: { file: pkg.main, format: 'cjs' },
    external: excludeAllExternals,
    plugins: [
      json(),
      resolve({ extensions }),
      babel(getBabelOptions({ useESModules: false })),
    ],
  },

  // EcmaScript Module (esm) build
  // - Keeping console.log statements
  // - All external packages are not bundled
  {
    input,
    output: { file: pkg.module, format: 'esm' },
    external: excludeAllExternals,
    plugins: [
      json(),
      resolve({ extensions }),
      babel(getBabelOptions({ useESModules: true })),
    ],
  },

  // TypeScript declaration
  {
    input,
    output: [{ file: 'dist/dnd.d.ts', format: 'es' }],
    plugins: [dts()],
  },
];

const ports = {
  storybook: 9002,
  cspServer: 9003,
};

module.exports = ports;
