/* eslint-disable @typescript-eslint/no-var-requires */
const path = require('path');

module.exports = {
  parser: '@typescript-eslint/parser',
  plugins: ['@typescript-eslint', 'prettier'],
  extends: ['plugin:@typescript-eslint/recommended', 'plugin:prettier/recommended'],
  parserOptions: {
    sourceType: 'module',
    useJSXTextNode: true,
    project: [path.resolve(__dirname, 'tsconfig.test.json')],
  },
  rules: {
    'no-underscore-dangle': 0,
    'arrow-body-style': 0,
    'no-unused-expressions': 1,
    'no-plusplus': 0,
    'no-console': 0,
    'func-names': 0,
    'comma-dangle': [
      'error',
      {
        arrays: 'always-multiline',
        objects: 'always-multiline',
        imports: 'always-multiline',
        exports: 'always-multiline',
        functions: 'never',
      },
    ],
    'no-prototype-builtins': 0,
    'prefer-destructuring': 0,
    'no-else-return': 1,
    'lines-between-class-members': ['error', 'always', { exceptAfterSingleLine: true }],
    '@typescript-eslint/explicit-member-accessibility': 0,
    '@typescript-eslint/no-explicit-any': 0,
    '@typescript-eslint/no-inferrable-types': 0,
    '@typescript-eslint/explicit-function-return-type': 0,
    '@typescript-eslint/no-use-before-define': 0,
    '@typescript-eslint/no-empty-function': 0,
    curly: ['error', 'all'],
    'padding-line-between-statements': [
      'warn',
      {
        blankLine: 'always',
        prev: '*',
        next: 'return', // add blank line *before* all returns (if there are statements before)
      },
      {
        blankLine: 'always',
        prev: '*',
        next: 'if', // add blank line *before* all ifs
      },
      {
        blankLine: 'always',
        prev: 'if',
        next: '*', // add blank line *after* all ifs
      },
      {
        blankLine: 'any',
        prev: 'if',
        next: 'if', // allow blank line between ifs, but not enforce either
      },
      {
        blankLine: 'always',
        prev: '*',
        next: ['function', 'class'], // add blank line *before* all functions and classes
      },
      {
        blankLine: 'always',
        prev: ['function', 'class'],
        next: '*', // add blank line *after* all functions and classes
      },
      {
        blankLine: 'always',
        prev: '*',
        next: 'import', // add blank line *before* all imports
      },
      {
        blankLine: 'always',
        prev: 'import',
        next: '*', // add blank line *after* all imports
      },
      {
        blankLine: 'never',
        prev: 'import',
        next: 'import', // dont allow blank line between imports
      },
    ],
  },
  env: {
    node: true,
    jest: true,
  },
};

module.exports = {
  plugins: [
    [
      '@semantic-release/commit-analyzer',
      {
        preset: "angular",
        releaseRules: [
          {breaking: true, release: 'major'},
          {type: "feat", release: "minor"},
          {type: "fix", release: "patch"},
          {type: "docs", release: false},
          {type: "style", release: false},
          {type: "refactor", release: "patch"},
          {type: "perf", release: "patch"},
          {type: "test", release: false},
          {type: "chore", release: false},
          {type: "deps", release: "minor"},
          {type: "devdeps", release: false},
          // backwards compatability, remove after 9.0 has been published
          {type: "dependencies", release: "minor"},
          // dont trigger another release on release commit
          {type: "release", release: false}
        ],
        parserOpts: {
          noteKeywords: ['BREAKING CHANGE', 'BREAKING CHANGES']
        }
      }
    ],
    [
      '@semantic-release/release-notes-generator',
      {
        preset: "conventionalcommits",
        presetConfig: {
          types: [
            {type: "feat", section: "Features"},
            {type: "fix", section: "Fixes"},
            {type: "docs", hidden: true},
            {type: "style", section: "Style"},
            {type: "refactor", section: "Refactor"},
            {type: "perf", section: "Performance"},
            {type: "test", hidden: true},
            {type: "chore", hidden: true},
            {type: "deps", section: "Dependencies"},
            {type: "devdeps", section: "Dev-Dependencies"},
            // backwards compatability, remove after 9.0 has been published
            {type: "dependencies", section: "Dependencies"},
            {type: "revert", section: "Reverts"},
            {type: "release", hidden: true}
          ]
        }
      }
    ],
    [
      // Update versions in sub-packages dependencies
      "semantic-release-replace-plugin",
      {
        "replacements": [
          {
            "files": ["packages/*/package.json"],
            "from": "\"mongodb-memory-server-core\": \".*\"",
            "to": "\"mongodb-memory-server-core\": \"${nextRelease.version}\"",
          }
        ]
      }
    ],
    "@semantic-release/changelog",
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server-core',
      },
    ],
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server',
      },
    ],
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server-global',
      },
    ],
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server-global-4.0',
      },
    ],
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server-global-4.2',
      },
    ],
    [
      '@semantic-release/npm',
      {
        npmPublish: true,
        pkgRoot: './packages/mongodb-memory-server-global-4.4',
      },
    ],
    ["@semantic-release/git", {
      "assets": ["packages/*/package.json", "CHANGELOG.md"],
      "message": "release: v${nextRelease.version}"
    }],
    "@semantic-release/github"
  ],
  branches: [
    // from what i read in the semantic-release configuration and in some issues, the order has to be like this:
    // other branches
    // main / upstream branch
    // prerelease branches
    { name: "old\\/(\\d+)(\\.x)", range: "${name.replace(/^old\\//g, '')}", prerelease: false },
    "master",
    { name: "beta", prerelease: true }
  ]
};
