const NodeGlobals = ['module', 'require'];

module.exports = {
  root: true,
  parser: '@typescript-eslint/parser',
  ignorePatterns: [
    '**/dist',
    'dev/',
    'shimsVue.d.ts',
    'api-extractor.json',
    'packages/*/dist',
  ],
  parserOptions: {
    sourceType: 'module',
  },
  plugins: ['@typescript-eslint'],
  rules: {
    'no-unused-vars': 'off',
    // Fix "no-unused-vars" check on function parameters within TypeScript Types/Interfaces
    '@typescript-eslint/no-unused-vars': [
      'error',
      // we are only using this rule to check for unused arguments since TS
      // catches unused variables but not args.
      { varsIgnorePattern: '.*', args: 'after-used', argsIgnorePattern: '^_' },
    ],
    // most of the codebase are expected to be env agnostic
    'no-restricted-globals': ['error', ...NodeGlobals],
    // forbidden to use export default
    'no-restricted-syntax': [
      0,
      {
        selector: 'ExportDefaultDeclaration',
        message: 'Forbidden to use export default.',
      },
    ],
    camelcase: [
      'error',
      {
        properties: 'never',
      },
    ],
    semi: [0, 'never'],
    eqeqeq: [2, 'allow-null'],
    quotes: ['error', 'single'],
    'no-var': 2,
    'prefer-const': 2,
    'func-style': [0, 'declaration'],
    'comma-dangle': ['error', 'always-multiline'],
    'multiline-ternary': ['error', 'always-multiline'],
    'multiline-comment-style': ['error', 'separate-lines'],
  },
  overrides: [
    {
      files: [
        'scripts/**',
        '*.config.js',
        '.eslintrc.js',
        '**/__tests__/**',
        'packages/utils/src/test.ts',
      ],
      rules: {
        'object-curly-newline': 'off',
        'no-restricted-globals': 'off',
        'no-restricted-syntax': 'off',
      },
    },
    {
      files: ['.eslintrc.js'],
      rules: {
        'array-element-newline': ['error', 'consistent'],
        'object-curly-newline': ['error', { consistent: true }],
      },
    },
  ],
};

function readPackage(pkg, context) {
  // vue-template-compiler use vue as devDependency and use file protocol
  if (pkg.name === 'vue-template-compiler' && pkg.version.startsWith('2.')) {
    pkg.devDependencies = {
      ...pkg.devDependencies,
      vue: '^2.0.0',
    }
    pkg.peerDependencies = {
      vue: '^2.0.0',
    }
    context.log('vue => vue@2 in dependencies of vue-template-compiler');
  }

  return pkg;
}

module.exports = {
  hooks: {
    readPackage,
  },
}

{
  "semi": true,
  "singleQuote": true,
  "printWidth": 80,
  "trailingComma": "all"
}

module.exports = {
  presets: [
    [
      '@babel/preset-env',
      {
        useBuiltIns: 'entry',
        targets: ['last 2 versions', 'ie >= 9'],
        modules: 'commonjs',
      },
    ],
  ],
  plugins: ['@babel/plugin-transform-regenerator'],
};

import { defineConfig } from 'cypress'

export default defineConfig({
  projectId: 'yren6i',
  e2e: {
    // We've imported your old cypress plugins here.
    // You may want to clean this up later by importing these.
    setupNodeEvents(on, config) {
      return require('./cypress/plugins/index.js')(on, config)
    },
    specPattern: 'cypress/e2e/**/*.{js,jsx,ts,tsx}',
  },
})

// For a detailed explanation regarding each configuration property, visit:
// https://jestjs.io/docs/en/configuration.html

module.exports = {
  clearMocks: true,
  testTimeout: 20000,
  testEnvironment: 'jsdom',
  preset: 'jest-puppeteer',
  coverageDirectory: 'coverage',
  coveragePathIgnorePatterns: ['__tests__', '/node_modules/'],
  coverageProvider: 'v8',
  globals: {
    __DEV__: true,
    __TEST__: true,
    __VERSION__: '"unknow"',
  },
  preset: 'ts-jest',
  transformIgnorePatterns: [
    // Change MODULE_NAME_HERE to your module that isn't being compiled
    '/node_modules/(?!(@garfish)).+\\.js$',
  ],
  transform: {
    '^.+\\.(t|j)sx?$': ['@swc/jest'],
  },
  rootDir: __dirname,
  testMatch: ['<rootDir>/packages/**/__tests__/**/*spec.[jt]s?(x)'],
  testPathIgnorePatterns: ['/node_modules/', '/dev/'],
  moduleNameMapper: {
    '@garfish/(.*)': '<rootDir>packages/$1/src',
  },
};

{
  "extends": [
    "config:base"
  ]
}

import type { Options } from 'tsup';
import { replace } from 'esbuild-plugin-replace';

const watch = process.env.WATCH;
const debug = process.env.DEBUG;
const sourcemap = Boolean(process.env.SOURCEMAP);
const dts = process.env.DTS === 'false' ? false : true;

export const baseTsup = (pkg, deleteGlobalName = false): Options => {
  const options: Options = {
    dts,
    sourcemap,
    clean: true,
    watch: watch ? 'src/' : false,
    // Cannot output iife format using wireit cache
    format: ['esm', 'cjs'],
    legacyOutput: true,
    globalName: pkg.name
      .replace(/@/g, '')
      .split(/[\/-]/g)
      .map((l) => l[0].toUpperCase() + l.slice(1))
      .join(''),
    esbuildPlugins: [
      replace({
        include: /.(ts|js)$/,
        __TEST__: 'false',
        __VERSION__: `'${pkg.version}'`,
        __DEV__:
          '(typeof process !== "undefined" && process.env && process.env.NODE_ENV ? (process.env.NODE_ENV !== "production") : false)',
      }),
    ],
  };

  // Can be directly by chrome plugins debugging injected garfish page
  if (deleteGlobalName) {
    delete options.globalName;
  }
  return options;
};
