/**
 * e2eテストと .env の設計：
 * - デフォルトで .env.test を読み込む。
 *   モックエンジンが使われる。
 * - Electronテストはテストファイル内で様々な .env を読み込む。
 *   テスト条件によって用意したい環境が異なるため。
 */

import type { PlaywrightTestConfig, Project } from "@playwright/test";
import dotenv from "dotenv";

dotenv.config({ path: ".env.test", override: true });

let project: Project;
let webServers: PlaywrightTestConfig["webServer"];
const isElectron = process.env.VITE_TARGET === "electron";
const isBrowser = process.env.VITE_TARGET === "browser";
const isStorybook = process.env.TARGET === "storybook";

const viteServer = {
  command: "vite --mode test --port 7357",
  port: 7357,
  reuseExistingServer: !process.env.CI,
};
const storybookServer = {
  command: "storybook dev --ci --port 7357",
  port: 7357,
  reuseExistingServer: !process.env.CI,
};

if (isElectron) {
  project = { name: "electron", testDir: "./tests/e2e/electron" };
  webServers = [viteServer];
} else if (isBrowser) {
  project = { name: "browser", testDir: "./tests/e2e/browser" };
  webServers = [viteServer];
} else if (isStorybook) {
  project = { name: "storybook", testDir: "./tests/e2e/storybook" };
  webServers = [storybookServer];
} else {
  throw new Error(`VITE_TARGETの指定が不正です。${process.env.VITE_TARGET}`);
}

/**
 * Read environment variables from file.
 * https://github.com/motdotla/dotenv
 */

/**
 * See https://playwright.dev/docs/test-configuration.
 */
const config: PlaywrightTestConfig = {
  testDir: "./tests/e2e",
  /* Maximum time one test can run for. */
  timeout: 60 * 1000,
  globalTimeout: 5 * 60 * 1000,
  expect: {
    /**
     * Maximum time expect() should wait for the condition to be met.
     * For example in `await expect(locator).toHaveText();`
     */
    timeout: 5 * 1000,
  },
  // ファイルシステムが関連してくるので、Electronテストでは並列化しない
  fullyParallel: !isElectron,
  workers: isElectron ? 1 : undefined,
  /* Fail the build on CI if you accidentally left test.only in the source code. */
  forbidOnly: !!process.env.CI,
  reporter: process.env.CI
    ? [["html", { open: "never" }], ["github"]]
    : [["html", { open: "on-failure" }]],
  /* Retry on CI only */
  retries: process.env.CI ? 2 : 0,
  use: {
    /* Maximum time each action such as `click()` can take. Defaults to 0 (no limit). */
    actionTimeout: 0,
    /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
    trace: "on-first-retry",
    video: {
      mode: "retain-on-failure",
    },
  },

  /* Configure projects for major browsers */
  projects: [project],

  /* Folder for test artifacts such as screenshots, videos, traces, etc. */
  // outputDir: 'test-results/',

  webServer: webServers,
};

export default config;

/// <reference types="vitest" />
import path from "path";
import { rm } from "fs/promises";

import electron from "vite-plugin-electron";
import tsconfigPaths from "vite-tsconfig-paths";
import vue from "@vitejs/plugin-vue";
import checker from "vite-plugin-checker";
import { BuildOptions, defineConfig, loadEnv, Plugin } from "vite";
import { quasar } from "@quasar/vite-plugin";
import { z } from "zod";

const isElectron = process.env.VITE_TARGET === "electron";
const isBrowser = process.env.VITE_TARGET === "browser";

export default defineConfig((options) => {
  const mode = z
    .enum(["development", "test", "production"])
    .parse(options.mode);

  const packageName = process.env.npm_package_name;
  const env = loadEnv(mode, import.meta.dirname);
  if (!packageName?.startsWith(env.VITE_APP_NAME)) {
    throw new Error(
      `"package.json"の"name":"${packageName}"は"VITE_APP_NAME":"${env.VITE_APP_NAME}"から始まっている必要があります`,
    );
  }

  // 型を曖昧にして下の[process.platform]のエラーを回避する
  const sevenZipBinNames: Record<string, string> = {
    win32: "7za.exe",
    linux: "7zzs",
    darwin: "7zz",
  };
  const sevenZipBinName = sevenZipBinNames[process.platform];
  if (!sevenZipBinName) {
    throw new Error(`Unsupported platform: ${process.platform}`);
  }
  process.env.VITE_7Z_BIN_NAME =
    (mode !== "production"
      ? path.join(import.meta.dirname, "vendored", "7z") + path.sep
      : "") + sevenZipBinName;
  process.env.VITE_APP_VERSION = process.env.npm_package_version;

  const shouldEmitSourcemap = ["development", "test"].includes(mode);
  const sourcemap: BuildOptions["sourcemap"] = shouldEmitSourcemap
    ? "inline"
    : false;

  // ref: electronの起動をスキップしてデバッグ起動を軽くする
  const skipLahnchElectron =
    mode === "test" || process.env.SKIP_LAUNCH_ELECTRON === "1";

  return {
    root: path.resolve(import.meta.dirname, "src"),
    envDir: import.meta.dirname,
    build: {
      outDir: path.resolve(import.meta.dirname, "dist"),
      chunkSizeWarningLimit: 10000,
      sourcemap,
    },
    publicDir: path.resolve(import.meta.dirname, "public"),
    css: {
      preprocessorOptions: {
        scss: {
          api: "modern",
          includePaths: [path.resolve(import.meta.dirname, "node_modules")],
        },
      },
    },
    resolve: {
      alias: {
        "@": path.resolve(import.meta.dirname, "src/"),
      },
    },
    plugins: [
      vue(),
      quasar({ autoImportComponentCase: "pascal" }),
      mode !== "test" &&
        checker({
          overlay: false,
          eslint: {
            lintCommand: "eslint --ext .ts,.vue .",
          },
          vueTsc: true,
        }),
      isElectron && [
        cleanDistPlugin(),
        electron([
          {
            entry: "./backend/electron/main.ts",
            // ref: https://github.com/electron-vite/vite-plugin-electron/pull/122
            onstart: ({ startup }) => {
              console.log("main process build is complete.");
              if (!skipLahnchElectron) {
                void startup([".", "--no-sandbox"]);
              }
            },
            vite: {
              plugins: [tsconfigPaths({ root: import.meta.dirname })],
              build: {
                outDir: path.resolve(import.meta.dirname, "dist"),
                sourcemap,
              },
            },
          },
          {
            // ref: https://electron-vite.github.io/guide/preload-not-split.html
            entry: "./backend/electron/preload.ts",
            onstart({ reload }) {
              if (!skipLahnchElectron) {
                reload();
              }
            },
            vite: {
              plugins: [tsconfigPaths({ root: import.meta.dirname })],
              build: {
                outDir: path.resolve(import.meta.dirname, "dist"),
                sourcemap,
                rollupOptions: {
                  output: { inlineDynamicImports: true },
                },
              },
            },
          },
        ]),
      ],
      isBrowser && injectBrowserPreloadPlugin(),
    ],
  };
});
const cleanDistPlugin = (): Plugin => {
  return {
    name: "clean-dist",
    apply: "build",
    enforce: "pre",
    async buildStart() {
      await rm(path.resolve(import.meta.dirname, "dist"), {
        recursive: true,
        force: true,
      });
    },
  };
};

const injectBrowserPreloadPlugin = (): Plugin => {
  return {
    name: "inject-browser-preload",
    transformIndexHtml: {
      order: "pre",
      handler: (html: string) =>
        html.replace(
          "<!-- %BROWSER_PRELOAD% -->",
          `<script type="module" src="./backend/browser/preload.ts"></script>`,
        ),
    },
  };
};

/// <reference types="vitest" />
import { defineWorkspace } from "vitest/config";
import { storybookTest } from "@storybook/experimental-addon-test/vitest-plugin";

const nodeTestPaths = ["../tests/unit/**/*.node.{test,spec}.ts"];
const browserTestPaths = ["../tests/unit/**/*.browser.{test,spec}.ts"];
const normalTestPaths = ["../tests/unit/**/*.{test,spec}.ts"];

const ignorePaths = (paths: string[]) => paths.map((path) => `!${path}`);

export default defineWorkspace([
  // Node.js環境
  {
    extends: "./vite.config.mts",
    test: {
      include: nodeTestPaths,
      name: "node",
      environment: "node",
      globals: true,
    },
  },

  // happy-domのエミュレート版ブラウザ環境
  {
    extends: "./vite.config.mts",
    plugins: [],
    test: {
      include: [
        ...normalTestPaths,
        ...ignorePaths(nodeTestPaths),
        ...ignorePaths(browserTestPaths),
      ],
      globals: true,
      name: "unit",
      environment: "happy-dom",
    },
  },

  // Chromiumブラウザ環境
  {
    extends: "./vite.config.mts",
    test: {
      include: browserTestPaths,
      globals: true,
      name: "browser",
      browser: {
        enabled: true,
        name: "chromium",
        provider: "playwright",
        headless: true,
        api: 7158,
        ui: false,
      },
    },
  },

  // Storybook
  {
    extends: "./vite.config.mts",
    plugins: [
      storybookTest({
        storybookScript: "storybook --ci --port 7160",
        storybookUrl: "http://localhost:7160",
      }),
    ],
    resolve: {
      alias: {
        // NOTE: Storybookで`template:`指定を使うために必要
        vue: "vue/dist/vue.esm-bundler.js",
      },
    },
    test: {
      include: ["./**/*.stories.ts"],
      globals: true,
      name: "storybook",
      browser: {
        enabled: true,
        name: "chromium",
        provider: "playwright",
        headless: true,
        api: 7159,
        ui: false,
      },
      isolate: false,
      setupFiles: ["./.storybook/vitest.setup.ts"],
    },
  },
]);
