module.exports = {
  extends: ['@commitlint/config-angular'],
  rules: {
    'type-enum': [
      2,
      'always',
      ['build', 'chore', 'ci', 'docs', 'feat', 'fix', 'perf', 'refactor', 'revert', 'style', 'test', 'wip'],
    ],
  },
};

{
  "rules": {
    "no-long-code": 0
  }
}

{
  "mainEntryPointFilePath": "<projectFolder>/lib/index.d.ts",
  "apiReport": {
    "enabled": false
  },
  "docModel": {
    "enabled": false
  },
  "dtsRollup": {
    "enabled": true
  },
  "tsdocMetadata": {
    "enabled": false
  },
  "messages": {
    "extractorMessageReporting": {
      "ae-missing-release-tag": {
        "logLevel": "none"
      },
      "ae-forgotten-export": {
        "logLevel": "none"
      }
    },
    "tsdocMessageReporting": {
      "tsdoc-param-tag-missing-hyphen": {
        "logLevel": "none"
      },
      "tsdoc-escape-greater-than": {
        "logLevel": "none"
      }
    }
  }
}

// Installing third-party modules by tnpm or cnpm will name modules with underscore as prefix.
// In this case _{module} is also necessary.
const esm = ['internmap', 'd3-*', 'lodash-es'].map((d) => `_${d}|${d}`).join('|');

module.exports = {
  runner: 'jest-electron/runner',
  testEnvironment: 'jest-electron/environment',
  testTimeout: 30000,
  setupFilesAfterEnv: ['jest-extended', './jest.setup.js'],
  preset: 'ts-jest',
  collectCoverage: false,
  collectCoverageFrom: [
    'src/**/*.ts',
    '!**/node_modules/**',
    '!**/vendor/**',
    '!**/_template/**',
    '!**/interactions/**',
    '!**/venn/layout/**',
  ],
  testRegex: '/__tests__/.*-spec\\.ts?$',
  moduleNameMapper: {
    'lodash-es': 'lodash',
    'd3-color': 'd3-color/dist/d3-color.min.js',
    'd3-interpolate': 'd3-interpolate/dist/d3-interpolate.min.js',
  },
};

const webpack = require('webpack');
const resolve = require('path').resolve;
const BundleAnalyzerPlugin = require('webpack-bundle-analyzer').BundleAnalyzerPlugin;

const BABEL_OPTIONS = {
  presets: [
    [
      '@babel/preset-env',
      {
        targets: '> 0.25%, not dead',
      },
    ],
  ],
  plugins: [
    [
      '@babel/transform-runtime',
      {
        helpers: false,
        regenerator: true,
      },
    ],
  ],
};

module.exports = {
  entry: {
    g2plot: './src/index.ts',
  },
  output: {
    filename: '[name].min.js',
    library: 'G2Plot',
    libraryTarget: 'umd',
    path: resolve(__dirname, 'dist/'),
  },
  resolve: {
    mainFields: ['module', 'main'],
    extensions: ['.ts', '.js', '.less'],
  },
  module: {
    rules: [
      {
        test: /\.ts$/,
        use: [
          {
            loader: 'babel-loader',
            options: BABEL_OPTIONS,
          },
          {
            loader: 'ts-loader',
            options: {
              transpileOnly: true,
            },
          },
        ]
      },
      {
        test: /\.js$/,
        /** bable åªéœ€è¦å¤„ç† node_modules ä¸­çš„ es6 æ¨¡å—ï¼Œsrc ä¸­çš„äº¤ç»™ ts-loader å³å¯ */
        include: /node_modules/,
        use: {
          loader: 'babel-loader',
          options: BABEL_OPTIONS,
        },
      },
    ],
  },
  plugins: [
    new webpack.NoEmitOnErrorsPlugin(),
    new webpack.optimize.AggressiveMergingPlugin(),
    ...(process.env.MODE === 'ANALYZER' ? [new BundleAnalyzerPlugin({ analyzerMode: 'static' })] : []),
  ],
  performance: {
    hints: false,
  },
  devtool: 'source-map',
};
