module.exports = {
  // "parser": '@typescript-eslint/parser', // ÂêØÁî®‰ºöÂØºËá¥vueÊñá‰ª∂eslintÈîôËØØ
  "plugins": [
    '@typescript-eslint',
    'jest'
  ],
  "env": {
    "browser": true,
    "es6": true,
    "node": true,
    "commonjs": true,
    "jest/globals": true
  },
  // Êé•ÂÖ•vueÂ§±Ë¥• ÊöÇ‰∏îeslint ÂøΩÁï•vueÊñá‰ª∂
  "extends": [
    // "standard",
    "plugin:vue/essential"
  ],
  "parserOptions": {
    // "parser": "babel-eslint",
    "parser": "@typescript-eslint/parser",
    "ecmaVersion": 2018,
    "ecmaFeatures": {
      "arrowFunctions": true,
      "classes": true,
      "modules": true,
      "defaultParams": true,
      "experimentalObjectRestSpread": true
    },
    "sourceType": "module",
    "parserOptions": {
      "allowImportExportEverywhere": true
    }
  },
  "globals": {
    "window": true,
    "define": true,
    "console": true,
    "require": true,
    "module": true,
  },
  "rules": {
    // 'no-var': "error",
    // ‰ºòÂÖà‰ΩøÁî® interface ËÄå‰∏çÊòØ type
    '@typescript-eslint/consistent-type-definitions': [
        "error",
        "interface"
    ],
    "@typescript-eslint/no-unused-vars": "error", // ‰ΩøÁî® ts Êú™‰ΩøÁî®ÂèòÈáèÁöÑËßÑÂàô ÊØîÂ¶ÇÊûö‰∏æÁ±ªÂûãÂú®es‰∏≠‰ºöÊä•Èîô
    "no-extend-native": 0,
    "no-new": 0,
    "no-var": 2,
    "prefer-const": 2,
    "no-useless-escape": 0,
    "no-useless-constructor": 0,
    "no-trailing-spaces": ["error", { "skipBlankLines": true }],
    "indent": ["error", 4, {
      "SwitchCase": 1
    }],
    "space-infix-ops": ["error", {"int32Hint": false}],
    "space-before-function-paren": ["error", {
      "anonymous": "always",
      "named": "always",
      "asyncArrow": "always"
    }],
    "semi": ["error", "always"],
    "comma-dangle": 0,
    "no-console": 0,
    "no-debugger": 0,
    "id-length": 0,
    "eol-last": 0,
    "object-curly-spacing": ["error", "never"],
    "arrow-spacing": "error",
    "no-multiple-empty-lines": "error",
    "no-unused-vars": "error",
    "spaced-comment": "error",
    "quotes": ["error", "single", { "allowTemplateLiterals": true }],
    "no-unreachable": "error",
    "keyword-spacing": "error",
    "space-before-blocks": "error",
    "semi-spacing": "error",
    "comma-spacing": "error",
    "key-spacing": "error",
    "no-undef": "error",
    "prefer-const": ["error", {
      "destructuring": "any",
      "ignoreReadBeforeAssign": false
    }],
    "vue/script-indent": ["warn", 4, {
      "baseIndent": 1,
      "switchCase": 1
    }],
    "vue/html-indent": ["error", 4],
    "vue/html-quotes": ["error", "single", { "avoidEscape": true }],
  },
  "overrides": [
    {
       "files": ["*.vue"],
       "rules": {
         "indent": "off"
       }
     }
   ]
}

module.exports = {
    preset: 'ts-jest/presets/js-with-babel',
    testEnvironment: 'jsdom',
    // roots: ['./src/'],
    // testEnvironment: 'jest-environment-jsdom-global',
    testEnvironmentOptions: {
        userAgent: 'Mozilla/5.0 (iPhone; CPU iPhone OS 11_0 like Mac OS X) AppleWebKit/604.1.38 (KHTML, like Gecko) Version/11.0 Mobile/15A372 Safari/604.1'
    },
    collectCoverage: true,
    coverageDirectory: './coverage/',
    // collectCoverageFrom: [
    //     'packages/*/src/**/*.ts'],
    verbose: false,
    // testPathIgnorePatterns: ['<rootDir>/node_modules/', '<rootDir>/packages/simulator/'],
    // coveragePathIgnorePatterns: ['<rootDir>/node_modules/', '<rootDir>/packages/simulator/'],
    globals: {
        __TEST__: true,
        __VERSION__: require('./package.json').version,
        ontouchstart: null
    },
    moduleFileExtensions: ['ts', 'tsx', 'js', 'json'],
    moduleNameMapper: {
        '^cnchar$': '<rootDir>/src/cnchar/main',
        '^cnchar-types$': '<rootDir>/src/cnchar-types',
        '^@cnchar-plugin/(.*?)$': '<rootDir>/src/cnchar/plugin/$1',
        '^testUtils$': '<rootDir>/helper/testUtils',
    },
    testRegex: 'src/cnchar/main/__tests__/(.*).spec.ts?$',
    modulePathIgnorePatterns: [
        'npm/packages/cnchar/package.json',
        './package.json'
    ],
};

window.jsboxCode = {
    lib: 'https://fastly.jsdelivr.net/npm/cnchar-all/cnchar.all.min.js',
    lang: 'html',
    code: /* html */`<style>
    #cnchar-area{
        line-height: 30px;
    }
</style>
<div id='draw-area'></div>
<div id='cnchar-area'></div>
<script>
    var drawArea = document.getElementById('draw-area');
    drawArea.innerHTML = '';
    var option = {
        clear: false,
        el: drawArea,
        style: {
            radicalColor: '#44f',
            backgroundColor: '#eee',
            length: 60
        }
    };
    option.type = cnchar.draw.TYPE.NORMAL;
    cnchar.draw('‰Ω†Â•Ω', option);

    option.type = cnchar.draw.TYPE.STROKE;
    cnchar.draw('Ê±âÂ≠ó', option);

    option.type = cnchar.draw.TYPE.ANIMATION;
    cnchar.draw('ÂæàÈÖ∑', option);

    var res = [
        cnchar.version,
        cnchar.stroke('‰Ω†Â•Ω', 'order', 'shape'),
        cnchar.stroke('ÈïøÂüé', 'order', 'name'),
        cnchar.orderToWord(['Ê®™', 'Êíá', 'Êç∫']),
        cnchar.convert.simpleToTrad('‰∏≠ÂõΩÈïøÂüé'),
        cnchar.convert.tradToSimple('Êº¢Â≠ó'),
        'ÁæéÂ•ΩÁöÑÂú∞Êñπ'.spell('tone'),
        'ÈïøÂ§ß‰∫Ü'.spell(),
        'ÈïøÂ§ß‰∫Ü'.spell('poly'),
        cnchar.strokeToWord(25, 'simple'),
        cnchar.idiom(['‰∫î', '', 'ÂçÅ', '']),
        cnchar.xhy('Â§ßÊ∞¥ÂÜ≤‰∫ÜÈæôÁéãÂ∫ô'),
        cnchar.radical('‰Ω†Â•ΩÂëÄ'),
        cnchar.sortSpell(['Âº†‰∏â', 'Âº†Â§ß','ÊùéÂõõ','Áéã‰∫î']),
        cnchar.sortStroke(['‰Ω†', 'Â•Ω', 'ÂëÄ']),
        cnchar.isCnChar('A'),
        JSON.stringify(cnchar.spellInfo('H√†n')),
    ]
    document.getElementById('cnchar-area').innerText = res.join('\\n');
    console.log(res);
</script>`
};

