module.exports = {
  // https://eslint.org/docs/user-guide/configuring#specifying-parser-options
  parserOptions: {
    ecmaVersion: 2019,
    sourceType: 'module',
    ecmaFeatures: {
      // Supports JSX syntax (not the same as supporting React).
      jsx: true,
    },
  },

  // commonly used envs
  env: {
    browser: true,
    node: true,
    es6: true,
    jest: true,
  },

  overrides: [
    {
      files: ['**/*.ts', '**/*.tsx'],
      parser: '@typescript-eslint/parser',
      parserOptions: {
        project: ['**/tsconfig.json'],
      },
      extends: [
        'plugin:import/typescript',
        // https://www.npmjs.com/package/@typescript-eslint/eslint-plugin
        'plugin:@typescript-eslint/recommended',
        'plugin:@typescript-eslint/recommended-requiring-type-checking',
        // NOTE: To override other configs, Prettier must be the last extension
        'plugin:prettier/recommended',
      ],
      rules: {
        'no-unused-vars': 'off',
        '@typescript-eslint/no-unused-vars': 'error',
        '@typescript-eslint/no-unsafe-assignment': 'warn',
        '@typescript-eslint/no-unsafe-member-access': 'warn',
        '@typescript-eslint/explicit-module-boundary-types': 'off',
        '@typescript-eslint/no-explicit-any': 'warn',
      },
    },
  ],

  // we use recommended configurations
  extends: [
    // https://eslint.org/docs/rules/
    'eslint:recommended',
    // https://github.com/yannickcr/eslint-plugin-react
    'plugin:react/recommended',
    // https://www.npmjs.com/package/eslint-plugin-react-hooks
    'plugin:react-hooks/recommended',
    // https://github.com/benmosher/eslint-plugin-import
    'plugin:import/recommended',
    // NOTE: To override other configs, Prettier must be the last extension
    // https://github.com/prettier/eslint-plugin-prettier
    'plugin:prettier/recommended',
  ],

  plugins: ['react-hooks', '@typescript-eslint'],

  rules: {
    // disable nice-to-have rules for migrate convenience
    'react/prop-types': 'off',
    'react/no-find-dom-node': 'off',
    'react/display-name': 'off',

    // recommended rules
    'prefer-const': 'error',
    'no-var': 'error',

    // hooks
    'react-hooks/rules-of-hooks': 'error',
    'react-hooks/exhaustive-deps': 'error',
  },

  settings: {
    // https://github.com/yannickcr/eslint-plugin-react#configuration
    react: {
      version: '16',
    },
  },
}

module.exports = {
  bracketSpacing: false,
  semi: false,
  singleQuote: true,
  trailingComma: 'es5',
}

module.exports = {
  preset: 'es-jest',
  testEnvironment: 'node',
  setupFiles: [require.resolve('./jest/setup.js')],
  collectCoverageFrom: [
    'src/**/*.{js,jsx,ts,tsx}',
    // 约定：入口文件只包含 import/export，不执行逻辑
    '!src/index.{js,jsx,ts,tsx}',
    // 约定：只包含类型定义的文件排除
    '!src/**/types.ts',
  ],
  testPathIgnorePatterns: ['/__mocks__/'],
}

const base = require('./jest.config.base')

module.exports = {
  ...base,
  projects: ['packages/*'],
}

import path from 'path'
import typescript from 'rollup-plugin-typescript2'
import findCacheDir from 'find-cache-dir'

const resolveCwd = path.resolve.bind(null, process.cwd())
const pkg = require(resolveCwd('package.json'))
const deps = Object.keys({...pkg.dependencies, ...pkg.peerDependencies})
const reExternal = new RegExp(`^(${deps.join('|')})($|/)`)

export default [
  {
    input: pkg.source,
    output: [
      {
        file: pkg.main,
        format: 'cjs',
      },
      {
        file: pkg.module,
        format: 'esm',
      },
    ],
    plugins: [
      typescript({
        cacheRoot: findCacheDir({
          name: 'rollup-plugin-typescript2',
          cwd: __dirname,
        }),
        tsconfigOverride: {
          include: [resolveCwd('src/**/*')],
          exclude: ['**/*.spec.*', '**/__tests__'],
          compilerOptions: {
            // reset to empty
            paths: {},
          },
        },
      }),
    ],
    external: (id) => (deps.length ? reExternal.test(id) : false),
  },
]

{
  "compilerOptions": {
    /* Visit https://aka.ms/tsconfig.json to read more about this file */
    "target": "ES6",
    "jsx": "react",
    "module": "ESNext",
    "lib": ["ESNext", "DOM"],
    "baseUrl": ".",
    "paths": {
      // link all packages without build step
      "griffith-mp4": ["./packages/griffith-mp4/src/index.ts"],
      "griffith-hls": ["./packages/griffith-hls/src/index.ts"],
      "griffith-utils": ["./packages/griffith-utils/src/index.ts"],
      "griffith-message": ["./packages/griffith-message/src/index.ts"],
      "griffith": ["./packages/griffith/src/index.ts"]
    },
    "declaration": true,
    // import @types/react
    "allowSyntheticDefaultImports": true,
    // import aphrodite/no-important
    "moduleResolution": "Node",
    "forceConsistentCasingInFileNames": true,
    "strict": true,
    "skipLibCheck": true
  }
}
