import conventional from "@commitlint/config-conventional";


const commitLintConfig = {
  extends: ["@commitlint/config-conventional"],
  plugins: ["commitlint-plugin-function-rules"],
  helpUrl:
    "https://github.com/jrgarciadev/tailwind-variants/blob/main/CONTRIBUTING.MD#commit-convention",
  rules: {
    ...conventional.rules,
    "type-enum": [
      2,
      "always",
      ["feat", "feature", "fix", "refactor", "docs", "build", "test", "ci", "chore"],
    ],
    "function-rules/header-max-length": [0],
  },
};

export default commitLintConfig;

{
  "$schema": "https://json.schemastore.org/eslintrc.json",
  "env": {
    "browser": false,
    "es2021": true,
    "node": true
  },
  "extends": ["plugin:prettier/recommended"],
  "plugins": ["prettier", "import", "@typescript-eslint"],
  "parser": "@typescript-eslint/parser",
  "parserOptions": {
    "ecmaFeatures": {
      "jsx": true
    },
    "ecmaVersion": 12,
    "sourceType": "module"
  },
  "settings": {
    "react": {
      "version": "detect"
    }
  },
  "overrides": [
    {
      "files": ["*/__tests__/**"],
      "plugins": ["jest"],
      "extends": ["plugin:jest/recommended"]
    }
  ],
  "rules": {
    "no-console": "warn",
    "prettier/prettier": "warn",
    "@typescript-eslint/no-unused-vars": [
      "warn",
      {
        "args": "after-used",
        "ignoreRestSiblings": false,
        "argsIgnorePattern": "^_.*?$"
      }
    ],
    "import/order": [
      "warn",
      {
        "groups": [
          "type",
          "builtin",
          "object",
          "external",
          "internal",
          "parent",
          "sibling",
          "index"
        ],
        "pathGroups": [
          {
            "pattern": "~/**",
            "group": "external",
            "position": "after"
          }
        ],
        "newlines-between": "always"
      }
    ],
    "padding-line-between-statements": [
      "warn",
      {"blankLine": "always", "prev": "*", "next": "return"},
      {"blankLine": "always", "prev": ["const", "let", "var"], "next": "*"},
      {
        "blankLine": "any",
        "prev": ["const", "let", "var"],
        "next": ["const", "let", "var"]
      }
    ]
  }
}

import {relative} from "path";

import {ESLint} from "eslint";

const removeIgnoredFiles = async (files) => {
  const cwd = process.cwd();
  const eslint = new ESLint();
  const relativePaths = files.map((file) => relative(cwd, file));
  const isIgnored = await Promise.all(relativePaths.map((file) => eslint.isPathIgnored(file)));
  const filteredFiles = files.filter((_, i) => !isIgnored[i]);

  return filteredFiles.join(" ");
};


const lintStaged = {
  // *.!(js|ts|jsx|tsx|d.ts)
  "**/*.{js,cjs,mjs,ts,jsx,tsx,json,md}": async (files) => {
    const filesToLint = await removeIgnoredFiles(files);

    return [`prettier --config .prettierrc.json --ignore-path --write ${filesToLint}`];
  },
  "**/*.{js,cjs,mjs,ts,jsx,tsx}": async (files) => {
    const filesToLint = await removeIgnoredFiles(files);

    return [`eslint -c .eslintrc.json --max-warnings=0 --fix ${filesToLint}`];
  },
};

export default lintStaged;

/* eslint-disable no-console */
import Benchmark from "benchmark";
import {cva} from "class-variance-authority";
import {extendTailwindMerge} from "tailwind-merge";

import {tv} from "./src/index.js";

const suite = new Benchmark.Suite();

const COMMON_UNITS = ["small", "medium", "large"];

const twMergeConfig = {
  theme: {
    opacity: ["disabled"],
    spacing: [
      "divider",
      "unit",
      "unit-2",
      "unit-4",
      "unit-6",
      "unit-8",
      "unit-10",
      "unit-12",
      "unit-14",
    ],
    borderWidth: COMMON_UNITS,
    borderRadius: COMMON_UNITS,
  },
  classGroups: {
    shadow: [{shadow: COMMON_UNITS}],
    "font-size": [{text: ["tiny", ...COMMON_UNITS]}],
    "bg-image": ["bg-stripe-gradient"],
    "min-w": [
      {
        "min-w": ["unit", "unit-2", "unit-4", "unit-6", "unit-8", "unit-10", "unit-12", "unit-14"],
      },
    ],
  },
};

// without slots no custom tw-merge config
const noSlots = {
  avatar: tv({
    base: "relative flex shrink-0 overflow-hidden rounded-full",
    variants: {
      size: {
        xs: "h-6 w-6",
        sm: "h-8 w-8",
        md: "h-10 w-10",
        lg: "h-12 w-12",
        xl: "h-14 w-14",
      },
    },
    defaultVariants: {
      size: "md",
    },
    compoundVariants: [
      {
        size: ["xs", "sm"],
        class: "ring-1",
      },
      {
        size: ["md", "lg", "xl", "2xl"],
        class: "ring-2",
      },
    ],
  }),
  image: tv({
    base: "aspect-square h-full w-full",
    variants: {
      withBorder: {
        true: "border-1.5 border-white",
      },
    },
  }),
  fallback: tv({
    base: "flex h-full w-full items-center justify-center rounded-full bg-muted",
    variants: {
      size: {
        xs: "text-xs",
        sm: "text-sm",
        md: "text-base",
        lg: "text-lg",
        xl: "text-xl",
      },
    },
    defaultVariants: {
      size: "md",
    },
  }),
};

// without slots & no tw-merge enabled
const noSlotsNoTwMerge = {
  avatar: tv(
    {
      base: "relative flex shrink-0 overflow-hidden rounded-full",
      variants: {
        size: {
          xs: "h-6 w-6",
          sm: "h-8 w-8",
          md: "h-10 w-10",
          lg: "h-12 w-12",
          xl: "h-14 w-14",
        },
      },
      defaultVariants: {
        size: "md",
      },
      compoundVariants: [
        {
          size: ["xs", "sm"],
          class: "ring-1",
        },
        {
          size: ["md", "lg", "xl", "2xl"],
          class: "ring-2",
        },
      ],
    },
    {
      twMerge: false,
    },
  ),
  image: tv(
    {
      base: "aspect-square h-full w-full",
      variants: {
        withBorder: {
          true: "border-1.5 border-white",
        },
      },
    },
    {
      twMerge: false,
    },
  ),
  fallback: tv(
    {
      base: "flex h-full w-full items-center justify-center rounded-full bg-muted",
      variants: {
        size: {
          xs: "text-xs",
          sm: "text-sm",
          md: "text-base",
          lg: "text-lg",
          xl: "text-xl",
        },
      },
      defaultVariants: {
        size: "md",
      },
    },
    {
      twMerge: false,
    },
  ),
};

// without slots & custom tw-merge config
const noSlotsWithCustomConfig = {
  avatar: tv(
    {
      base: "relative flex shrink-0 overflow-hidden rounded-full",
      variants: {
        size: {
          xs: "h-unit-6 w-unit-6",
          sm: "h-unit-8 w-unit-8",
          md: "h-unit-10 w-unit-10",
          lg: "h-unit-12 w-unit-12",
          xl: "h-unit-14 w-unit-14",
        },
      },
      defaultVariants: {
        size: "md",
      },
      compoundVariants: [
        {
          size: ["xs", "sm"],
          class: "ring-1",
        },
        {
          size: ["md", "lg", "xl", "2xl"],
          class: "ring-2",
        },
      ],
    },
    {
      twMergeConfig,
    },
  ),
  image: tv({
    base: "aspect-square h-full w-full",
    variants: {
      withBorder: {
        true: "border-1.5 border-white",
      },
    },
  }),
  fallback: tv(
    {
      base: "flex h-full w-full items-center justify-center rounded-full bg-muted",
      variants: {
        size: {
          sm: "text-small",
          md: "text-medium",
          lg: "text-large",
        },
      },
      defaultVariants: {
        size: "md",
      },
    },
    {
      twMergeConfig,
    },
  ),
};

// with slots no custom tw-merge config
export const avatar = (twMerge = true) =>
  tv(
    {
      slots: {
        base: "relative flex shrink-0 overflow-hidden rounded-full",
        image: "aspect-square h-full w-full",
        fallback: "flex h-full w-full items-center justify-center rounded-full bg-muted",
      },
      variants: {
        withBorder: {
          true: {
            image: "border-1.5 border-white",
          },
        },
        size: {
          xs: {
            base: "h-6 w-6",
            fallback: "text-xs",
          },
          sm: {
            base: "h-8 w-8",
            fallback: "text-sm",
          },
          md: {
            base: "h-10 w-10",
            fallback: "text-base",
          },
          lg: {
            base: "h-12 w-12",
            fallback: "text-large",
          },
          xl: {
            base: "h-14 w-14",
            fallback: "text-xl",
          },
        },
      },
      defaultVariants: {
        size: "md",
        withBorder: false,
      },
      compoundVariants: [
        {
          size: ["xs", "sm"],
          class: "ring-1",
        },
        {
          size: ["md", "lg", "xl", "2xl"],
          class: "ring-2",
        },
      ],
    },
    {
      twMerge,
    },
  );

// with slots & custom tw-merge config
export const avatarWithCustomConfig = tv(
  {
    slots: {
      base: "relative flex shrink-0 overflow-hidden rounded-full",
      image: "aspect-square h-full w-full",
      fallback: "flex h-full w-full items-center justify-center rounded-full bg-muted",
    },
    variants: {
      withBorder: {
        true: {
          image: "border-1.5 border-white",
        },
      },
      size: {
        sm: {
          base: "h-unit-8 w-unit-8",
          fallback: "text-small",
        },
        md: {
          base: "h-unit-10 w-unit-10",
          fallback: "text-medium",
        },
        lg: {
          base: "h-unit-12 w-unit-12",
          fallback: "text-large",
        },
      },
    },
    defaultVariants: {
      size: "md",
      withBorder: false,
    },
    compoundVariants: [
      {
        size: ["sm"],
        class: "ring-1",
      },
      {
        size: ["md", "lg"],
        class: "ring-2",
      },
    ],
  },
  {
    twMergeConfig,
  },
);

// CVA without tw-merge config
const cvaNoMerge = {
  avatar: cva("relative flex shrink-0 overflow-hidden rounded-full", {
    variants: {
      size: {
        xs: "h-6 w-6",
        sm: "h-8 w-8",
        md: "h-10 w-10",
        lg: "h-12 w-12",
        xl: "h-14 w-14",
      },
    },
    defaultVariants: {
      size: "md",
    },
    compoundVariants: [
      {
        size: ["xs", "sm"],
        class: "ring-1",
      },
      {
        size: ["md", "lg", "xl", "2xl"],
        class: "ring-2",
      },
    ],
  }),
  image: cva("aspect-square h-full w-full", {
    variants: {
      withBorder: {
        true: "border-1.5 border-white",
      },
    },
  }),
  fallback: cva("flex h-full w-full items-center justify-center rounded-full bg-muted", {
    variants: {
      size: {
        xs: "text-xs",
        sm: "text-sm",
        md: "text-base",
        lg: "text-lg",
        xl: "text-xl",
      },
    },
    defaultVariants: {
      size: "md",
    },
  }),
};

const cvaMerge = extendTailwindMerge({extend: twMergeConfig});

// add tests
suite
  .add("TV without slots & tw-merge (enabled)", function () {
    noSlots.avatar({size: "md"});
    noSlots.fallback();
    noSlots.image();
  })
  .add("TV without slots & tw-merge (disabled)", function () {
    noSlotsNoTwMerge.avatar({size: "md"});
    noSlotsNoTwMerge.fallback();
    noSlotsNoTwMerge.image();
  })
  .add("TV with slots & tw-merge (enabled)", function () {
    const {base, fallback, image} = avatar(true)({size: "md"});

    base();
    fallback();
    image();
  })
  .add("TV with slots & tw-merge (disabled)", function () {
    const {base, fallback, image} = avatar(false)({size: "md"});

    base();
    fallback();
    image();
  })
  .add("TV without slots & custom tw-merge config", function () {
    noSlotsWithCustomConfig.avatar({size: "md"});
    noSlotsWithCustomConfig.fallback();
    noSlotsWithCustomConfig.image();
  })
  .add("TV with slots & custom tw-merge config", function () {
    const {base, fallback, image} = avatarWithCustomConfig({size: "md"});

    base();
    fallback();
    image();
  })
  .add("CVA without slots & tw-merge (enabled)", function () {
    cvaMerge(cvaNoMerge.avatar({size: "md"}));
    cvaMerge(cvaNoMerge.fallback());
    cvaMerge(cvaNoMerge.image());
  })
  .add("CVA without slots & tw-merge (disabled)", function () {
    cvaNoMerge.avatar({size: "md"});
    cvaNoMerge.fallback();
    cvaNoMerge.image();
  })

  // add listeners
  .on("cycle", function (event) {
    console.log(String(event.target));
  })
  .on("complete", function () {
    console.log("Fastest is " + this.filter("fastest").map("name"));
  })
  // run async
  .run({async: true});

{
  "remove": ["tsup", "packageManager"],
  "replace": {
    "main": "dist/index.cjs",
    "module": "dist/index.js",
    "types": "dist/index.d.ts",
    "exports": {
      ".": {
        "require": "./dist/index.cjs",
        "import": "./dist/index.js",
        "types": "./dist/index.d.ts"
      },
      "./dist/*": "./dist/*",
      "./transformer": {
        "require": "./dist/transformer.cjs",
        "types": "./dist/transformer.d.ts",
        "default": "./dist/transformer.js"
      },
      "./package.json": "./package.json"
    }
  }
}

const fs = require("fs");
const path = require("path");

const srcPath = path.join(__dirname, "src");
const typesPath = path.join(__dirname, "dist");

// Check if the "types" folder exists, if not, create it
if (!fs.existsSync(typesPath)) {
  fs.mkdirSync(typesPath);
}

// Read the files in the "src" folder
fs.readdir(srcPath, (err, files) => {
  if (err) {
    console.error(err);

    return;
  }

  // Iterate through the files in the "src" folder
  files.forEach((file) => {
    // Check if the file is a .d.ts file
    if (file.endsWith(".d.ts")) {
      // Construct the source and destination file paths
      const srcFile = path.join(srcPath, file);
      const destFile = path.join(typesPath, file);

      // Copy the file
      fs.copyFile(srcFile, destFile, (err) => {
        if (err) {
          console.error(err);
        }
      });
    }
  });
});

const config = {
  rootDir: "src",
  globals: {
    "ts-jest": {
      tsconfig: "tsconfig.json",
    },
  },
  transform: {
    "^.+\\.(t|j)sx?$": "@swc/jest",
  },
  setupFilesAfterEnv: ["<rootDir>/setupTests.ts"],
  extensionsToTreatAsEsm: [".ts"],
};

export default config;

