{
  "extends": [
    "teact-app",
    "airbnb",
    "airbnb-typescript"
  ],
  "plugins": [
    "no-async-without-await",
    "no-null",
    "simple-import-sort",
    "react-hooks-static-deps",
    "eslint-multitab-tt"
  ],
  "rules": {
    "eslint-multitab-tt/no-immediate-global": "error",
    "eslint-multitab-tt/must-update-global-after-await": "off",
    "eslint-multitab-tt/set-global-only-variable": "error",
    "eslint-multitab-tt/no-getactions-in-actions": "error",
    "eslint-multitab-tt/must-specify-action-handler-return-type": "error",
    "indent": [
      "error",
      2,
      {
        "SwitchCase": 1
      }
    ],
    "max-len": [
      "error",
      {
        "code": 120,
        "ignoreComments": true,
        "ignorePattern": "\\sd=\".+\"" // Ignore lines with "d" attribute
      }
    ],
    "array-bracket-newline": [
      2,
      "consistent"
    ],
    "no-null/no-null": 2,
    "no-console": "error",
    "semi": "error",
    "no-implicit-coercion": "error",
    "react-hooks/exhaustive-deps": "off",
    "react-hooks-static-deps/exhaustive-deps": [
      "error",
      {
        "additionalHooks": "(useSyncEffect|useAsync|useDebouncedCallback|useThrottledCallback|useEffectWithPrevDeps|useLayoutEffectWithPrevDeps|useDerivedState|useDerivedSignal|useThrottledResolver|useDebouncedResolver|useThrottleForHeavyAnimation)$",
        "staticHooks": {
          "getActions": true,
          "useFlag": [false, true, true],
          "useForceUpdate": true,
          "useReducer": [false, true],
          "useLastCallback": true
        }
      }
    ],
    "arrow-body-style": "off",
    "no-else-return": "off",
    "no-plusplus": "off",
    "no-void": "off",
    "no-continue": "off",
    "default-case": "off",
    "no-param-reassign": "off",
    "no-prototype-builtins": "off",
    "no-await-in-loop": "off",
    "no-nested-ternary": "off",
    "function-paren-newline": [
      "error",
      "consistent"
    ],
    "prefer-destructuring": "off",
    // Allow for...of. Edited from:
    // https://github.com/airbnb/javascript/blob/b4377fb03089dd7f08955242695860d47f9caab4/packages/eslint-config-airbnb-base/rules/style.js#L333
    "no-restricted-syntax": [
      "error",
      {
        "selector": "ForInStatement",
        "message": "for..in loops iterate over the entire prototype chain, which is virtually never what you want. Use Object.{keys,values,entries}, and iterate over the resulting array."
      },
      {
        "selector": "LabeledStatement",
        "message": "Labels are a form of GOTO; using them makes code confusing and hard to maintain and understand."
      },
      {
        "selector": "WithStatement",
        "message": "`with` is disallowed in strict mode because it makes code impossible to predict and optimize."
      }
    ],
    "import/no-extraneous-dependencies": "off",
    "import/prefer-default-export": "off",
    "import/named": "off",
    "import/no-webpack-loader-syntax": "off",
    "import/no-cycle": [
      "error",
      {
        "allowUnsafeDynamicCyclicDependency": true // TODO: Fix this
      }
    ],
    "react/prop-types": "off",
    "react/jsx-one-expression-per-line": "off",
    "react/button-has-type": "off",
    "react/require-default-props": "off",
    "react/function-component-definition": "off",
    // Teact feature
    "react/style-prop-object": "off",
    "react/no-unknown-property": "off",
    "react/jsx-no-bind": [
      "error",
      {
        "ignoreRefs": true,
        "allowArrowFunctions": false,
        "allowFunctions": false,
        "allowBind": false,
        "ignoreDOMComponents": true
      }
    ],
    "jsx-a11y/click-events-have-key-events": "off",
    "jsx-a11y/mouse-events-have-key-events": "off",
    "jsx-a11y/no-static-element-interactions": "off",
    "jsx-a11y/label-has-associated-control": "off",
    "jsx-a11y/anchor-is-valid": "off",
    "jsx-a11y/no-noninteractive-element-to-interactive-role": "off",
    "jsx-a11y/media-has-caption": "off",
    "no-async-without-await/no-async-without-await": 1,
    "@typescript-eslint/no-use-before-define": [
      "error",
      {
        "functions": false
      }
    ],
    "@typescript-eslint/camelcase": "off",
    "@typescript-eslint/member-delimiter-style": "error",
    "@typescript-eslint/default-param-last": "off",
    "@typescript-eslint/return-await": [
      "error",
      "in-try-catch"
    ],
    "@typescript-eslint/consistent-type-imports": [
      "error",
      {
        "prefer": "type-imports",
        "disallowTypeAnnotations": false
      }
    ],
    "simple-import-sort/imports": [
      "error",
      {
        "groups": [
          // Side effect imports
          ["^\\u0000"],
          // Lib and global imports
          [
            "^react",
            "^@?\\w",
            "dist(/.*|$)",
            "^(\\.+/)+(lib/(teact|gramjs))(/.*|$)",
            "^(\\.+/)+global$"
          ],
          // Type imports
          [
            "^(\\.+/)+.+\\u0000$",
            "^(\\.+/|\\w+/)+(types)(/.*|$)",
            "^(\\.+/|\\w+/)+(types)\\u0000"
          ],
          // Config, utils, helpers
          [
            "^(\\.+/)+config",
            "^(\\.+/)+(lib)(?!/(gramjs|teact))(/.*|$)",
            "^(\\.+/)+global/.+",
            "^(\\.+/)+(util)(/.*|$)",
            "^\\.\\.(?!/?$)",
            "^\\.\\./?$",
            "^\\./(?=.*/)(?!/?$)",
            "^\\.(?!/?$)",
            "^\\./?$"
          ],
          // Hooks
          [
            ".+(/hooks/)(.*|$)"
          ],
          // Components
          [
            "\/[A-Z](([a-z]+[A-Z]?)*)"
          ],
          // Styles and CSS modules
          [
            "^.+\\.s?css$"
          ],
          // Assets: images, stickers, etc
          [
            "^(\\.+/)+(assets)(/.*|$)"
          ]
        ]
      }
    ]
  },
  "settings": {
    "import/resolver": {
      "webpack": {
        "extensions": [".js", ".jsx", ".ts", ".tsx"]
      }
    },
    "import/core-modules": [
      "fs",
      "path",
      "crypto"
    ]
  },
  "parserOptions": {
    "project": "./tsconfig.json"
  }
}

module.exports = {
  inputDir: './src/assets/font-icons',
  outputDir: './src/styles',
  name: 'icons',
  fontTypes: ['woff2', 'woff'],
  assetTypes: ['scss', 'ts'],
  tag: '',
  // Use a custom Handlebars template
  templates: {
    scss: './dev/icons.scss.hbs'
  },
  formatOptions: {
    ts: {
      types: ['literalId'],
      singleQuotes: true,
      literalIdName: 'FontIconName',
    },
  },
  pathOptions: {
    ts: './src/types/icons/font.ts',
  },
};

{
  "plugins": {
    "autoprefixer": {},
  }
}

{
  "extends": [
    "stylelint-config-recommended-scss",
    "@stylistic/stylelint-config"
  ],
  "ignoreFiles": [
    "dist/*.css",
    "src/styles/icons.scss"
  ],
  "plugins": [
    "@stylistic/stylelint-plugin",
    "stylelint-declaration-block-no-ignored-properties",
    "stylelint-high-performance-animation",
    "stylelint-group-selectors",
    "stylelint-selector-tag-no-without-class",
    "./dev/wholePixel.js"
  ],
  "rules": {
    "property-no-unknown": [
      true,
      {
        "ignoreProperties": [
          "composes",
          "compose-with"
        ]
      }
    ],
    "selector-attribute-quotes": "always",
    "scss/operator-no-unspaced": null,
    "no-descending-specificity": null,
    "plugin/declaration-block-no-ignored-properties": true,
    "plugin/no-low-performance-animation-properties": [
      true,
      {
        "ignore": "paint-properties"
      }
    ],
    "plugin/stylelint-group-selectors": [true, { "severity": "warning" }],
    "plugin/whole-pixel": [true, { "ignoreList": ["letter-spacing"] }],
    "selector-pseudo-class-no-unknown": [
      true,
      {
        "ignorePseudoClasses": ["global"]
      }
    ],
    "plugin/selector-tag-no-without-class": ["/^(?!body|html)([^_-]*)$/", { "severity": "warning" }],
    "@stylistic/number-leading-zero": "always",
    "@stylistic/string-quotes": null,
    "@stylistic/color-hex-case": null,
    "@stylistic/selector-list-comma-newline-after": null,
    "@stylistic/block-closing-brace-newline-after": null,
    "@stylistic/indentation": [2, { "ignore": ["value"] }],
    "at-rule-empty-line-before": ["always", { "ignore": ["first-nested", "after-comment", "blockless-after-blockless"] }],
    "rule-empty-line-before": ["always", {
      "except": ["after-single-line-comment", "first-nested"],
      "ignore": ["after-comment", "inside-block"]
    }]
  }
}

const isTest = process.env.APP_ENV === 'test';
const isMocked = Boolean(process.env.APP_MOCKED_CLIENT);

module.exports = {
  presets: [
    [
      '@babel/typescript',
    ],
    [
      '@babel/preset-env',
    ],
    [
      '@babel/preset-react',
    ],
  ],
  plugins: [
    '@babel/plugin-transform-class-properties',
    '@babel/plugin-syntax-nullish-coalescing-operator',
    '@babel/plugin-transform-logical-assignment-operators',
    ...(isTest && !isMocked ? ['babel-plugin-transform-import-meta'] : []),
  ],
};

module.exports = {
  setupFilesAfterEnv: ['./tests/init.js'],
  moduleNameMapper: {
    '\\.(css|scss|wasm|jpg|jpeg|png|gif|eot|otf|webp|svg|ttf|woff|woff2|mp4|webm|wav|mp3|m4a|aac|oga|tgs)$':
      '<rootDir>/tests/staticFileMock.js',
  },
  testPathIgnorePatterns: [
    '<rootDir>/tests/playwright/',
    '<rootDir>/node_modules/',
    '<rootDir>/legacy_notes_and_workbook/',
    '<rootDir>/client/src/stylesheets/',
  ],
  testEnvironment: 'jsdom',
  transform: {
    '\\.(jsx?|tsx?)$': 'babel-jest',
    '\\.txt$': '@glen/jest-raw-loader',
  },
  globals: {
    APP_REVISION: "jest-test",
    APP_VERSION: "0.0.1",
  },
};

