{
  "name": "dts-gen",
  "version": "0.7.4",
  "description": "TypeScript Definition File Generator",
  "main": "bin/lib/index.js",
  "bin": {
    "dts-gen": "bin/lib/run.js"
  },
  "dependencies": {
    "dts-dom": "latest",
    "parse-git-config": "^1.1.1",
    "typescript": "^3.5.1",
    "yargs": "^4.8.1"
  },
  "devDependencies": {
    "@types/mocha": "latest",
    "@types/node": "latest",
    "@types/parse-git-config": "^1.1.0",
    "@types/yargs": "latest",
    "ecurve": "1.0.5",
    "jquery": "latest",
    "lodash": "latest",
    "mocha": "^3.0.2",
    "react": "latest",
    "tslint": "^5.1.0",
    "webpack": "^1.13.1"
  },
  "keywords": [
    "typescript"
  ],
  "preferGlobal": true,
  "scripts": {
    "test": "mocha bin/tests/test.js",
    "build": "tsc",
    "build-browser": "tsc -p browser && webpack",
    "lint": "tslint --project tsconfig.json --format stylish"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/Microsoft/dts-gen.git"
  },
  "author": "Ryan Cavanaugh <ryanca@microsoft.com> (https://www.github.com/RyanCavanaugh)",
  "license": "Apache-2.0",
  "bugs": {
    "url": "https://github.com/Microsoft/dts-gen/issues"
  },
  "homepage": "https://github.com/Microsoft/dts-gen#readme",
  "engines": {
    "node": ">=6.0.0"
  }
}

const webpack = require('webpack');

 module.exports = {
     entry: './bin/browser/browser.js',
     output: {
         path: './docs',
         filename: 'browser-bundle.js'
     },
    plugins: [
        new webpack.optimize.UglifyJsPlugin({
            compress: {
                warnings: false,
            },
            output: {
                comments: false,
            },
        }),
    ]
 };
