import type { UserConfig } from '@commitlint/types'
import { PACKAGES } from './scripts/constants.js'

export default {
  extends: ['@commitlint/config-conventional'],
  rules: {
    'scope-enum': [2, 'always', [...PACKAGES, 'e2e']],
    'footer-max-line-length': [0],
  },
} satisfies UserConfig

import path from 'node:path'
import { vuepress } from 'eslint-config-vuepress'
import { PACKAGES, ROOT } from './scripts/constants.js'

export default vuepress(
  {
    imports: {
      packageDir: [
        ROOT,
        path.resolve(ROOT, 'e2e'),
        ...PACKAGES.map((item) => path.resolve(ROOT, `packages/${item}`)),
      ],
    },
    javascript: {
      overrides: {
        'no-underscore-dangle': [
          'warn',
          {
            allow: [
              '__dirname',
              '_context',
              '_pageChunk',
              '_registeredComponents',
            ],
          },
        ],
      },
    },
    vue: {
      overrides: {
        'no-useless-assignment': 'off', // TODO: false positive in vue sfc
      },
    },
  },
  {
    files: ['**/tests/**'],
    rules: {
      'import/no-unresolved': 'off',
      'no-console': 'off',
      'prefer-template': 'off',
    },
  },
)

import { defineConfig } from 'taze'

export default defineConfig({
  packageMode: {
    esbuild: 'minor',
    vite: 'minor',
  },
  recursive: true,
  write: true,
})

import path from 'node:path'
import { defineConfig } from 'vitest/config'
import { PACKAGES, ROOT } from './scripts/constants.js'

export default defineConfig({
  resolve: {
    alias: [
      {
        find: new RegExp(`^@vuepress/(${PACKAGES.join('|')})$`),
        replacement: path.resolve(ROOT, './packages/$1/src/index.ts'),
      },
    ],
  },
  test: {
    coverage: {
      all: true,
      exclude: [
        'packages/bundler*/**',
        'packages/client/**',
        'packages/vuepress/**',
      ],
      include: ['packages/*/src/**'],
      provider: 'istanbul',
      reporter: ['clover', 'json', 'lcov', 'text'],
    },
    include: ['packages/*/tests/**/*.spec.ts'],
  },
})
